INSERT INTO `doansang` (`STT`, `Ten_sp`, `Ten_tieng_Anh`, `Hinh_anh`, `Gia`, `Mieu_ta`, `Thoi_gian_tao`, `Trang_thai`) VALUES
(1, 'Bánh canh cua', 'Tapioca noodle soup with crab meat and pork', '1564018631banh-canh-cua-quan-an-ngon-1498458607.jpg', 60000, '<p>Sợi b&aacute;nh canh l&agrave;m từ bột năng hoặc bột gạo, to hơn sợi b&uacute;n ngo&agrave;i Bắc, m&agrave;u trắng hơi trong trong, ăn dai hơn b&uacute;n. Nước d&ugrave;ng b&aacute;nh canh Qu&aacute;n Ăn Ngon cũng thường c&oacute; dạng sền sệt, ng&ograve;n ngọt ninh từ thịt, cua ghẹ hải sản v&agrave; một số phụ liệu kh&aacute;c, chứ kh&ocirc;ng nhan nh&aacute;t nhạt như nước d&ugrave;ng b&uacute;n phở.<br />\r\nC&oacute; kh&aacute; nhiều loại b&aacute;nh canh như b&aacute;nh canh t&ocirc;m, thịt lợn nhưng &quot;&aacute;m ảnh&quot; nhất vẫn l&agrave; b&aacute;nh canh cua. Con cua to, luộc ch&iacute;n, m&agrave;u đỏ cam bắt mắt, cả c&aacute;i c&agrave;ng chắc thịt ngự tr&ecirc;n b&aacute;t b&aacute;nh canh n&oacute;ng hổi, ăn k&egrave;m với thịt ch&acirc;n gi&ograve; v&agrave; ch&uacute;t ớt xanh, chanh tươi.</p>\r\n', '2019-07-25 01:38:00', '1'),
(2, ' Bánh cuốn nhân tôm ', 'Steamed rice pancake with shrimp and minced pork ', '1563943918banh-cuon-nhan--tom-quan-an-ngon-1498458784.jpg', 60000, '<p>B&aacute;nh cuốn nh&acirc;n t&ocirc;m l&agrave; một trong những m&oacute;n b&aacute;nh được rất nhiều người y&ecirc;u th&iacute;ch bởi hương vị thơm ngon, dễ ăn, ăn nhiều m&agrave; kh&ocirc;ng hề cảm thấy ch&aacute;n.</p>\r\n', '2019-07-24 04:51:58', '1'),
(3, ' Bò kho bánh mỳ ', 'Braised beef bread ', '1563943930bo-kho-banh-mi.jpg', 55000, '<p>Thịt b&ograve; l&agrave; m&oacute;n ăn bổ dưỡng với nhiều c&aacute;ch chế biến kh&aacute;c nhau trong đ&oacute; c&oacute; m&oacute;n b&ograve; kho b&aacute;nh m&igrave;. Đ&acirc;y l&agrave; m&oacute;n rất dễ ăn lại gi&agrave;u dưỡng chất l&agrave; một lựa chọn rất tuyệt vời cho bạn v&agrave;o những dịp cuối tuần.</p>\r\n', '2019-07-24 04:52:11', '1'),
(4, ' Bún bò Huế ', 'Beef noodle soup with pig leg in Hue style ', '1563943949bun-bo-hue-27-1496718176.jpg', 60000, '<p>Một t&ocirc; b&uacute;n b&ograve; đạt ti&ecirc;u chuẩn gồm c&oacute;: b&uacute;n, thịt bắp b&ograve;, gi&ograve; heo, rau thơm, sa tế, mắm ruốc,&hellip;Điều đặc biệt của m&oacute;n ăn n&agrave;y l&agrave; nước l&egrave;o phải được nấu trong một nồi tr&ograve;n bằng nh&ocirc;m được g&ograve; bởi b&agrave;n tay kh&eacute;o l&eacute;o của người thợ.</p>\r\n', '2019-07-24 04:52:29', '1'),
(5, ' Bún bò Nam Bộ ', 'Southern style sautéed beef with rice noodles ', '1563945311bun-bo-nam-bo-1496721350.jpg', 60000, '<p>B&uacute;n b&ograve; Nam bộ, nghe c&aacute;i t&ecirc;n cũng đủ biết nguồn gốc xuất xứ. B&uacute;n n&agrave;y c&ograve;n được gọi l&agrave; b&uacute;n x&agrave;o v&igrave; m&oacute;n b&uacute;n n&agrave;y kh&ocirc;ng giống như những loại b&uacute;n nước kh&aacute;c m&agrave; l&agrave; b&uacute;n kh&ocirc;, l&agrave; sự kết hợp giữa b&uacute;n, rau sống, gi&aacute;, thịt b&ograve; x&agrave;o, đậu phộng rang, c&agrave; rốt đu đủ th&aacute;i sợi, h&agrave;nh phi thơm,&hellip;sau đ&oacute; th&ecirc;m nước mắm ớt chua cay v&agrave;o trộn đều.</p>\r\n', '2019-07-24 05:15:11', '1'),
(6, ' Bún dọc mùng chân giò ', 'Pork-legs rice noodle soup with Vietnamese taro ', '1563945323bun-doc-mung-quan-an-ngon-1498461895.jpg', 45000, '<p>M&oacute;ng ninh mềm ngọt, hương thơm nh&egrave; nhẹ k&egrave;m ch&uacute;t hăng của h&agrave;nh cọng sắt mỏng, nước d&ugrave;ng trong, vị chua thanh m&aacute;t.</p>\r\n', '2019-07-24 05:15:23', '1'),
(7, ' Bún mắm ', 'Rice noodle soup with fermented fish, seafood and roasted pork ', '1563945337bun-mam-quan-an-ngon-1498462126.jpg', 55000, '<p>B&uacute;n mắm vốn được biến tấu của mắm kho, một trong những m&oacute;n ăn đặc trưng của miền T&acirc;y s&ocirc;ng nước. Nước l&egrave;o của b&uacute;n mắm được nấu từ c&aacute; linh hay c&aacute; sặc, v&igrave; vậy m&agrave; m&oacute;n ăn c&oacute; m&ugrave;i vị mằn mặn nồng đặc biệt nhưng kh&ocirc;ng dễ thưởng thức với một v&agrave;i người. Th&agrave;nh phần ch&iacute;nh gồm c&aacute; linh, b&ocirc;ng s&uacute;ng, t&ocirc;m, mực,&hellip;v&agrave; quan trọng nhất l&agrave; rau đắng, thứ rau đặc biệt phổ biến ở miền T&acirc;y s&ocirc;ng nước.</p>\r\n', '2019-07-24 05:15:37', '1'),
(8, ' Bún măng gà ', 'Chicken & dry bamboo shoot rice noodle - soup or dipping sauce ', '1563945370bun-mang-ga-quan-an-ngon-1498462230.jpg', 50000, '<p>B&uacute;n măng g&agrave; l&agrave; m&oacute;n ăn ngon ph&ugrave; hợp với ng&agrave;y h&egrave; oi bức. Vị ngọt của g&agrave; v&agrave; măng kh&ocirc; h&ograve;a quyện với nhau l&agrave;m cho m&oacute;n ăn trở n&ecirc;n hấp dẫn hơn.</p>\r\n', '2019-07-24 05:16:10', '1'),
(9, ' Bún nem nướng ', 'Grilled pork balls served with rice noodles & fresh herbs ', '1563945385bun-mem-nuong-quan-an-ngon-1497429549.jpg', 60000, '<p>L&agrave; m&oacute;n ăn phổ biến ở c&aacute;c tỉnh miền Trung Việt Nam. V&agrave; t&ugrave;y theo v&ugrave;ng miền m&agrave; nem nướng c&oacute; c&aacute;ch l&agrave;m, c&aacute;ch ăn cũng kh&aacute;c nhau. B&uacute;n ăn k&egrave;m nem nướng thơm, mỡ h&agrave;nh, rắc đậu phộng rang gi&atilde; nhỏ, chan nước mắm dưa g&oacute;p chua ngọt trộn đều.</p>\r\n', '2019-07-24 05:16:25', '1'),
(10, ' Bún ốc ', 'Snail noodle soup or dipping sauce ', '1563945398bun-oc-quan-an-ngon-1497430880.jpg', 50000, '<p>Ốc nhồi lựa con loại to, nước d&ugrave;ng trong, m&agrave;u đỏ nhạt của c&agrave; chua, thơm m&ugrave;i ốc v&agrave; h&agrave;nh phi, giấm bỗng, vị chua dịu. M&oacute;n ăn k&egrave;m rau sống, tương ớt, mắm t&ocirc;m.</p>\r\n', '2019-07-24 05:16:38', '1'),
(11, ' Bún riêu cua ', 'Fresh water crab with rice noodle soup ', '1563945413bun-reu-cua-quan-an-ngon-1497431304.jpg', 50000, '<p>Gạch cua đồng gi&atilde; tay, nước d&ugrave;ng trong m&agrave;u đỏ nhạt, thơm m&ugrave;i giấm bỗng, đậm m&ugrave;i cua, vị vừa, chua dịu. M&oacute;n ăn k&egrave;m rau sống, tương ớt, một ch&uacute;t mắm t&ocirc;m (để ri&ecirc;ng t&ugrave;y khẩu vị).</p>\r\n', '2019-07-24 05:16:53', '1'),
(12, ' Bún Thái ', 'Thai noodles ', '1563945429bunthai.jpg', 60000, '<p>B&uacute;n Th&aacute;i l&agrave; m&oacute;n ăn c&oacute; nguồn gốc từ Th&aacute;i Lan nhưng lại được nhiều bạn trẻ Việt Nam ưa chuộng v&igrave; c&oacute; vị chua cua, cay cay c&ugrave;ng với m&ugrave;i thơm đặc trưng dễ chịu.</p>\r\n', '2019-07-24 05:17:09', '1'),
(13, '  Hủ tiếu Nam Vang khô  ', 'Cambodia style rice vermicelli with pork,shrimp,pork-liver & quail egg (mixed)  ', '1563945471hu-tieu-kho-1501063365.jpg', 65000, '<p>Cũng l&agrave; c&aacute;c nguy&ecirc;n liệu giống như một b&aacute;t Hủ tiếu Nam Vang chan nước l&egrave;o, hủ tiếu Nam Vang kh&ocirc; để nguy&ecirc;n c&aacute;c nguy&ecirc;n liệu v&ocirc; b&aacute;t v&agrave; b&ecirc;n cạnh đặt một b&aacute;t nước d&ugrave;ng ri&ecirc;ng cho người n&agrave;o kh&ocirc;ng th&iacute;ch chan nước. Khi ăn trộn đều. Thi thoảng hơi ng&aacute;n c&oacute; thể nhấp một v&agrave;i th&igrave;a nước d&ugrave;ng để sẵn ngo&agrave;i b&aacute;t ri&ecirc;ng c&oacute; thả v&agrave;i l&aacute;t h&agrave;nh l&aacute;.</p>\r\n', '2019-07-24 05:17:51', '1'),
(14, ' Hủ tiếu Nam Vang ', 'Cambodia style rice vermicelli soup with pork, shrimp, pork-liver & quail egg ', '1563945488hu-tieu-nam-vang-2-1501063325.jpg', 65000, '<p>Hủ tiếu Nam Vang, m&oacute;n ăn n&agrave;y c&oacute; nguy&ecirc;n liệu ch&iacute;nh l&agrave; l&ograve;ng heo c&ugrave;ng c&aacute;c phụ liệu kh&aacute;c l&agrave; hủ t&iacute;u kh&ocirc;, nước l&egrave;o hầm từ xương heo, t&ocirc;m, thịt, trứng c&uacute;t, thịt heo băm,&hellip;</p>\r\n', '2019-07-24 05:18:08', '1'),
(15, ' Miến măng gà ', 'Chicken & dry bamboo shoot vermicelli - soup or mixed ', '1563945511mien-mang-ga-1496717069.jpg', 55000, '<p>Măng c&oacute; thể kết hợp v&oacute;i nhiều loại thịt như thịt ch&acirc;n gi&ograve;, thịt vịt, thịt g&agrave; để cho ra những m&oacute;n ăn ngon. Trong số đ&oacute; phải kể đến m&oacute;n miến măng g&agrave;. T&ocirc; miến g&agrave; với đầy đủ sắc m&agrave;u, ngập tr&agrave;n hương vị. Nhờ bổ trợ cho nhau, miếng thịt g&agrave; như đỡ ngậy hơn, v&agrave; miếng măng kh&ocirc; th&igrave; lại đậm đ&agrave; hơn. Miến măng g&agrave; ngon l&agrave; miến c&oacute; nước l&egrave;o trong v&agrave; ngọt thanh, gồm c&oacute; thịt g&agrave; ta th&aacute;i miếng, h&agrave;nh chẻ, miến v&agrave; th&agrave;nh phần ch&iacute;nh l&agrave; măng kh&ocirc;. Măng kh&ocirc; ngon phải c&oacute; độ gi&ograve;n nhất định. Ăn k&egrave;m với rau thơm, l&aacute;t chanh v&agrave; một ch&uacute;t tương ớt.</p>\r\n', '2019-07-24 05:18:31', '1'),
(16, ' Mì xá xíu ', 'Chinese noodles ', '1563945530mixaxiu.jpg', 60000, '<p>Thịt x&aacute; x&iacute;u đậm đ&agrave;, ngọt thơm ăn k&egrave;m c&ugrave;ng m&igrave; trứng dai dai, trộn th&ecirc;m &iacute;t x&agrave; l&aacute;ch m&aacute;t gi&ograve;n khiến m&oacute;n m&igrave; x&aacute; x&iacute;u trở n&ecirc;n rất lạ miệng v&agrave; hấp dẫn. M&igrave; x&aacute; x&iacute;u sẽ l&agrave; bữa ăn &lsquo;đổi gi&oacute;&rsquo; ngon miệng, chất lượng cho gia đ&igrave;nh nhỏ của bạn</p>\r\n', '2019-07-24 05:18:50', '1'),
(17, ' Mì vằn thắn ', 'Chinese noodles ', '1563945550my-van-than-quan-an-ngon-1497429996.jpg', 60000, '<p>Vằn thắn (hay ho&agrave;nh th&aacute;nh, sủi cảo) l&agrave; một m&oacute;n ăn c&oacute; nguồn gốc từ Quảng Đ&ocirc;ng, Trung Quốc. Vằn thắn được l&agrave;m từ thịt, hải sản v&agrave; rau băm nhỏ, g&oacute;i lại bằng vỏ bột m&igrave;, đem hấp ch&iacute;n l&ecirc;n. Sau khi hấp, miếng vằn thắn ch&iacute;n, vỏ bột ch&iacute;n trong nh&igrave;n thấy cả nh&acirc;n b&ecirc;n trong, rất quyến rũ. Mỳ vằn thắn ngon nhất l&agrave; ăn v&agrave;o m&ugrave;a Đ&ocirc;ng trời l&agrave;nh lạnh. Ngồi y&ecirc;n vị trong nh&agrave;, h&uacute;p nước soup ngọt lừ, ăn miếng vằn thắn, thịt x&aacute; x&iacute;u ngon ngọt thấy người khỏe khoắn hẳn, y&ecirc;u đời hơn bao nhi&ecirc;u.</p>\r\n', '2019-07-24 05:19:10', '1'),
(18, ' Phở gà ', 'Chicken PHO noodle-soup or mixed ', '1563945563pho-ga-quan-an-ngon-1498552042.jpg', 50000, '<p>Nước phở trong, g&agrave; ta thịt ngọt, thơm m&ugrave;i g&agrave; v&agrave; l&aacute; chanh. Ăn k&egrave;m tương ớt v&agrave; tương đen, hai loại gia vị l&agrave;m tang th&ecirc;m hương vị đậm đ&agrave; của m&oacute;n phở n&agrave;y. Một t&ocirc; phở kh&ocirc;ng thể thiếu rau quế, h&uacute;ng l&igrave;u, ng&ograve; om, v&agrave;i l&aacute;t chanh tươi v&agrave; ớt. Người miền Nam c&ograve;n ăn phở với gi&aacute; trụng ch&iacute;n v&agrave; sa tế ớt.</p>\r\n', '2019-07-24 05:19:23', '1'),
(19, ' Mỳ Ý sốt bò băm ', 'Spaghetti ', '1563945582my-y-sot-bo-bam-don-gian-ma-ngon_2.jpg', 65000, '<p>M&igrave; &Yacute; sốt b&ograve; băm hay c&ograve;n gọi l&agrave; Spaghetti l&agrave; m&oacute;n ăn ngon v&agrave; phổ biến, d&ugrave;ng k&egrave;m với hỗn hợp sốt c&agrave; chua v&agrave; thịt b&ograve; bằm c&oacute; nguồn gốc từ Italia. Đ&acirc;y l&agrave; m&oacute;n ăn bổ dưỡng v&agrave; nhiều năng lượng m&agrave; bạn c&oacute; thể d&ugrave;ng v&agrave;o buổi s&aacute;ng.</p>\r\n', '2019-07-24 05:19:42', '1'),
(20, ' Phở bò chín ', 'PHO noodle soup with well-done beef ', '1563945594pho-bo-chin-quan-an-ngon-1498551790.jpg', 50000, '<p>Phở l&agrave; m&oacute;n ăn truyền thống từ xa xưa của Việt Nam v&agrave; cũng được xem l&agrave; một m&oacute;n ăn đại diện cho nền ẩm thực Việt Nam thời hiện đại.</p>\r\n', '2019-07-24 05:19:54', '1');

