CREATE TABLE IF NOT EXISTS `drink` (
`ID` int(10) NOT NULL,
  `Name` varchar(100) NOT NULL,
  `Img` varchar(100) NOT NULL,
  `Price` int(10) NOT NULL,
  `Description` text NOT NULL,
  `Created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `Status` int(1) NOT NULL
) ENGINE=InnoDB AUTO_INCREMENT=18 DEFAULT CHARSET=utf8;

--
-- Dumping data for table `drink`
--

INSERT INTO `drink` (`ID`, `Name`, `Img`, `Price`, `Description`, `Created_at`, `Status`) VALUES
(1, 'Cà phê sô cô la', '1564129760caphesocola.jpg', 50000, '<p>Một ly c&agrave; ph&ecirc; s&ocirc;-c&ocirc;-la thơm ngon sẽ gi&uacute;p bạn khoan kho&aacute;i suốt cả ng&agrave;y h&egrave; n&oacute;ng bức, đặc biệt l&agrave; người bị huyết &aacute;p thấp.</p>\r\n', '2019-07-26 08:29:20', 1),
(3, 'Cà phê vani đá xay', '1564129778caphevanidaxay.jpg', 50000, '<p>Dư vị nồng n&agrave;n của c&agrave; ph&ecirc; s&aacute;nh đ&ocirc;i ho&agrave;n hảo với hương vani ngọt ng&agrave;o như v&ograve;ng tay &acirc;u yếm, đệm th&ecirc;m một ch&uacute;t b&eacute;o ngậy của sữa v&agrave; kem tươi, tạo n&ecirc;n vi ngọt đậm m&agrave; h&ograve;a nh&atilde;.</p>\r\n', '2019-07-26 08:29:38', 1),
(4, 'Capuchino', '1564106053capuchino.jpg', 60000, '<p>Cappuccino được gọi vui l&agrave; thức uống &quot;một-phần-ba&quot; - 1/3 Espresso, 1/3 Sữa n&oacute;ng, 1/3 Foam.</p>\r\n', '2019-07-26 01:54:13', 1),
(5, 'Espresso', '1564106099espresso2.jpg', 60000, '<p>Một cốc Espresso nguy&ecirc;n bản được bắt đầu bởi những hạt Arabica chất lượng, phối trộn với tỉ lệ c&acirc;n đối hạt Robusta, cho ra vị ngọt caramel, vị chua dịu v&agrave; s&aacute;nh đặc. Để đạt được sự kết hợp n&agrave;y, ch&uacute;ng t&ocirc;i xay tươi hạt c&agrave; ph&ecirc; cho mỗi lần pha.</p>\r\n', '2019-07-26 01:54:59', 1),
(6, 'Cà phê đen', '1564106133ca phe den.jpg', 40000, '<p>Một t&aacute;ch c&agrave; ph&ecirc; đen thơm ng&agrave;o ngạt, phảng phất m&ugrave;i cacao l&agrave; m&oacute;n qu&agrave; tự thưởng tuyệt vời nhất cho những ai m&ecirc; đắm tinh chất nguy&ecirc;n bản nhất của c&agrave; ph&ecirc;. Một t&aacute;ch c&agrave; ph&ecirc; trầm lắng, thi vị giữa d&ograve;ng đời vồn v&atilde;.</p>\r\n', '2019-07-26 01:55:33', 1),
(7, 'Cà phê sữa đá', '1564106179caphesuada.jpg', 45000, '<p>C&agrave; ph&ecirc; phin kết hợp cũng sữa đặc l&agrave; một s&aacute;ng tạo đầy tự h&agrave;o của người Việt, được xem m&oacute;n uống thương hiệu của Việt Nam.</p>\r\n', '2019-07-26 01:56:19', 1),
(8, 'Victoria 01', '1564106232victoria01.jpg', 45000, '<p>Vị mứt cam xo&agrave;i h&ograve;a trộn độc đ&aacute;o với sữa chua, cho cảm gi&aacute;c chua ngọt rất sướng. Điểm nhấn l&agrave; những mẩu b&aacute;nh cookie gi&ograve;n tan gi&uacute;p sự thưởng thức th&ecirc;m th&uacute; vị.</p>\r\n', '2019-07-26 01:57:12', 1),
(9, 'Cam tươi', '1564106271camtuoi.jpg', 40000, '<p>Nước cam chứa nhiều calci, do đ&oacute; sẽ rất th&iacute;ch hợp cho những ai kh&ocirc;ng th&iacute;ch sữa. Uống nước cam cũng gi&uacute;p tăng cường calci cho răng v&agrave; xương mạnh mẽ, chắc khỏe&hellip;</p>\r\n', '2019-07-26 01:57:51', 1),
(10, 'Trà trái cây', '1564106384hinh-tra-trai-cay.jpg', 45000, '<p>L&agrave; thức uống &quot;bắt vị&quot; được lấy cảm hứng từ tr&aacute;i Vải - thức quả tr&ograve;n đầy, quen thuộc trong cuộc sống người Việt - kết hợp c&ugrave;ng Tr&agrave; &ocirc;long l&agrave;m từ những l&aacute; tr&agrave; tươi hảo hạng.</p>\r\n', '2019-07-26 01:59:44', 1),
(11, 'Kem ổi', '1564106426kemoi.jpg', 40000, '<p>Khi thưởng thức kem ổi bạn sẽ cảm nhận được hương thơm d&igrave;u dịu, thanh m&aacute;t của những tr&aacute;i ổi v&ocirc; c&ugrave;ng hấp dẫn. Hơn nữa trong ổi c&ograve;n c&oacute; rất nhiều vitamin C tốt cho l&agrave;n da của c&aacute;c chị em, gi&uacute;p da căng b&oacute;ng, s&aacute;ng mịn.</p>\r\n', '2019-07-26 02:00:26', 1),
(12, 'Matcha đá xay', '1564106456matchadaxay.jpg', 50000, '<p>Sự ho&agrave; quyện mịn m&agrave;ng giữa vị đắng thanh của c&agrave; ph&ecirc; v&agrave; vị đắng ngọt của chocolate, c&ugrave;ng vị b&eacute;o của kem tươi v&agrave; sữa tươi h&ograve;a quyện.</p>\r\n', '2019-07-26 02:00:56', 1),
(13, 'Noel 01', '1564106511noel1.jpg', 50000, '<p>Mứt Việt Quất chua thanh, ng&ograve;n ngọt, phối hợp nhịp nh&agrave;ng với d&ograve;ng sữa chua bổ dưỡng. L&agrave; m&oacute;n sinh tố thơm ngon m&agrave; cả đầu lưỡi v&agrave; l&agrave;n da đều th&iacute;ch.</p>\r\n', '2019-07-26 02:01:51', 1),
(14, 'Sô cô la đá xay', '1564106550socoladaxay.jpg', 50000, '<p>Vị đắng của c&agrave; ph&ecirc; kết hợp c&ugrave;ng vị cacao ngọt ng&agrave;ocủa s&ocirc;-c&ocirc;-la, vị sữa tươi ngọt b&eacute;o, đem đến trải nghiệm vị gi&aacute;c cực kỳ th&uacute; vị.</p>\r\n', '2019-07-26 02:02:30', 1),
(15, 'Trà đào', '1564106751tradao.jpg', 55000, '<p>Vị thanh ngọt của đ&agrave;o Hy Lạp, vị chua dịu của Cam V&agrave;ng nguy&ecirc;n vỏ, vị ch&aacute;t của tr&agrave; đen tươi được ủ mới mỗi 4 tiếng, c&ugrave;ng hương thơm nồng đặc trưng của sả ch&iacute;nh l&agrave; điểm s&aacute;ng l&agrave;m n&ecirc;n sức hấp dẫn của thức uống n&agrave;y. Sản phẩm hiện c&oacute; 2 phi&ecirc;n bản N&oacute;ng v&agrave; Lạnh ph&ugrave; hợp cho mọi thời gian trong năm.</p>\r\n', '2019-07-26 02:05:51', 1),
(16, 'Nole 04', '1564106868noel4.jpg', 45000, '<p>Mứt Việt Quất chua thanh, ng&ograve;n ngọt, phối hợp nhịp nh&agrave;ng với d&ograve;ng sữa chua bổ dưỡng. L&agrave; m&oacute;n sinh tố thơm ngon m&agrave; cả đầu lưỡi v&agrave; l&agrave;n da đều th&iacute;ch.</p>\r\n', '2019-07-26 02:07:48', 1),
(17, 'Trà tranh', '1564116622thucuongmuahe04.jpg', 40000, '<p>Tr&agrave; chanh l&agrave; thức uống được nhiều bạn trẻ y&ecirc;u th&iacute;ch. Đặc biệt tại c&aacute;c vỉa h&egrave; của đường phố H&agrave; Nội, tr&agrave; chanh đ&atilde; thực sự trở th&agrave;nh &ldquo;mốt&rdquo;. Tr&agrave; chanh kh&ocirc;ng những c&oacute; t&aacute;c dụng giải nhiệt m&ugrave;a h&egrave; m&agrave; n&oacute; c&ograve;n c&oacute; nhiều t&aacute;c dụng tốt cho sức khỏe.</p>\r\n', '2019-07-26 04:50:22', 1);

