--
-- Table structure for table `news`
--

CREATE TABLE IF NOT EXISTS `news` (
`id` int(11) NOT NULL COMMENT 'khóa chính',
  `title` varchar(255) NOT NULL COMMENT 'Tên bài viết',
  `category_id` int(11) DEFAULT NULL COMMENT 'Bài việc thuộc danh mục nào',
  `admin_id` int(11) DEFAULT NULL COMMENT 'Bài viết do admin nào tạo',
  `avatar` varchar(255) DEFAULT NULL COMMENT 'Ảnh đại diện của bài viết',
  `summary` varchar(500) DEFAULT NULL COMMENT 'Mô tả ngắn cho bài viết',
  `content` text COMMENT 'Nội dung chi tiết bài viết',
  `comment_total` int(11) DEFAULT NULL COMMENT 'Tổng số bình luận cho bài viết',
  `author` varchar(100) NOT NULL COMMENT 'Tổng số like của bài viết',
  `view` int(11) DEFAULT NULL COMMENT 'Tổng số lượt xem bài viết',
  `status` tinyint(3) DEFAULT NULL COMMENT 'Trạng thái bài viết, nhận 2 giá trị sau: 0 - Disabled, 1 - Active',
  `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'Ngày tạo bài viết, tự động sinh theo thời gian hiện tại'
) ENGINE=InnoDB AUTO_INCREMENT=35 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;

--
-- Dumping data for table `news`
--

INSERT INTO `news` (`id`, `title`, `category_id`, `admin_id`, `avatar`, `summary`, `content`, `comment_total`, `author`, `view`, `status`, `created_at`) VALUES
(1, 'Cách nhận biết cafe ngon, cà phê nguyên chất', 1, 1, '', 'Làm thế nào nhận biết bịch cà phê bạn mua về thực sự là bịch cà phê? Làm thế nào để biết ly cà phê bạn đang uống là cà phê có lợi cho sức khỏe, cho tuổi thọ và cho nhan sắc của bạn.', '<p>C&acirc;u trả lời qu&aacute; dễ, kh&ocirc;ng kh&oacute; t&iacute; n&agrave;o. C&agrave; ph&ecirc; l&agrave; một loại hạt rất đặc biệt, v&agrave; kh&aacute;c biệt, c&oacute; thể dễ d&agrave;ng ph&acirc;n biệt c&agrave; ph&ecirc; nguy&ecirc;n chất v&agrave; kh&ocirc;ng nguy&ecirc;n chất, bột c&agrave; ph&ecirc; với bột của c&aacute;c loại hạt ngũ cốc kh&aacute;c. Để bảo đảm cho sức khỏe v&agrave; khẩu vị thưởng thức c&agrave; ph&ecirc; đ&iacute;ch thực của ch&iacute;nh bạn, bạn cần ghi nhớ v&agrave;i chi tiết căn bản về thuộc t&iacute;nh của hạt c&agrave; ph&ecirc; rang rất kh&aacute;c với c&aacute;c loại hạt rang kh&aacute;c v&agrave; bột của ch&uacute;ng:</p>\r\n\r\n<p>I. PH&Acirc;N BIỆT TRƯỚC KHI PHA - TỨC NHẬN BIẾT BỘT C&Agrave; PH&Ecirc; NGUY&Ecirc;N CHẤT</p>\r\n\r\n<p><img src="imgs/news1/img1.jpg" /></p>\r\n\r\n<p>Bột c&agrave; ph&ecirc; nhẹ, tơi, xốp, khối lượng ri&ecirc;ng nhỏ</p>\r\n\r\n<p>1. Khối lượng (hoặc thể t&iacute;ch) của bột c&agrave; ph&ecirc; rang bao giờ cũng lớn hơn khối lượng (hoặc thể t&iacute;ch) của bột hạt đậu n&agrave;nh (đỗ tương) v&agrave; bắp rang:</p>\r\n\r\n<p>Hạt c&agrave; ph&ecirc; c&oacute; một đặc điểm kh&aacute;c hẳn c&aacute;c loại hạt kh&aacute;c l&agrave; khi rang l&ecirc;n đến 1 nhiệt độ nhất định sẽ nở lớn v&agrave; thể t&iacute;ch tăng từ 1,5-2 lần v&agrave; trọng lượng giảm từ 20-30%. Cho n&ecirc;n, bột c&agrave; ph&ecirc; lu&ocirc;n lu&ocirc;n c&oacute; khối lượng ri&ecirc;ng thấp hơn bột c&aacute;c loại ngũ cốc kh&aacute;c v&agrave; từ đ&oacute; thể t&iacute;ch (hay khối lượng) của 1 kg bột c&agrave; ph&ecirc; lu&ocirc;n lu&ocirc;n lớn hơn thể t&iacute;ch của bột c&aacute;c loại ngũ cốc rang. Dựa v&agrave;o đặc t&iacute;nh n&agrave;y bạn c&oacute; thể ph&acirc;n biệt ngay từ l&uacute;c đầu, d&ugrave; chưa cần phải mở bao b&igrave; ra. Nếu c&oacute; điều kiện so s&aacute;nh, bạn cầm trong tay 2 bịch 500g, bịch n&agrave;o chứa c&agrave; ph&ecirc;, (hay chứa tỷ lệ bột c&agrave; ph&ecirc; nhiều) th&igrave; sẽ nhiều, đầy hơn, to hơn, khối lượng bột chứa b&ecirc;n trong nhiều hơn n&ecirc;n ch&uacute;ng ta c&oacute; cảm tưởng bịch c&agrave; ph&ecirc; nguy&ecirc;n chất nhẹ hơn.</p>\r\n\r\n<p>2. Độ xốp của bột c&agrave; ph&ecirc;:</p>\r\n\r\n<p>Nh&igrave;n theo cảm quan, bột c&agrave; ph&ecirc; nguy&ecirc;n chất rất nhẹ, c&oacute; độ xốp , tơi v&agrave; rời. Bột của hạt ngũ cốc kh&aacute;c thường d&iacute;nh lại, &iacute;t tơi bong hơn. Nếu bạn c&oacute; một bịch c&agrave; ph&ecirc; nguy&ecirc;n chất v&agrave; một bịch c&agrave; ph&ecirc; kh&ocirc;ng nguy&ecirc;n chất, bạn mở 2 bịch ra, lấy 2 ch&eacute;n nước, m&uacute;c 2 muỗng bột của 2 bịch đổ l&ecirc;n mặt nước. Bột c&agrave; ph&ecirc; nguy&ecirc;n chất xốp nhẹ, c&oacute; khối lượng ri&ecirc;ng thấp, n&ecirc;n c&oacute; khuynh hướng nổi l&ecirc;n tr&ecirc;n, c&ograve;n bột của c&aacute;c hạt ngũ cốc kh&aacute;c c&oacute; khối lượng ri&ecirc;ng lớn hơn, n&ecirc;n ch&igrave;m xuống nhanh hơn. Ngo&agrave;i ra, hạt c&agrave; ph&ecirc; rang rất gi&ograve;n, c&oacute; cấu tr&uacute;c sợi cellulose đồng đều, dễ vỡ v&agrave; vỡ đều trong cối xay, n&ecirc;n bột c&agrave; ph&ecirc; xay ra c&oacute; độ xốp, mịn tương đối đồng đều. Tr&aacute;i lại c&aacute;c loại hạt đậu, bắp rang, khi xay bột độ mịn kh&ocirc;ng đồng đều n&ecirc;n kh&ocirc;ng c&oacute; độ tơi xốp như bột c&agrave; ph&ecirc; nguy&ecirc;n chất.</p>\r\n\r\n<p>3. Độ ẩm của bột c&agrave; ph&ecirc;:</p>\r\n\r\n<p><img src="imgs/news1/img2.jpg" /></p>\r\n\r\n<p>Bột c&agrave; ph&ecirc; m&agrave;u n&acirc;u đậm, độ mịn đồng đều.</p>\r\n\r\n<p>Bột c&agrave; ph&ecirc; nguy&ecirc;n chất &iacute;t ngậm nước, kh&ocirc;ng c&oacute; nhiều độ ẩm. Bột c&aacute;c loại ngũ cốc kh&aacute;c thường giữ nước v&agrave; c&oacute; độ ẩm cao hơn. Hơn nữa, do hạt đậu v&agrave; bắp kh&ocirc;ng c&oacute; m&ugrave;i thơm, n&ecirc;n khi nh&agrave; sản xuất trộn c&aacute;c loại hạt n&agrave;y v&agrave;o hạt c&agrave; ph&ecirc; chắc chắn sẽ l&agrave;m 1 động t&aacute;c k&egrave;m theo, đ&oacute; l&agrave; rưới h&oacute;a chất, hương liệu c&agrave; ph&ecirc; tổng hợp, nh&acirc;n tạo v&agrave;o trước khi xay ra bột. Do đ&oacute;, bột c&agrave; ph&ecirc; pha tạp, kh&ocirc;ng nguy&ecirc;n chất c&oacute; vẻ ẩm ướt, thậm ch&iacute; v&oacute;n cục khi được tẩm nhiều caramen tạo m&agrave;u, kh&aacute;c hẳn với bột c&agrave; ph&ecirc; nguy&ecirc;n chất rất kh&ocirc; v&agrave; tơi xốp.</p>\r\n\r\n<p>4. M&agrave;u của bột c&agrave; ph&ecirc;:</p>\r\n\r\n<p>Khi rang đến nhiệt độ v&agrave; thời gian th&iacute;ch hợp&hellip;bột c&agrave; ph&ecirc; c&oacute; m&agrave;u n&acirc;u đậm (nếu hạt c&agrave; ph&ecirc; rang chưa đến nhiệt độ v&agrave; thời gian nổ lần 1 th&igrave; bột c&oacute; m&agrave;u v&agrave;ng s&aacute;ng vị chua, v&agrave; m&ugrave;i nồng). Hạt bắp rang để độn v&agrave;o c&agrave; ph&ecirc; thường c&oacute; m&agrave;u đen đậm. Cho n&ecirc;n trong nghề l&agrave;m c&agrave; ph&ecirc; người ta đơn giản gọi bắp rang l&agrave; M&agrave;u. Do th&oacute;i quen người ti&ecirc;u d&ugrave;ng đ&ograve;i hỏi ly c&agrave; ph&ecirc; phải c&oacute; m&agrave;u đen, n&ecirc;n M&agrave;u (b&ecirc;n cạnh caramen v&agrave; cả chất tạo m&agrave;u h&oacute;a học) được d&ugrave;ng để nhuộm ly c&agrave; ph&ecirc;. Mặt kh&aacute;c, hạt c&aacute;c đậu n&agrave;nh rang v&agrave; xay ra bột c&oacute; m&agrave;u n&acirc;u đậm đục, ng&atilde; v&agrave;ng đục, ho&agrave;n to&agrave;n kh&ocirc;ng giống m&agrave;u n&acirc;u đậm của bột c&agrave; ph&ecirc;. Nếu bạn nh&igrave;n thấy bột trong 1 bịch chứa c&oacute; m&agrave;u n&acirc;u đậm ng&atilde; v&agrave;ng, thể t&iacute;ch nhỏ, nhưng cầm nặng tay l&agrave; tỷ lệ đậu nhiều. Bột c&oacute; m&agrave;u đen thui thể t&iacute;ch cũng nhỏ l&agrave; c&oacute; trộn nhiều bắp.</p>\r\n\r\n<p>5. M&ugrave;i của bột c&agrave; ph&ecirc;:</p>\r\n\r\n<p>Nếu quen thuộc, bạn kh&ocirc;ng kh&oacute; để nhận ra m&ugrave;i thơm rất dễ chịu, hấp dẫn v&agrave; rất đặc trưng của bột c&agrave; ph&ecirc; nguy&ecirc;n chất. Nhiều người vẫn bị lầm lẫn v&agrave; đ&aacute;nh gi&aacute; cao m&ugrave;i của hương liệu h&oacute;a học được tẩm v&agrave;o đậu, bắp bởi v&igrave; họ &iacute;t c&oacute; dịp ngửi m&ugrave;i c&agrave; ph&ecirc; bột nguy&ecirc;n chất. Bắp v&agrave; đậu n&agrave;nh cũng c&oacute; m&ugrave;i hơi tanh, theo cảm quan, nếu tinh tế một ch&uacute;t, bạn c&oacute; thể nhận thấy khi ngửi. Bột đậu n&agrave;nh c&oacute; m&ugrave;i gắt, h&ograve;a quyện với hương liệu bốc l&ecirc;n một m&ugrave;i thơm nặng nề chứ kh&ocirc;ng dịu d&agrave;ng như m&ugrave;i nguy&ecirc;n thủy của c&agrave; ph&ecirc; rang.</p>\r\n\r\n<p>II. PH&Acirc;N BIỆT ĐANG KHI PHA - TỨC NHẬN BIẾT TRẠNG TH&Aacute;I CỦA BỘT C&Agrave; PH&Ecirc; NGUY&Ecirc;N CHẤT KHI GẶP NƯỚC S&Ocirc;I</p>\r\n\r\n<p>Đ&acirc;y l&agrave; điểm m&agrave; bạn rất dễ d&agrave;ng nhận biết để ph&acirc;n biệt ch&iacute;nh x&aacute;c c&agrave; ph&ecirc; nguy&ecirc;n chất với c&agrave; ph&ecirc; pha tạp hay bột của c&aacute;c loại hạt ngũ cốc kh&aacute;c. Như đ&atilde; nhận định, do hạt c&agrave; ph&ecirc; được cấu tạo bởi cấu tr&uacute;c sợi celluose v&agrave; chứa rất &iacute;t tinh bột, n&ecirc;n thuộc t&iacute;nh đặc biệt của bột c&agrave; ph&ecirc; rang l&agrave; rất tơi xốp, v&agrave; chứa nhiều khoang kh&ocirc;ng kh&iacute; b&ecirc;n trong do cấu tr&uacute;c cao ph&acirc;n tử, c&aacute;c sợi cellulose bị bẻ gảy dưới t&aacute;c động nhiệt trong qu&aacute; tr&igrave;nh rang &hellip;Cho n&ecirc;n, khi bạn chế nước s&ocirc;i 100 độ C v&agrave;o phin chứa c&agrave; ph&ecirc; nguy&ecirc;n chất, lập tức bột c&agrave; ph&ecirc; sẽ nở phồng l&ecirc;n, sủi bọt mạnh, thậm ch&iacute; tr&agrave;n ra cả ngo&agrave;i phin. Nếu sau khi cho v&agrave;i muỗng bột (khoảng 20-25g) v&agrave;o 1 phin pha c&agrave; ph&ecirc;, bạn chế nước s&ocirc;i v&agrave;o m&agrave; thấy bột ấy kh&ocirc;ng nở phồng l&ecirc;n, tr&aacute;i lại c&ograve;n bẹp xuống , lịm xuống v&agrave; bốc m&ugrave;i thơm lan tỏa ra nồng nực th&igrave; bạn biết chắc chắn trong phin nầy c&oacute; rất rất &iacute;t c&agrave; ph&ecirc;. Tr&aacute;i lại, trong loại bột nầy c&oacute; tỷ lệ phần trăm rất nhiều bột của c&aacute;c loại hạt kh&aacute;c được tẩm hương liệu nh&acirc;n tạo nồng độ cao. Bột bắp , bột đậu rang khi gặp nước s&ocirc;i trở n&ecirc;n dẽo , d&iacute;nh bệt v&agrave; xẹp xuống do c&aacute;c loại ngũ cốc lu&ocirc;n c&oacute; chứa nhiều tinh bột. C&agrave; ph&ecirc; kh&ocirc;ng phải l&agrave; một loại hạt ngũ cốc. Tr&aacute;i lại c&agrave; ph&ecirc; được cấu tạo bởi c&aacute;c hợp chất cao ph&acirc;n tử cellulose, chứa rất &iacute;t tinh bột. Qu&aacute; qu&aacute; tr&igrave;nh rang hạt c&agrave; ph&ecirc; nở lớn, b&ecirc;n trong tạo ra c&aacute;c khoang kh&ocirc;ng kh&iacute;, gặp nước s&ocirc;i, kh&ocirc;ng kh&iacute; b&ecirc;n trong nở lớn, l&agrave;m sủi bọt bột c&agrave; ph&ecirc; v&agrave; khiến bột c&agrave; ph&ecirc; tr&agrave;o l&ecirc;n trong phin. Điều nầy rất để nhận thấy.</p>\r\n\r\n<p>III. PH&Acirc;N BIỆT SAU KHI PHA - TỨC NHẬN BIẾT NƯỚC CỦA LY C&Agrave; PH&Ecirc; NGUY&Ecirc;N CHẤT</p>\r\n\r\n<p><img src="imgs/news1/img3.jpg" /></p>\r\n\r\n<p>M&agrave;u nước n&acirc;u c&aacute;nh gi&aacute;n, n&acirc;u trong trẻo như hổ ph&aacute;ch, tuyệt đẹp</p>\r\n\r\n<p>1. M&agrave;u của nước c&agrave; ph&ecirc;:</p>\r\n\r\n<p>C&agrave; ph&ecirc; l&agrave; một loại hạt kh&aacute; kỳ lạ. D&ugrave; bạn rang n&oacute; đến nhiệt độ cao bao nhi&ecirc;u, thời gian l&acirc;u bao nhi&ecirc;u, v&agrave; ch&aacute;y gần th&agrave;nh than, rồi bạn xay ra bột v&agrave; pha th&agrave;nh ly c&agrave; ph&ecirc;, th&igrave; m&agrave;u nước của n&oacute; cũng kh&ocirc;ng hề c&oacute; m&agrave;u đen thui, đen đục v&agrave; đen đậm như thường nh&igrave;n thấy c&aacute;c ly c&agrave; ph&ecirc; ở phần lớn c&aacute;c qu&aacute;n. Ly c&agrave; ph&ecirc; nguy&ecirc;n chất v&agrave; l&agrave;nh mạnh lu&ocirc;n c&oacute; m&agrave;u n&acirc;u từ c&aacute;nh gi&aacute;n đến n&acirc;u đậm, khi cho đ&aacute; v&agrave;o sẽ c&oacute; m&agrave;u n&acirc;u hổ ph&aacute;ch, một m&agrave;u n&acirc;u trong trẻo rất quyến rũ. Để ra &aacute;nh nắng, nh&igrave;n ly c&agrave; ph&ecirc; đ&aacute; c&oacute; m&agrave;u n&acirc;u s&aacute;ng lung linh. Kh&ocirc;ng hiểu v&igrave; sao nhiều người th&iacute;ch c&oacute; ly nước phải c&oacute; m&agrave;u đen thui đen th&ugrave;i, m&agrave;u đen đục của hạt đậu, hạt bắp rang thay v&igrave; m&agrave;u n&acirc;u hổ ph&aacute;ch tinh anh, trong s&aacute;ng của ly c&agrave; ph&ecirc; nguy&ecirc;n chất. N&oacute;i theo cảm quan, thực phẩm ăn uống m&agrave; c&oacute; m&agrave;u đen thui th&ugrave;i th&igrave; đ&aacute;ng ra ch&uacute;ng ta phải n&ecirc;n c&acirc;n nhắc nhiều hơn l&agrave; dễ d&atilde;i t&aacute;n th&agrave;nh. Nếu quen với c&agrave; ph&ecirc; nguy&ecirc;n chất, nh&igrave;n ly nước m&agrave;u n&acirc;u c&aacute;ch gi&aacute;n trong trẻo tự nhi&ecirc;n ch&uacute;ng ta đ&atilde; c&oacute; 1 cảm gi&aacute;c sạch, vệ sinh, thay cho cảm gi&aacute;c &aacute;i ngại khi nh&igrave;n ly nước đen th&ugrave;i. C&agrave; ph&ecirc; nguy&ecirc;n chất khi pha c&agrave; ph&ecirc; sữa sẽ kh&ocirc;ng cho m&agrave;u n&acirc;u đậm m&agrave; chỉ cho một m&agrave;u n&acirc;u nhạt. Nhiều người ưa th&iacute;ch m&agrave;u n&acirc;u đậm do hạt đậu, v&agrave; bắp rang ch&aacute;y tạo ra cho ly c&agrave; ph&ecirc; sữa. Kh&ocirc;ng biết ưa th&iacute;ch như vậy để l&agrave;m g&igrave;?</p>\r\n\r\n<p>Nước c&agrave; ph&ecirc; kh&ocirc;ng s&aacute;nh kẹo, dẽo quẹo, độ s&aacute;nh gần như nước uống th&ocirc;ng thường v&igrave; hạt c&agrave; ph&ecirc; chứa &iacute;t tinh bột.</p>\r\n\r\n<p>2. Độ s&aacute;nh của nước c&agrave; ph&ecirc;:</p>\r\n\r\n<p>Khi pha, nước của ly c&agrave; ph&ecirc; nguy&ecirc;n chất c&oacute; độ s&aacute;nh hầu như kh&ocirc;ng đ&aacute;ng kể. Tr&aacute;i với nước của bột bắp rang hay bột đậu rang, vốn chứa rất nhiều tinh bột, n&ecirc;n rất rất s&aacute;nh, rất đặc kẹo, thậm ch&iacute; l&agrave; s&aacute;nh dẽo. C&aacute;c b&agrave; nội trợ vẫn hay d&ugrave;ng bột bắp trong nh&agrave; bếp v&igrave; t&iacute;nh chất nầy. Một số người ti&ecirc;u d&ugrave;ng c&ograve;n tự h&agrave;o rằng m&igrave;nh l&agrave; người hiểu biết về c&agrave; ph&ecirc;, cho rằng, ly cafe ngon th&igrave; nước trong ly c&agrave; ph&ecirc; phải kẹo kẹo, b&aacute;m tr&ecirc;n th&agrave;nh ly v&agrave; phải &ldquo;&ocirc;m đ&aacute;&rdquo; v&agrave; vi&ecirc;n đ&aacute; trong ly c&agrave; ph&ecirc; phải c&oacute; m&agrave;u n&acirc;u do nước c&agrave; ph&ecirc; song s&aacute;nh, dẽo kẹo b&aacute;m lấy đ&aacute;. Nhưng điều đ&oacute; ch&iacute;nh l&agrave; bằng chứng cho thấy trong ly c&agrave; ph&ecirc; ấy chỉ c&oacute; rất &iacute;t c&agrave; ph&ecirc; m&agrave; to&agrave;n đậu n&agrave;nh rang. Đậu n&agrave;nh rang chứa nhiều tinh bột hiển nhi&ecirc;n l&agrave; rất &ldquo;&ocirc;m đ&aacute;&rdquo;. C&agrave; ph&ecirc; đ&iacute;ch thật kh&ocirc;ng s&aacute;nh dẽo v&agrave; kh&ocirc;ng &ldquo;&ocirc;m đ&aacute;&rdquo;.</p>\r\n\r\n<p>M&ugrave;i hương c&agrave; ph&ecirc; kh&ocirc;ng mạnh mẽ, th&ocirc; bạo dai dẵng như m&ugrave;i h&oacute;a chất nhưng dịu d&agrave;ng, hấp dẫn, tinh tế m&agrave; s&acirc;u lắng... đ&aacute;nh thức cảm x&uacute;c con người.</p>\r\n\r\n<p>3. M&ugrave;i thơm của ly c&agrave; ph&ecirc; pha:</p>\r\n\r\n<p>C&agrave; ph&ecirc; bột lẫn c&agrave; ph&ecirc; pha c&oacute; một m&ugrave;i thơm rất đặc trưng, rất quyến rũ. Tuy nhi&ecirc;n đ&acirc;y c&oacute; thể l&agrave; điểm bạn rất kh&oacute; ph&acirc;n biệt nhất. C&oacute; thể n&oacute;i bạn chỉ c&oacute; thể ph&acirc;n biệt được m&ugrave;i của ly c&agrave; ph&ecirc; nguy&ecirc;n chất với m&ugrave;i của hương liệu h&oacute;a học khi bạn đ&atilde; nhiều lần v&agrave; thường xuy&ecirc;n uống c&agrave; ph&ecirc; nguy&ecirc;n chất. Nhiều loại c&agrave; ph&ecirc; bột điều đ&atilde; được tẩm hương liệu. Hương thơm đ&iacute;ch thực v&agrave; nguy&ecirc;n thủy của c&agrave; ph&ecirc; kh&ocirc;ng nồng nực, kh&ocirc;ng th&ocirc; bạo, kh&ocirc;ng mạnh mẽ, nhưng dịu d&agrave;ng, lưu luyến, thanh cao, tinh tế v&agrave; s&acirc;u lắng, đ&ocirc;i l&uacute;c l&agrave;m ng&acirc;y ngất người y&ecirc;u c&agrave; ph&ecirc;&hellip; Thật kh&oacute; diễn tả, nhưng nếu quen với m&ugrave;i c&agrave; ph&ecirc; nguy&ecirc;n chất bạn sẽ ph&aacute;t hiện ra m&ugrave;i h&oacute;a chất tuy giả kh&aacute; giống m&ugrave;i c&agrave; ph&ecirc; nhưng vẫn l&agrave; giả tạo, vẫn mang hương vị gay gắt, tuy mạnh mẽ, dai dẵng nhưng th&ocirc; thiển v&agrave; g&acirc;y cảm gi&aacute;c nặng nề, kh&ocirc;ng giống như m&ugrave;i thanh cao của ch&iacute;nh hạt c&agrave; ph&ecirc; đ&iacute;ch thực&hellip;</p>\r\n\r\n<p>4. Vị của c&agrave; ph&ecirc;:</p>\r\n\r\n<p>C&agrave; ph&ecirc; khi rang với thời gian đủ v&agrave; đạt đến nhiệt độ th&iacute;ch hợp sẽ cho ch&uacute;ng ta ly c&agrave; ph&ecirc; c&oacute; vị đắng thanh xen lẫn vị chua nhẹ nh&agrave;ng, rất tinh tế do yếu tố của c&aacute;c th&agrave;nh phần acid ẩn chứa đằng sau của vị đắng trong hạt c&agrave; ph&ecirc;. Thật ra, c&aacute;c giống cafe ngon nhất tr&ecirc;n thế giới thuộc d&ograve;ng Arabica, c&oacute; gi&aacute; trị thưởng thức v&agrave; gi&aacute; trị thương phẩm h&agrave;ng đầu đều c&oacute; hậu vị chua thanh rất quyến rũ h&ograve;a quyện tinh tế với vị đắng tự nhi&ecirc;n. Tuy nhi&ecirc;n, do nhận thức kh&ocirc;ng đ&uacute;ng, do truyền thống v&agrave; do th&oacute;i quen kh&ocirc;ng th&iacute;ch đ&aacute;ng, phần lớn người Việt kh&ocirc;ng nghĩ rằng c&agrave; ph&ecirc; c&oacute; vị chua. Thậm ch&iacute; một số kh&ocirc;ng chịu chấp nhận một thuộc t&iacute;nh cố hữu, tuyệt vời của c&agrave; ph&ecirc; hảo hạng, đ&oacute; l&agrave; vị chua thanh quyến rũ của n&oacute;. Để đ&aacute;p ứng với y&ecirc;u cầu v&agrave; thị hiếu về khẩu vị tr&aacute;i ngược lại với thi&ecirc;n nhi&ecirc;n, với tự nhi&ecirc;n nầy của người ti&ecirc;u d&ugrave;ng, một số nh&agrave; sản xuất đ&atilde; cố gắng l&agrave;m cho c&agrave; ph&ecirc; mất đi vị chua thanh vốn c&oacute; của n&oacute; bằng c&aacute;ch tẩm v&agrave;o bột c&agrave; ph&ecirc; c&aacute;c loại h&oacute;a chất tạo đắng c&oacute; gốc kh&aacute;ng sinh. Hậu quả, ly c&agrave; ph&ecirc; thường c&oacute; vị đắng nh&acirc;n tạo, ti&ecirc;u diệt hẳn vị chua thanh quyến rũ của c&agrave; ph&ecirc;. Bạn n&ecirc;n tr&aacute;nh xu hướng đi t&igrave;m v&agrave; nghiện vị đắng kh&ocirc;ng tự nhi&ecirc;n đ&oacute;, v&igrave; n&oacute; sẽ ph&aacute; hủy khẩu vị ch&acirc;n ch&iacute;nh của bạn về m&ugrave;i vị đ&iacute;ch thực của c&agrave; ph&ecirc;. Nhiều người được phục vụ &quot;c&agrave; ph&ecirc; đậu n&agrave;nh&quot; nhiều ng&agrave;y nhiều th&aacute;ng n&ecirc;n đ&atilde; quen c&aacute;i vị đậm l&egrave; của đậu n&agrave;nh rang, e rằng sẽ ch&ecirc; c&agrave; ph&ecirc; nghuy&ecirc;n chất kh&ocirc;ng đậm đ&agrave;. Thật ra, c&agrave; ph&ecirc; nguy&ecirc;n chất rất đậm đ&agrave; hương vị, nhưng do khẩu vị c&agrave; ph&ecirc; của ch&uacute;ng ta đ&atilde; bị ph&aacute; hủy dữ dội. Ch&iacute;nh khẩu vị uống c&agrave; ph&ecirc; đậm đặc từ đậu rang của ch&uacute;ng ta cũng đ&atilde; được huấn luyện v&agrave; x&acirc;y dựng từ nhiều chục năm nay. Điều đ&oacute; cho thấy khẩu vị c&oacute; thể được điều chỉnh. Nếu hiểu biết c&aacute;c thuộc t&iacute;nh của c&agrave; ph&ecirc;, bạn sẽ kh&ocirc;ng khăng khăng đ&ograve;i ly nước đen thui, đậm l&egrave;, đắng ngắt của bột đậu rang ch&aacute;y kh&eacute;t tẩm đủ mọi loại hương liệu bốc m&ugrave;i thơm nồng nực. Bạn sẽ tự chọn cho m&igrave;nh khẩu vị đ&uacute;ng.</p>\r\n\r\n<p>5. Bọt của c&agrave; ph&ecirc;:</p>\r\n\r\n<p><img src="imgs/news1/img4.jpg" /></p>\r\n\r\n<p>Bọt c&agrave; ph&ecirc; &iacute;t, m&agrave;u n&acirc;u đẹp, mau tan</p>\r\n\r\n<p>Bản th&acirc;n nước pha c&agrave; ph&ecirc; khi đ&aacute;nh l&ecirc;n với đường cũng tạo ra 1 &iacute;t bọt m&agrave;u n&acirc;u s&aacute;ng tr&ocirc;ng rất đẹp. Nhưng c&oacute; 1 số người ti&ecirc;u d&ugrave;ng ngộ nhận v&agrave; y&ecirc;u cầu qu&aacute; đ&aacute;ng về ly c&agrave; ph&ecirc; phải c&oacute; bọt đẹp. Để đ&aacute;p ứng đ&ograve;i hỏi của kh&aacute;ch h&agrave;ng hầu giữ kh&aacute;ch v&agrave; thu lợi nhuận một số nh&agrave; sản xuất cho chất tẩy rửa bề mặt v&agrave;o c&agrave; ph&ecirc; để tạo bọt. Vậy, bạn cũng cần biết c&aacute;ch ph&acirc;n biệt hai thứ bọt n&agrave;y. Nếu bọt mỏng tanh, c&oacute; &oacute;ng &aacute;nh m&agrave;u cầu vồng, đ&aacute;nh l&ecirc;n đầy cả ly v&agrave; rất l&acirc;u tan nh&igrave;n ly c&agrave; ph&ecirc; c&oacute; vẻ kh&aacute; đẹp th&igrave; chắc chắn đấy l&agrave; bọt x&agrave; b&ocirc;ng. Bọt c&agrave; ph&ecirc; ti&ecirc;u biểu kh&aacute; đồng đều về k&iacute;ch cỡ, đục hơn v&agrave; tr&ocirc;ng &quot;d&agrave;y&quot; hơn, nhưng mau xẹp xuống.</p>\r\n\r\n<p>IV. PH&Acirc;N BIỆT CUỐI C&Ugrave;NG, RẤT THỰC TIỄN, L&Agrave; TRẢI NGHIỆM CỦA CH&Iacute;NH BẠN</p>\r\n\r\n<p>Cuối c&ugrave;ng, rất thực tiễn, l&agrave; ch&iacute;nh bạn, v&agrave;o 1 ng&agrave;y đẹp trời n&agrave;o đ&oacute; , bạn h&atilde;y bỏ ch&uacute;t th&igrave; giờ ra tiệm b&aacute;n c&agrave; ph&ecirc; hạt đ&atilde; rang mua v&agrave;i lạng c&agrave; ph&ecirc; mộc. Khi mua bạn cần xem x&eacute;t kỹ đ&oacute; l&agrave; c&agrave; ph&ecirc; hạt rang nguy&ecirc;n chất, mộc mạc, kh&ocirc;ng trộn hạt g&igrave; kh&aacute;c v&agrave; kh&ocirc;ng tẩm c&aacute;c phụ gia n&agrave;o kh&aacute;c. Mua xong, bạn nhờ xay ra bột. Đem về, s&aacute;ng h&ocirc;m sau, bạn tự tay, khoan thai, pha 1 ly c&agrave; ph&ecirc;. Pha xong, bạn đến ngồi 1 chỗ tĩnh lặng, b&igrave;nh y&ecirc;n. Trong c&aacute;i kh&ocirc;ng gian y&ecirc;n l&agrave;nh v&agrave; tinh anh của buổi s&aacute;ng, với t&acirc;m hồn thanh tịnh, bạn ngồi đ&oacute; từ từ uống từng ngụm nhỏ c&agrave; ph&ecirc; n&oacute;ng, nguy&ecirc;n chất... Đ&acirc;y l&agrave; c&aacute;ch tốt nhất để nếm biết hương vị tinh thuần của c&agrave; ph&ecirc;.</p>\r\n\r\n<p><img src="imgs/news1/img5.jpg" /></p>\r\n\r\n<p>Ch&iacute;nh bạn trải nghiệm l&agrave; hay nhất</p>\r\n', 30, 'Nguyễn Thanh Tình - PHP39 (Sưu tầm)', NULL, 1, '2019-07-28 11:46:11'),
(15, 'Kinh nghiệm mở quán & kinh doanh cafe hiệu quả', 1, 1, '', 'Bạn đang có một số vốn nhàn rỗi và yêu thích mở một quán cà phê để kinh doanh nhưng lại không biết bắt đầu như thế nào? Mở quán cà phê cần gì, làm sao để quán cà phê của bạn cạnh tranh với các quán cà phê khác, làm sao để quán cà phê của bạn thu hút khách và nhiều người biết đến? với nhiều câu hỏi và băn khoăn như vậy, hãy để chúng tôi giúp bạn.', '<p>I. Sơ lược c&aacute;c yếu tố cần thiết để mở qu&aacute;n c&agrave; ph&ecirc;</p>\r\n\r\n<p>Ng&agrave;y nay với nền kinh tế đang tăng trưởng v&agrave; ph&aacute;t triển th&igrave; nhu cầu thị hiếu ng&agrave;y c&agrave;ng cao, c&aacute;c qu&aacute;n c&agrave; ph&ecirc; nhiều v&ocirc; số kể nhưng rất &iacute;t qu&aacute;n thực sự nổi bật, để thu h&uacute;t được kh&aacute;ch h&agrave;ng th&igrave; kh&ocirc;ng chỉ cần &ldquo;ngon&rdquo; l&agrave; đủ m&agrave; phải c&oacute; t&iacute;nh &ldquo;thẩm mỹ&rdquo;, ngo&agrave;i ra c&ograve;n phải &ldquo;lạ&rdquo; hoặc đ&ocirc;i khi tương đối &ldquo;độc&rdquo;.<br />\r\nDo vậy &yacute; tưởng v&agrave; thiết kế qu&aacute;n rất quan trọng khi khởi sự một qu&aacute;n c&agrave; ph&ecirc;. Ngo&agrave;i sự chuẩn bị chu đ&aacute;o về mặt kế hoạch, nếu bạn l&agrave; một người mới v&agrave;o nghề th&igrave; cần phải t&igrave;m hiểu v&agrave; nghi&ecirc;n cứu thật kĩ c&aacute;c m&ocirc; h&igrave;nh kinh doanh để chọn cho m&igrave;nh một hướng đi ph&ugrave; hợp.<br />\r\nĐể khởi nghiệp tốt trong một ng&agrave;nh kinh doanh n&oacute;i chung v&agrave; trong việc mở qu&aacute;n c&agrave; ph&ecirc; n&oacute;i ri&ecirc;ng, bạn n&ecirc;n t&igrave;m hiểu chu đ&aacute;o c&aacute;c yếu tố ch&iacute;nh quyết định th&agrave;nh c&ocirc;ng của việc kinh doanh. Dưới đ&acirc;y, Purio c&oacute; một số gợi &yacute; để c&oacute; thể gi&uacute;p c&aacute;c bạn đẩy nhanh qu&aacute; tr&igrave;nh chuẩn bị v&agrave; t&igrave;m hiểu của m&igrave;nh.</p>\r\n\r\n<p>1. Địa điểm tốt chiếm 40% th&agrave;nh c&ocirc;ng của một qu&aacute;n c&agrave; ph&ecirc;. Do đ&oacute; bạn cần phải nghi&ecirc;n cứu thật kĩ địa điểm dự định kinh doanh, yếu tố cạnh tranh, t&igrave;nh h&igrave;nh d&acirc;n cư v&agrave; những t&aacute;c động kh&aacute;c ảnh hưởng tới qu&aacute;n c&agrave; ph&ecirc; của m&igrave;nh.</p>\r\n\r\n<p>2. Vốn: gồm c&oacute; vốn cố định v&agrave; vốn lưu động. Ng&acirc;n s&aacute;ch cần chuẩn bị th&igrave; t&ugrave;y theo quy m&ocirc; của qu&aacute;n.</p>\r\n\r\n<p>3. Khảo s&aacute;t thị hiếu, sở th&iacute;ch của kh&aacute;ch h&agrave;ng, từ đ&oacute; đưa ra đối tượng ch&iacute;nh để kinh doanh.</p>\r\n\r\n<p>4. Nghi&ecirc;n cứu thị trường v&agrave; c&aacute;c đối thủ cạnh tranh tại khu vực m&igrave;nh sắp kinh doanh.</p>\r\n\r\n<p>II. C&aacute;c bước hiện thực ho&aacute;</p>\r\n\r\n<p>1. &Yacute; tưởng</p>\r\n\r\n<p>Bạn kh&ocirc;ng thể bắt đầu dự &aacute;n kinh doanh m&agrave; kh&ocirc;ng c&oacute; &yacute; tưởng. Một &yacute; tưởng kinh doanh đột ph&aacute; v&agrave; c&oacute; t&iacute;nh ứng dụng cao sẽ gi&uacute;p bạn nhanh ch&oacute;ng thu hồi vốn v&agrave; đạt lợi nhuận nhất. Để bắt đầu điều đ&oacute;, bạn n&ecirc;n kinh doanh hướng tới niềm đam m&ecirc; của m&igrave;nh, c&oacute; đam m&ecirc;, bạn sẽ dễ d&agrave;ng suy nghĩ ra những &yacute; tưởng ri&ecirc;ng. Niềm đam m&ecirc; đ&oacute; sẽ tạo ra phong c&aacute;ch ri&ecirc;ng cho qu&aacute;n c&agrave; ph&ecirc; của bạn. V&agrave; n&eacute;t &ldquo;ri&ecirc;ng&rdquo; của mỗi qu&aacute;n c&agrave; ph&ecirc; l&agrave; điểm mạnh v&agrave; gi&uacute;p cho qu&aacute;n c&agrave; ph&ecirc; đ&oacute; khẳng định t&ecirc;n tuổi tr&ecirc;n thị trường.</p>\r\n\r\n<p><img src="imgs/news2/img2.jpg" /></p>\r\n\r\n<p>2. Kế hoạch kinh doanh v&agrave; khả năng đầu tư cho dự &aacute;n của m&igrave;nh</p>\r\n\r\n<p>Kinh doanh l&agrave; một c&ocirc;ng việc đ&ograve;i hỏi sự đầu tư cao v&igrave; mức độ rủi ro kh&aacute; lớn, do đ&oacute; bạn cần phải c&oacute; một kế hoạch chi tiết để giảm thiểu khả năng thua lỗ cho qu&aacute;n c&agrave; ph&ecirc; của m&igrave;nh.<br />\r\nBạn n&ecirc;n vạch ra một kế hoạch kinh doanh cho qu&aacute;n c&agrave; ph&ecirc;, kinh doanh mặt h&agrave;ng g&igrave;, chất lượng như thế n&agrave;o, gi&aacute; cả ra sao v&agrave; c&oacute; thể đ&aacute;p ứng đủ cung cầu cho thị trường hay kh&ocirc;ng?<br />\r\nDựa tr&ecirc;n số vốn đầu tư của m&igrave;nh, bạn c&oacute; thể x&aacute;c định quy m&ocirc; qu&aacute;n c&agrave; ph&ecirc; lớn hay nhỏ, c&oacute; những qu&aacute;n c&agrave; ph&ecirc; đầu tư l&ecirc;n đến 5 - 7 tỷ đồng nhưng thu hồi vốn chỉ trong v&ograve;ng 1 - 2 năm nhờ những đột ph&aacute; trong kinh doanh v&igrave; c&oacute; một kế hoạch kinh doanh ho&agrave;n hảo. Ngược lại cũng c&oacute; những qu&aacute;n c&agrave; ph&ecirc; biến mất tr&ecirc;n thị trường trong v&ograve;ng một-hai th&aacute;ng. Do đ&oacute; để th&agrave;nh c&ocirc;ng, bạn cần một kế hoạch kinh doanh s&aacute;ng suốt, r&otilde; r&agrave;ng v&agrave; chi tiết.<br />\r\nC&oacute; rất nhiều c&aacute;c loại chi ph&iacute; m&agrave; bạn phải chi trả h&agrave;ng th&aacute;ng, v&igrave; thế bạn n&ecirc;n ph&ograve;ng hờ một số vốn dự ph&ograve;ng rủi ro để x&aacute;c định qu&aacute;n c&agrave; ph&ecirc; của m&igrave;nh c&oacute; thể tồn tại trong bao l&acirc;u nếu trong thời gian đầu chưa c&oacute; thật nhiều kh&aacute;ch h&agrave;ng.<br />\r\nTừ đ&oacute; ch&uacute;ng ta c&oacute; thể thấy rằng khi lập một dự &aacute;n kinh doanh, ch&uacute;ng ta phải dự to&aacute;n được lời lỗ trong kinh doanh, phải nhận thức được cung - cầu của thị trường để tạo ra những sản phẩm đặc biệt, nhận thức được tiềm năng của c&ocirc;ng việc m&igrave;nh đang triển khai để c&oacute; kế hoạch đ&uacute;ng đắn cho qu&aacute;n c&agrave; ph&ecirc; của m&igrave;nh.</p>\r\n\r\n<p><img src="imgs/news2/img3.jpg" /></p>\r\n\r\n<p>Địa điểm của qu&aacute;n c&agrave; ph&ecirc; l&agrave; một yếu tố quyết định sự th&agrave;nh bại trong kinh doanh qu&aacute;n c&agrave; ph&ecirc;. Do đ&oacute; bạn phải lựa chọn một mặt bằng kinh doanh ph&ugrave; hợp với &yacute; tưởng kinh doanh v&agrave; phong c&aacute;ch của qu&aacute;n c&agrave; ph&ecirc;.&nbsp;<br />\r\n- Qu&aacute;n c&agrave; ph&ecirc; s&acirc;n vườn: bạn cần một mặt bằng rộng r&atilde;i v&agrave; tho&aacute;ng m&aacute;t với nhiều c&acirc;y xanh, kh&ocirc;ng gian y&ecirc;n b&igrave;nh ph&oacute;ng kho&aacute;ng l&atilde;ng mạn.<br />\r\n- Qu&aacute;n c&agrave; ph&ecirc; bar: bạn cần lựa chọn một địa điểm kinh doanh gần trung t&acirc;m, c&oacute; mức sống cao v&agrave; mật độ d&acirc;n cư đ&ocirc;ng đ&uacute;c, nhộn nhịp.<br />\r\n- Qu&aacute;n c&agrave; ph&ecirc; văn ph&ograve;ng: một qu&aacute;n c&agrave; ph&ecirc; văn ph&ograve;ng cần chọn một mặt bằng gần c&aacute;c trung t&acirc;m h&agrave;nh ch&iacute;nh.<br />\r\n- Một số qu&aacute;n c&agrave; ph&ecirc; kh&aacute;c như c&agrave; ph&ecirc; sinh vi&ecirc;n, c&agrave; ph&ecirc; c&oacute;c gi&agrave;nh cho c&aacute;c tầng lớp b&igrave;nh d&acirc;n chỉ cần một mặt bằng rộng vừa phải với c&aacute;c d&ograve;ng nhạc thị trường kh&ocirc;ng cần qu&aacute; nổi bật, thường được mở xung quanh c&aacute;c khu c&ocirc;ng nghiệp v&agrave; c&aacute;c khu d&acirc;n cư.<br />\r\nB&ecirc;n cạnh đ&oacute;, nếu bạn đ&atilde; c&oacute; địa điểm kinh doanh qu&aacute;n c&agrave; ph&ecirc;, bạn cần khảo s&aacute;t khu vực xung quanh đ&oacute; đ&atilde; c&oacute; những qu&aacute;n c&agrave; ph&ecirc; loại n&agrave;o, khả năng cạnh tranh ra sao &hellip; Bạn cần phải đ&aacute;nh gi&aacute; kỹ c&agrave;ng về t&igrave;nh h&igrave;nh d&acirc;n cư, giao th&ocirc;ng, kh&aacute;ch v&atilde;ng lai ở khu vực bạn chọn để mở qu&aacute;n c&agrave; ph&ecirc;, từ đ&oacute; c&oacute; thể quyết định mở qu&aacute;n hay kh&ocirc;ng v&agrave; phải mở theo hướng n&agrave;o, phong c&aacute;ch n&agrave;o. Tuy nhi&ecirc;n bạn cũng c&oacute; thể hỏi &yacute; kiến của những người xung quanh, nhất l&agrave; những người c&oacute; kinh nghiệm để th&agrave;nh c&ocirc;ng chắc chắn hơn.</p>\r\n\r\n<p><img src="imgs/news2/img4.jpg" /></p>\r\n\r\n<p>4. Tạo phong c&aacute;ch v&agrave; trang tr&iacute; cho qu&aacute;n c&agrave; ph&ecirc; cho qu&aacute;n c&agrave; ph&ecirc;</p>\r\n\r\n<p>Mỗi qu&aacute;n c&agrave; ph&ecirc; đều c&oacute; một phong c&aacute;ch ri&ecirc;ng được gọi l&agrave; điểm nhấn cho qu&aacute;n c&agrave; ph&ecirc;. T&ugrave;y thuộc v&agrave;o loại qu&aacute;n c&agrave; ph&ecirc; m&agrave; c&oacute; điểm nhấn kh&aacute;c nhau, tạo n&ecirc;n sự đặc trưng cho từng qu&aacute;n. Phong c&aacute;ch c&agrave;ng nổi bật th&igrave; qu&aacute;n c&agrave; ph&ecirc; c&agrave;ng thu h&uacute;t được lượng lớn kh&aacute;ch h&agrave;ng.<br />\r\n- C&agrave; ph&ecirc; t&igrave;nh nh&acirc;n: thật chất đ&acirc;y l&agrave; qu&aacute;n c&agrave; ph&ecirc; s&acirc;n vườn c&oacute; m&ocirc;t s&acirc;n rộng với nhiều c&acirc;y xanh, tiếng nước chảy r&oacute;c r&aacute;ch, tạo khung cảnh gần gũi với thi&ecirc;n nhi&ecirc;n với kh&ocirc;ng gian ri&ecirc;ng biệt gi&agrave;nh cho c&aacute;c cặp t&igrave;nh nh&acirc;n với kh&ocirc;ng gian y&ecirc;n tĩnh.<br />\r\n- C&agrave; ph&ecirc; trực tiếp b&oacute;ng đ&aacute;: l&agrave; những qu&aacute;n c&agrave; ph&ecirc; chiếu trực tiếp c&aacute;c trận b&oacute;ng đ&aacute; c&aacute;c giải Ngoại Hạng Anh, C1, World Cup&hellip; phục vụ những người h&acirc;m mộ m&ocirc;n thể thao vua, kh&ocirc;ng kể ng&agrave;y hay đ&ecirc;m. Những qu&aacute;n n&agrave;y thường trang bị nhiều Tivi với m&agrave;n h&igrave;nh lớn v&agrave; độ n&eacute;t cao đặt ở nhiều g&oacute;c nh&igrave;n kh&aacute;c nhau.<br />\r\n- C&agrave; ph&ecirc; theo phong c&aacute;ch ngoại: ở Việt Nam c&oacute; rất nhiều qu&aacute;n c&agrave; ph&ecirc; theo phong c&aacute;ch ngoại từ ch&acirc;u &Acirc;u đến ch&acirc;u &Aacute;. Những loại qu&aacute;n n&agrave;y thường c&oacute; điểm chung l&agrave; sang trọng ấm c&uacute;ng v&agrave; bắt mắt với phong c&aacute;ch phục vụ chuy&ecirc;n nghiệp, lịch sự.<br />\r\n- C&agrave; ph&ecirc; văn ph&ograve;ng: qu&aacute;n c&agrave; ph&ecirc; thường phục vụ tầng lớp nh&acirc;n vi&ecirc;n văn ph&ograve;ng với những b&agrave;n l&agrave;m việc rộng c&oacute; wifi, thường phục vụ th&ecirc;m cả thức ăn như: điểm t&acirc;m, cơ văn ph&ograve;ng, thức ăn nhanh&hellip;<br />\r\n- C&aacute;c qu&aacute;n c&agrave; ph&ecirc; kết hợp: qu&aacute;n c&agrave; ph&ecirc; kết hợp tr&agrave; đạo, qu&aacute;n c&agrave; ph&ecirc; kết hợp xem phim 3D, c&agrave; ph&ecirc; kem, c&agrave; ph&ecirc; h&aacute;t với nhau&hellip; Kết hợp b&aacute;n c&agrave; ph&ecirc; v&agrave; c&aacute;c loại h&igrave;nh kh&aacute;c tạo phong c&aacute;ch ri&ecirc;ng g&acirc;y cảm gi&aacute;c th&uacute; vị cho kh&aacute;ch h&agrave;ng.<br />\r\nB&ecirc;n cạnh đ&oacute;, bạn cần phải trang tr&iacute; qu&aacute;n c&agrave; ph&ecirc;, bố tr&iacute; b&agrave;n ghế vật dụng sao cho ph&ugrave; hợp nhất, bắt mắt nhất v&agrave; c&oacute; một lượng &aacute;nh s&aacute;ng ph&ugrave; hợp theo phong c&aacute;ch qu&aacute;n c&agrave; ph&ecirc; của m&igrave;nh. Bạn n&ecirc;n tham khảo &yacute; kiến của những người xung quanh hoặc c&aacute;c chuy&ecirc;n gia để b&agrave;i tr&iacute; kh&ocirc;ng gian tiết kiệm v&agrave; hợp l&yacute; nhất.</p>\r\n\r\n<p><img src="imgs/news2/img5.jpg" /></p>\r\n\r\n<p>5. Tuyển dụng v&agrave; đ&agrave;o tạo nh&acirc;n sự</p>\r\n\r\n<p>Ng&agrave;y nay, khi cuộc sống c&agrave;ng ng&agrave;y c&agrave;ng n&acirc;ng cao, c&aacute;c qu&aacute;n c&agrave; ph&ecirc; ng&agrave;y c&agrave;ng nhiều th&igrave; cung c&aacute;ch phục vụ của nh&acirc;n vi&ecirc;n qu&aacute;n c&agrave; ph&ecirc; phải ng&agrave;y c&agrave;ng lịch thiệp, chu đ&aacute;o. T&iacute;nh chuy&ecirc;n nghiệp trong phục vụ c&agrave; ph&ecirc; cũng l&agrave; một điểm mạnh gi&uacute;p qu&aacute;n c&agrave; ph&ecirc; ng&agrave;y c&agrave;ng đ&ocirc;ng kh&aacute;ch hơn. Đ&agrave;o tạo nh&acirc;n vi&ecirc;n chuy&ecirc;n nghiệp sẽ n&acirc;ng cao chất lượng phục vụ, gi&uacute;p kh&aacute;ch h&agrave;ng thoải m&aacute;i hơn khi v&agrave;o qu&aacute;n c&agrave; ph&ecirc;. Một số qu&aacute;n c&agrave; ph&ecirc; lớn hiện nay đều n&acirc;ng cao chất lượng phục vụ kh&aacute;ch h&agrave;ng bằng c&aacute;ch đ&agrave;o tạo nh&acirc;n vi&ecirc;n phục vụ một c&aacute;ch chuy&ecirc;n nghiệp, những nh&acirc;n vi&ecirc;n ở đ&acirc;y đều được mặc đồng phục v&agrave; trải qua c&aacute;c kh&oacute;a nghiệp vụ do qu&aacute;n c&agrave; ph&ecirc; đ&agrave;o tạo. Từ đ&oacute; n&acirc;ng cao chất lượng phục vụ kh&aacute;ch h&agrave;ng, tạo cảm gi&aacute;c thoải m&aacute;i cho kh&aacute;ch h&agrave;ng khi bước v&agrave;o cũng như qu&aacute; tr&igrave;nh kh&aacute;ch h&agrave;ng thư gi&atilde;n tại qu&aacute;n c&agrave; ph&ecirc;. C&aacute;c nh&acirc;n vi&ecirc;n pha chế cũng được n&acirc;ng cao về chất lượng, thể hiện t&iacute;nh chuy&ecirc;n nghiệp về tốc độ pha chế, m&ugrave;i vị, thậm ch&iacute; ở một số qu&aacute;n, quầy pha chế được đặt ở trung t&acirc;m để c&aacute;c bartender c&oacute; thể biểu diễn pha chế cho kh&aacute;ch h&agrave;ng thưởng thức.</p>\r\n\r\n<p><img src="imgs/news2/img6.jpg" /></p>\r\n\r\n<p>6. Đưa v&agrave;o hoạt động v&agrave; ph&aacute;t triển</p>\r\n\r\n<p>Kh&ocirc;ng chỉ những dự tr&ugrave;, khi triển khai hoạt động qu&aacute;n c&agrave; ph&ecirc;, c&aacute;c bạn cần quản l&yacute; v&agrave; gi&aacute;m s&aacute;t chặt chẽ hoạt động của qu&aacute;n c&agrave; ph&ecirc;. Từ đ&oacute; đ&aacute;nh gi&aacute;:<br />\r\n- Nh&acirc;n vi&ecirc;n hoạt động c&oacute; hiệu quả kh&ocirc;ng ?<br />\r\n- Thức uống đ&atilde; ph&ugrave; hợp với kh&aacute;ch h&agrave;ng hay kh&ocirc;ng ?<br />\r\n- Kh&aacute;ch h&agrave;ng c&oacute; cảm thấy thoải m&aacute;i hay kh&ocirc;ng ?<br />\r\n- Lượng kh&aacute;ch h&agrave;ng qua c&aacute;c tuần, th&aacute;ng, qu&yacute; trong năm.<br />\r\nTừ những điều đ&oacute; c&oacute; những điều chỉnh hợp l&yacute; c&aacute;ch hoạt động trong nội bộ qu&aacute;n c&agrave; ph&ecirc; cũng như c&aacute;c hoạt động marketing nhằm thu h&uacute;t kh&aacute;ch h&agrave;ng, tăng doanh thu cho qu&aacute;n c&agrave; ph&ecirc;, r&uacute;t ngắn thời gian thu hồi vốn.<br />\r\nC&aacute;c bạn cũng n&ecirc;n đ&aacute;nh gi&aacute; những mặt hạn chế, n&acirc;ng cao những mặt tốt của qu&aacute;n c&agrave; ph&ecirc;, lập biểu to&aacute;n kinh ph&iacute;, thu chi của qu&aacute;n c&agrave; ph&ecirc; một c&aacute;ch r&otilde; r&agrave;ng theo từng tuần, thăm d&ograve; &yacute; kiến kh&aacute;ch h&agrave;ng v&agrave; bổ sung những khiếm khuyết của qu&aacute;n trong thời gian ngắn nhất để kịp thời lấp trống những khiếm khuyết đồng thời c&oacute; chiến lượt thu h&uacute;t kh&aacute;ch h&agrave;ng v&agrave; ph&aacute;t triển qu&aacute;n c&agrave; ph&ecirc; với ti&ecirc;u ch&iacute;: Giữ vững điểm mạnh, X&oacute;a bỏ thiếu s&oacute;t, N&acirc;ng cao về chất lượng.</p>\r\n\r\n<p><img src="imgs/news2/img7.jpg" /></p>\r\n\r\n<p>III. Kết Luận</p>\r\n\r\n<p>Những yếu tố kh&aacute;ch quan từ kh&acirc;u chuẩn bị đến triển khai dự &aacute;n, ch&uacute;ng ta thấy r&otilde; được việc đầu tư v&agrave; kinh doanh qu&aacute;n c&agrave; ph&ecirc; cũng rất phức tạp, đầy rủi ro trong kinh doanh. V&igrave; thế, trong từng bước đi ch&uacute;ng ta phải thật vững v&agrave;ng v&agrave; hiệu quả. Ch&uacute;ng t&ocirc;i tin rằng, b&agrave;i viết n&agrave;y sẽ hữu &iacute;ch cho c&aacute;c bạn khi tham gia v&agrave;o thị trường n&agrave;y v&agrave; từng bước sẽ gi&uacute;p c&aacute;c bạn đi đến th&agrave;nh c&ocirc;ng.</p>\r\n', 0, 'Nguyễn Thanh Tình - PHP39 (Sưu tầm)', NULL, 1, '2019-07-28 11:48:22'),
(16, '3 vùng trồng cà phê Arabica ngon nhất Việt Nam', 1, 1, '', 'Ở Việt Nam, khá nhiều nơi có khí hậu và thổ nhưỡng thích hợp với việc trồng cây cà phê. Nhưng đâu là vùng trồng trọt cho ra những trái cà phê Arabica ngon nhất trên thế giới, hẳn không nhiều người biết.\r\n', '<p>Theo ch&acirc;n c&aacute;c gi&aacute;o sĩ người Ph&aacute;p, c&acirc;y c&agrave; ph&ecirc; đ&atilde; c&oacute; mặt ở nước ta từ thế kỷ 19 v&agrave; ph&aacute;t triển mạnh mẽ cho tới ng&agrave;y h&ocirc;m nay. Hiện Việt Nam l&agrave; nước c&oacute; sản lượng c&agrave; ph&ecirc; xuất khẩu lớn nhất thế giới, với khoảng 900.000 &ndash;1.200.000 tấn/năm.&nbsp;<br />\r\nNhưng trong 500.000 ha trồng c&agrave; ph&ecirc; của cả nước, chỉ c&oacute; khoảng 35.000 ha c&agrave; ph&ecirc; Arabica, tập trung chủ yếu ở L&acirc;m Đồng, Thừa Thi&ecirc;n Huế, Quảng Trị, Nghệ An, H&ograve;a B&igrave;nh, Sơn La, Điện Bi&ecirc;n... C&agrave; ph&ecirc; Arabica vốn l&agrave; loại c&oacute; gi&aacute; trị kinh tế nhất trong c&aacute;c lo&agrave;i c&agrave; ph&ecirc;.<br />\r\nTr&ecirc;n thị trường c&agrave; ph&ecirc; Arabica lu&ocirc;n được đ&aacute;nh gi&aacute; cao nhờ c&oacute; hương vị thơm ngon. Gi&aacute; c&agrave; ph&ecirc; Arabica cũng thường cao gấp đ&ocirc;i so với gi&aacute; c&agrave; ph&ecirc; Robusta. C&agrave; ph&ecirc; Arabica c&oacute; nguồn gốc từ v&ugrave;ng cao nguy&ecirc;n nhiệt đới Ethiopia (Đ&ocirc;ng Phi). Brasil v&agrave; Colombia l&agrave; hai nước xuất khẩu ch&iacute;nh loại c&agrave; ph&ecirc; n&agrave;y, với chất lượng được đ&aacute;nh gi&aacute; cao nhất.</p>\r\n\r\n<p><img src="imgs/news3/img1.jpg" /></p>\r\n\r\n<p>C&agrave; ph&ecirc; Arabica được đ&aacute;nh gi&aacute; l&agrave; ngon v&agrave; thường c&oacute; gi&aacute; cao gấp đ&ocirc;i c&agrave; ph&ecirc; Robusta v&agrave; được trồng ở v&ugrave;ng đất cao từ 1000-1600m.</p>\r\n\r\n<p>C&acirc;y c&agrave; ph&ecirc; Arabica thường được trồng ở độ cao từ 1.000 - 1.600 m. C&acirc;y c&oacute; t&aacute;n lớn, m&agrave;u xanh đậm, l&aacute; h&igrave;nh oval. Hạt Arabica hơi d&agrave;i, to hơn hạt Robusta. D&ugrave; c&oacute; lượng cafeine chỉ bằng 1 nửa Robusta, nhưng c&agrave; ph&ecirc; Arabica c&oacute; hương thơm nhẹ nh&agrave;ng, vị chua thanh n&ecirc;n rất được ưa chuộng.</p>\r\n\r\n<p>Đ&agrave; Lạt &ldquo;thi&ecirc;n đường&rdquo; của c&agrave; ph&ecirc; Arabica</p>\r\n\r\n<p>Khi nhắc đến những v&ugrave;ng đất trồng c&agrave; ph&ecirc; ở nước ta, người ta thường nghĩ ngay đến T&acirc;y Nguy&ecirc;n, hay c&ograve;n gọi l&agrave; Cao nguy&ecirc;n trung phần, nơi may mắn được tạo h&oacute;a ban cho diện t&iacute;ch đất đỏ bazan tr&ugrave; ph&uacute;.</p>\r\n\r\n<p><img src="imgs/news3/img2.jpg" /></p>\r\n\r\n<p>Đ&agrave; Lạt &ldquo;thi&ecirc;n đường&rdquo; của c&agrave; ph&ecirc; Arabica</p>\r\n\r\n<p>C&agrave; ph&ecirc; Arabica được trồng ở c&aacute;c huyện như: Đơn Dương, L&acirc;m H&agrave;, Di Linh, Đức Trọng v&agrave; một số v&ugrave;ng ngoại th&agrave;nh của th&agrave;nh phố Đ&agrave; Lạt được đ&aacute;nh gi&aacute; l&agrave; c&oacute; gi&aacute; trị cao nhất Việt Nam. Thậm ch&iacute;, hương vị s&aacute;nh ngang với c&agrave; ph&ecirc; ngon nhất thế giới l&agrave; Arabica Bourbon (Moka).&nbsp;<br />\r\nV&ugrave;ng đất n&agrave;y được coi l&agrave; &ldquo;thi&ecirc;n đường&rdquo; c&agrave; ph&ecirc; Arabica nhờ những &ldquo;chỉ số v&agrave;ng&rdquo; như : độ cao 1.500 m so với mặt nước biển, kh&iacute; hậu &ocirc;n đới m&aacute;t quanh năm, nhiệt độ cực đại trong năm kh&ocirc;ng vượt qu&aacute; 33 độ, nhiệt độ cực tiểu 5 độ.</p>\r\n\r\n<p>Điện Bi&ecirc;n, Sơn La c&oacute; lịch sử trồng c&agrave; ph&ecirc; Arabica cả trăm năm</p>\r\n\r\n<p>Ở ph&iacute;a Bắc, kh&iacute; hậu lạnh do chịu ảnh hưởng của gi&oacute; m&ugrave;a đ&ocirc;ng bắc, c&ugrave;ng lượng mưa lớn, m&ugrave;a kh&ocirc; kh&ocirc;ng r&otilde; rệt, cũng l&agrave; những lợi thế gi&uacute;p cho c&agrave; ph&ecirc; Arabica sinh trưởng v&agrave; ph&aacute;t triển. Nơi đ&acirc;y cũng đ&atilde; h&igrave;nh th&agrave;nh n&ecirc;n những v&ugrave;ng c&agrave; ph&ecirc; c&oacute; hương vị rất tuyệt như ở huyện Y&ecirc;n B&igrave;nh, thuộc tỉnh Y&ecirc;n B&aacute;i.<br />\r\nV&ugrave;ng T&acirc;y Bắc, c&agrave; ph&ecirc; được trồng ở c&aacute;c tỉnh như Sơn La, Điện Bi&ecirc;n cũng được kh&aacute;ch h&agrave;ng quốc tế đ&aacute;nh gi&aacute; rất cao nhờ c&oacute; thổ nhưỡng tương tự như v&ugrave;ng Sao Paulo của Brasil, c&oacute; kh&aacute;c chăng chỉ l&agrave; 2 v&ugrave;ng c&agrave; ph&ecirc; nằm n&agrave;y ở ph&iacute;a Bắc v&agrave; ph&iacute;a Nam b&aacute;n cầu.</p>\r\n\r\n<p><img src="imgs/news3/img3.jpg" /></p>\r\n\r\n<p>Ngo&agrave;i T&acirc;y Nguy&ecirc;n, ở Việt Nam c&ograve;n c&oacute; v&ugrave;ng c&agrave; ph&ecirc; Arabica T&acirc;y Bắc đ&atilde; c&oacute; lịch sử cả trăm năm.</p>\r\n\r\n<p>Đặc biệt c&agrave; ph&ecirc; Chiềng Ban, Sinh Ban( Sơn La), tuy kh&ocirc;ng được trồng ở v&ugrave;ng đất đỏ bazan v&agrave; kh&ocirc;ng nằm ở độ cao l&yacute; tưởng như c&aacute;c tỉnh T&acirc;y Nguy&ecirc;n, song Sơn La c&oacute; những loại đất trong nh&oacute;m đất đỏ v&agrave;ng th&iacute;ch hợp với c&acirc;y c&agrave; ph&ecirc; như Fk, Fv, Fs&hellip; Lại nằm ở vĩ độ kh&aacute; cao về ph&iacute;a Bắc (20039&rsquo; &ndash; 22002&rsquo; vĩ độ Bắc) n&ecirc;n kh&ocirc;ng phải tưới nước, nhưng c&acirc;y c&agrave; ph&ecirc; Arabica vẫn c&oacute; sức sống rất m&atilde;nh liệt. Nhiều c&acirc;y v&agrave;i chục năm tuổi, th&acirc;n to, t&aacute;n rộng m&agrave; hạt c&agrave; ph&ecirc; c&oacute; hương vị kh&ocirc;ng hề thua k&eacute;m so với giống c&acirc;y m&agrave; người Ph&aacute;p đ&atilde; trồng ở L&acirc;m Đồng từ những năm 30 của thế kỷ trước.</p>\r\n\r\n<p>Quảng Trị, Nghệ An c&agrave; ph&ecirc; Arabica với hương thơm s&acirc;u lắng</p>\r\n\r\n<p>Khu vực Trung bộ nước ta cũng c&oacute; những v&ugrave;ng đất như: Khe Sanh (Quảng Trị), Phủ Quỳ (Nghệ An) rất th&iacute;ch hợp với loại c&agrave; ph&ecirc; Arabica, đặc biệt l&agrave; giống Catimor (loại được lai giữa chủng Caturra với Hybrid de Timor). C&ugrave;ng thuộc họ Arabica, tuy kh&ocirc;ng c&oacute; vị ngọt đậm như Bourbon nhưng Catimor lại c&oacute; hương thơm s&acirc;u lắng v&agrave; vị ch&aacute;t, mặn.<br />\r\nNg&agrave;y nay, ngo&agrave;i những giống Arabica truyền thống, được trồng từ trước, mới đ&acirc;y, Viện Khoa học Kinh tế N&ocirc;ng L&acirc;m Nghiệp T&acirc;y Nguy&ecirc;n đ&atilde; lai tạo ra nhiều giống c&agrave; ph&ecirc; Arabica c&oacute; năng suất v&agrave; chất lượng cao.<br />\r\nTrong đ&oacute;, hai giống lai TN1, TN2 c&oacute; năng suất cao v&agrave; chất lượng tốt đ&atilde; được Bộ N&ocirc;ng nghiệp v&agrave; Ph&aacute;t triển N&ocirc;ng th&ocirc;n cho ph&eacute;p khu vực ho&aacute; canh t&aacute;c. C&aacute;c giống c&agrave; ph&ecirc; Arabica lai mới n&agrave;y c&oacute; đặc t&iacute;nh c&acirc;y sinh trưởng khỏe, ph&acirc;n c&agrave;nh nhiều. Năng suất đạt từ 4 - 5 tấn c&agrave; ph&ecirc; nh&acirc;n/ha, k&iacute;ch cỡ hạt lớn, trọng lượng 100 nh&acirc;n tr&ecirc;n 15 - 17gram, kh&aacute;ng cao đối với bệnh gỉ sắt...</p>\r\n\r\n<p><img src="imgs/news3/img4.jpg" /></p>\r\n\r\n<p>C&agrave; ph&ecirc; Arabica của Việt Nam rất được c&aacute;c thương hiệu c&agrave; ph&ecirc; nổi tiếng nhất tr&ecirc;n thế giới ưa chuộng.</p>\r\n\r\n<p>Như vậy, c&ugrave;ng l&agrave; c&acirc;y c&agrave; ph&ecirc; Arabica, nhưng ở mỗi v&ugrave;ng trồng đặc trưng ở nước ta lại tạo ra những hương vị c&agrave; ph&ecirc; rất ri&ecirc;ng để chinh phục những người s&agrave;nh về ẩm thực nhất tr&ecirc;n thế giới. Ch&iacute;nh v&igrave; l&yacute; do n&agrave;y, c&agrave; ph&ecirc; Arabica của Việt Nam ng&agrave;y c&agrave;ng được c&aacute;c thương hiệu c&agrave; ph&ecirc; nổi tiếng nhất tr&ecirc;n thế giới lựa chọn, trong đ&oacute; c&oacute; thương hiệu Purio Coffee.</p>\r\n', 22, 'Nguyễn Thanh Tình - PHP39 (Sưu tầm)', NULL, 1, '2019-07-28 11:50:54');
INSERT INTO `news` (`id`, `title`, `category_id`, `admin_id`, `avatar`, `summary`, `content`, `comment_total`, `author`, `view`, `status`, `created_at`) VALUES
(17, '10 lợi ích chính của cà phê nguyên chất đối với sức khỏe con người', 1, 1, '', 'Ngoài tác dụng giúp chúng ta tỉnh táo, tăng cường các hoạt động về tâm thần và trí tuệ, tạo sự sảng khoái tích cực về tâm lý, làm chúng ta dễ tính hơn. Cà phê còn có 10 ích lợi chính đồi với sức khỏe như sau:', '<p>1. C&agrave; ph&ecirc; chứa nhiều chất chống &ocirc;xy h&oacute;a:&nbsp;Hạt c&agrave; ph&ecirc; chứa một số chất kho&aacute;ng như Mg, Ca, K v&agrave; h&agrave;m lượng cao những hợp chất polyphenols l&agrave; những chất chống oxy ho&aacute; hữu &iacute;ch. Chất chống oxy ho&aacute; gi&uacute;p trung ho&agrave; những gốc tự do để ngăn chặn qu&aacute; tr&igrave;nh hư hại tế b&agrave;o v&agrave; DNA trong c&aacute;c loại bệnh tim mạch, ung thư v&agrave; qu&aacute; tr&igrave;nh l&atilde;o ho&aacute;, khiến l&acirc;u gi&agrave;.</p>\r\n\r\n<p><img src="imgs/news4/img1.jpg" /></p>\r\n\r\n<p>2. Gi&uacute;p giảm c&acirc;n:&nbsp;C&agrave; ph&ecirc; gi&uacute;p bạn giảm b&eacute;o v&agrave; c&oacute; th&acirc;n h&igrave;nh thon gọn hơn. L&agrave; thức uống c&oacute; lượng calo thấp, sử dụng c&agrave; ph&ecirc; v&agrave;o buổi s&aacute;ng c&oacute; t&aacute;c dụng rất tốt bởi n&oacute; l&agrave;m tăng qu&aacute; tr&igrave;nh trao đổi chất, gi&uacute;p đốt ch&aacute;y calo.</p>\r\n\r\n<p><img src="imgs/news4/img2.jpg" /></p>\r\n\r\n<p>3. C&agrave; ph&ecirc; đối với hệ tim mạch:&nbsp;L&agrave; thực phẩm gi&agrave;u chất chống &ocirc;xy h&oacute;a tự nhi&ecirc;n, c&agrave; ph&ecirc; c&oacute; t&aacute;c dụng giữ cho m&aacute;u sạch, ngăn ngừa bệnh cao huyết &aacute;p v&agrave; bệnh tim mạch. C&agrave; ph&ecirc; chứa c&aacute;c hợp chất phenol với t&iacute;nh chống oxy h&oacute;a c&oacute; thể ngăn ngừa xơ vữa động mạch. Một số lợi &iacute;ch cho sức khỏe c&oacute; thể vươn xa đến ngừa tai biến mạch m&aacute;u n&atilde;o.</p>\r\n\r\n<p><img src="imgs/news4/img3.jpg" /></p>\r\n\r\n<p>4. C&agrave; ph&ecirc; đối với bệnh đ&aacute;i th&aacute;o đường:&nbsp;Tờ the Annals of Internal Medicine cho biết uống c&agrave; ph&ecirc; li&ecirc;n quan nghịch với vi&ecirc;m v&agrave; rối loạn chức năng nội m&ocirc;. C&agrave; ph&ecirc; c&oacute; thể l&agrave;m giảm nguy cơ bệnh đ&aacute;i th&aacute;o đường. C&aacute;c hợp chất điều tiết insulin c&oacute; trong c&agrave; ph&ecirc; sẽ gi&uacute;p ngăn chặn bệnh đ&aacute;i th&aacute;o đường tu&yacute;p II. Theo một nghi&ecirc;n cứu ở Singapore Chinese Health Study cho biết uống từ 4 cốc c&agrave;-ph&ecirc; trở l&ecirc;n h&agrave;ng ng&agrave;y giảm 30% nguy cơ bệnh đ&aacute;i th&aacute;o đường.</p>\r\n\r\n<p><img src="imgs/news4/img4.jpg" /></p>\r\n\r\n<p>5. C&agrave; ph&ecirc; ngừa ung thư:&nbsp;Tạp ch&iacute; chuy&ecirc;n đề JNCI của Viện Ung thư quốc gia Mỹ số ra đầu th&aacute;ng 5/2011 đăng tải nghi&ecirc;n cứu do c&aacute;c chuy&ecirc;n gia ĐH Harvard, Mỹ thực hiện th&igrave; những người uống c&agrave; ph&ecirc; c&oacute; h&agrave;m lượng cafein thấp điều độ v&agrave; đều đặn sẽ giảm được tới 50% nguy cơ mắc bệnh ung thư, nhất l&agrave; ung thư v&uacute;, ung thư gan, tiền liệt tuyến, trực v&agrave; đại tr&agrave;ng.</p>\r\n\r\n<p><img src="imgs/news4/img5.jpg" /></p>\r\n\r\n<p>6. Giảm thiểu bệnh Parkinson:&nbsp;Một nghi&ecirc;n cứu d&agrave;i 30 năm của c&aacute;c nh&agrave; khoa học trường Đại học Nam Florida cho thấy những người c&oacute; th&oacute;i quen uống c&agrave; ph&ecirc; th&igrave; c&oacute; rủi ro mắc bệnh Parkinson thấp hơn so với người kh&ocirc;ng uống hoặc uống &iacute;t. C&aacute;c nh&agrave; khoa học trường Đại học Kuopio, Phần Lan, Viện Karolinski ở Stockholm, Thụy Điển v&agrave; Viện Sức Khoẻ Quốc Gia Phần Lan cũng cho biết những người trung ni&ecirc;n thường d&ugrave;ng c&agrave; ph&ecirc; với lượng trung b&igrave;nh từ 3 đến 5 ly mỗi ng&agrave;y c&oacute; thể l&agrave;m giảm nguy cơ bị bệnh suy giảm tr&iacute; nhớ ở tuổi gi&agrave;.</p>\r\n\r\n<p><img src="imgs/news4/img6.jpg" /></p>\r\n\r\n<p>7. Ngừa bệnh xơ gan:&nbsp;Một số nghi&ecirc;n cứu được thực hiện gần đ&acirc;y cho thấy những người uống c&agrave; ph&ecirc; thường xuy&ecirc;n v&agrave; điều độ, giảm được tới 80% khả năng ph&aacute;t triển bệnh xơ gan.</p>\r\n\r\n<p><img src="imgs/news4/img7.jpg" /></p>\r\n\r\n<p>8. Giảm bệnh hen suyễn:&nbsp;Thực tế, c&aacute;c loại thuốc trị hen suyễn đều c&oacute; chứa caffeine v&igrave; vậy, uống c&agrave; ph&ecirc; điều độ c&oacute; t&aacute;c dụng t&iacute;ch cực trọng việc ngăn ngừa căn bệnh n&agrave;y.</p>\r\n\r\n<p><img src="imgs/news4/img8.jpg" /></p>\r\n\r\n<p>9. Lợi tiểu:&nbsp;C&agrave; ph&ecirc; kh&ocirc;ng chỉ được xem l&agrave; thức uống lợi tiểu m&agrave; c&ograve;n c&oacute; t&aacute;c dụng sạch dạ d&agrave;y v&agrave; hệ ti&ecirc;u h&oacute;a. Tuy nhi&ecirc;n những người c&oacute; vấn đề về dạ d&agrave;y, dễ phản ứng với c&agrave; ph&ecirc; th&igrave; kh&ocirc;ng n&ecirc;n uống nhiều c&agrave; ph&ecirc;.</p>\r\n\r\n<p><img src="imgs/news4/img9.jpg" /></p>\r\n\r\n<p>10. Giảm nguy cơ mắc bệnh sỏi mật:&nbsp;Theo nhiều nghi&ecirc;n cứu l&acirc;m s&agrave;ng, c&agrave; ph&ecirc; c&oacute; t&aacute;c dụng giảm cholesterol trong mật dạ d&agrave;y, gi&uacute;p ngăn ngừa nguy cơ tạo sỏi t&uacute;i mật.</p>\r\n\r\n<p><img src="imgs/news4/img10.jpg" /></p>\r\n', 25, 'Nguyễn Thanh Tình - PHP39 (Sưu tầm)', NULL, 1, '2019-07-28 11:52:02'),
(18, 'Phối trộn cà phê Arabica và cà phê Robusta: Nghệ thuật của sự tinh tế', 1, 1, '', '', '<p>Với kh&ocirc;ng &iacute;t người, nhấm nh&aacute;p vị đắng từ ly c&agrave; ph&ecirc; với hương thơm ng&agrave;o ngạt kh&ocirc;ng chỉ l&agrave; th&uacute; vui m&agrave; c&ograve;n l&agrave; sự tận hưởng, suy ngẫm về cuộc sống. Tuy nhi&ecirc;n, để pha được ly c&agrave; ph&ecirc; thơm ngon, đ&uacute;ng &ldquo;gu&rdquo; người thưởng thức l&agrave; cả một nghệ thuật!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src="imgs/news5/img1.jpg" /></p>\r\n\r\n<p>Người Việt c&oacute; phong c&aacute;ch thưởng thức c&agrave; ph&ecirc; rất ri&ecirc;ng, họ kh&ocirc;ng coi c&agrave; ph&ecirc; l&agrave; thức uống nhanh, c&oacute; t&aacute;c dụng chống buồn ngủ như người phương T&acirc;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngồi b&ecirc;n t&aacute;ch c&agrave; ph&ecirc;, vừa nhấp từng ngụm nhỏ vừa đọc b&aacute;o, nghe nhạc hay tr&ograve; chuyện c&ugrave;ng bạn b&egrave;, đối t&aacute;c&hellip; cũng l&agrave; c&aacute;ch để suy ngẫm về cuộc sống, chia sẻ về chuyện đời&hellip; B&ecirc;n ly c&agrave; ph&ecirc;, t&acirc;m hồn ta như tĩnh lại. V&igrave; thế, với nhiều người khi chọn được qu&aacute;n c&agrave; ph&ecirc; quen, hay hương vị c&agrave; ph&ecirc; m&igrave;nh y&ecirc;u th&iacute;ch th&igrave; chẳng c&oacute; g&igrave; c&oacute; thể l&agrave;m họ phải thay đổi. Người Việt c&oacute; phong c&aacute;ch thưởng thức c&agrave; ph&ecirc; rất ri&ecirc;ng, họ kh&ocirc;ng coi c&agrave; ph&ecirc; l&agrave; thức uống nhanh, c&oacute; t&aacute;c dụng chống buồn ngủ như người phương T&acirc;y.&nbsp;</p>\r\n\r\n<p>Vị ngon từ sự phối trộn c&agrave; ph&ecirc; nguy&ecirc;n liệu hợp l&yacute;</p>\r\n\r\n<p>Tr&ecirc;n thế giới cũng như tại Việt Nam, c&agrave; ph&ecirc; Arabica v&agrave; Robusta l&agrave; hai loại c&agrave; ph&ecirc; phổ biến nhất. So với c&agrave; ph&ecirc; Robusta, c&agrave; ph&ecirc; Arabica đ&ograve;i hỏi được gieo trồng ở những v&ugrave;ng đất cao hơn, c&oacute; kh&iacute; hậu m&aacute;t mẻ hơn. Thời gian để quả c&agrave; ph&ecirc; Arabica ch&iacute;n l&acirc;u hơn, sản lượng cũng thấp hơn. Tuy nhi&ecirc;n, Arabica lại cho những hạt c&agrave; ph&ecirc; c&oacute; m&ugrave;i vị thơm ngon hơn, ch&iacute;nh v&igrave; thế gi&aacute; th&agrave;nh của n&oacute; cũng cao gấp đ&ocirc;i so với c&agrave; ph&ecirc; Robusta.<br />\r\n<br />\r\nTrong khi, c&agrave; ph&ecirc; Robusta được xem l&agrave; kh&aacute; &ldquo;dễ t&iacute;nh&rdquo;, ch&uacute;ng lại ưa th&iacute;ch điều kiện kh&iacute; hậu ấm v&agrave; kh&ocirc; hơn. Loại c&agrave; ph&ecirc; n&agrave;y c&oacute; vị đắng ch&aacute;t, chứa nhiều caffeine v&agrave; nồng hơn v&agrave; n&ecirc;n kh&aacute; th&iacute;ch hợp cho ph&aacute;i nam. C&ograve;n c&agrave; ph&ecirc; Arabica rất thơm, &iacute;t đắng, c&oacute; vị chua thanh, lượng caffeine &iacute;t hơn n&ecirc;n rất được nữ giới ưa chuộng.</p>\r\n\r\n<p><img src="imgs/news5/img2.jpg" /></p>\r\n\r\n<p>Sự phối trộn giữa c&agrave; ph&ecirc; Arabica v&agrave; Robusta sẽ tạo n&ecirc;n sự c&acirc;n bằng v&agrave; phong ph&uacute; cho t&aacute;ch c&agrave; ph&ecirc;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>V&igrave; vậy, sự phối trộn giữa Arabica v&agrave; Robusta sẽ tạo n&ecirc;n sự c&acirc;n bằng v&agrave; phong ph&uacute; cho t&aacute;ch c&agrave; ph&ecirc;. Hương thơm của Arabica khi kết hợp với sự đậm đ&agrave; của Robusta sẽ tạo n&ecirc;n một loại c&agrave; ph&ecirc; ho&agrave;n hảo v&agrave; ph&ugrave; hợp với hương lẫn vị c&agrave; ph&ecirc; Việt Nam.<br />\r\n<br />\r\nTỷ lệ pha trộn (c&ograve;n được gọi l&agrave; blending) 30% Arabica 70% Robusta th&iacute;ch hợp cho người th&iacute;ch gu đắng nhưng muốn hương thơm nhiều hơn. Thưởng thức d&ograve;ng sản phẩm c&agrave; ph&ecirc; phối trộn nguy&ecirc;n chất giữa Arabica v&agrave; Robusta, bạn sẽ c&oacute; th&ecirc;m c&oacute; trải nghiệm mới về sự phong ph&uacute;, t&iacute;nh mới lạ v&agrave; cực kỳ hấp dẫn trong thế giới c&agrave; ph&ecirc; m&agrave; bạn c&ograve;n phải kh&aacute;m ph&aacute; rất nhiều.<br />\r\n&nbsp;</p>\r\n\r\n<p>Ly c&agrave; ph&ecirc; ngon cũng lắm c&aacute;ch pha</p>\r\n\r\n<p>Ngo&agrave;i nguy&ecirc;n liệu được lựa chọn kỹ lưỡng, phối trộn tinh tế, c&aacute;ch pha cũng g&oacute;p phần kh&ocirc;ng nhỏ v&agrave;o việc tạo n&ecirc;n hương vị lu&ocirc;n cuốn của một t&aacute;ch c&agrave; ph&ecirc;, cho d&ugrave; bạn thưởng thức tại nh&agrave; hay ngo&agrave;i qu&aacute;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src="imgs/news5/img3.jpg" /></p>\r\n\r\n<p>Một ly c&agrave; ph&ecirc; nguy&ecirc;n chất chuẩn bị kỹ lưỡng sẽ mang lại hương vị đậm đ&agrave; kh&oacute; qu&ecirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong thế kỷ 19 người ta cũng đ&atilde; biết d&ugrave;ng phin để pha c&agrave; ph&ecirc; với nguy&ecirc;n tắc chung vẫn l&agrave; cho nước n&oacute;ng đi qua c&agrave; ph&ecirc; bột. Nước sẽ đi từ tr&ecirc;n xuống nhờ trọng lực, c&ograve;n c&agrave; ph&ecirc; bột được giữ lại nhờ bộ lọc.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src="imgs/news5/img4.jpg" /></p>\r\n\r\n<p>Trước khi pha, bạn cần đảm bảo phin v&agrave; ly t&aacute;ch được sạch sẽ để thưởng thức trọn vẹn hương vị thơm nồng của ly c&agrave; ph&ecirc; nguy&ecirc;n chất</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Theo c&aacute;ch n&agrave;y, người ta hay d&ugrave;ng bột c&agrave; ph&ecirc; xay vừa. Trước khi pha, bạn cần đảm bảo phin v&agrave; ly t&aacute;ch được sạch sẽ v&agrave; kh&ocirc; r&aacute;o bằng c&aacute;ch tr&aacute;ng qua nước s&ocirc;i. H&atilde;y sử dụng nước tinh khiết, nước c&agrave;ng tinh khiết ly c&agrave; ph&ecirc; của bạn c&agrave;ng thơm ngon. Lượng c&agrave; ph&ecirc; cần d&ugrave;ng t&ugrave;y thuộc v&agrave;o sở th&iacute;ch của mỗi người. Tuy nhi&ecirc;n, trung b&igrave;nh sử dụng c&agrave; ph&ecirc; c&oacute; tỉ lệ 5/10 hoặc 6/10 phin.<br />\r\n<br />\r\nKhi c&agrave; ph&ecirc; nhỏ hết xuống ly, c&oacute; thể cho đường v&agrave;o khuấy tan, uống n&oacute;ng hoặc th&ecirc;m đ&aacute;. Nếu uống c&agrave; ph&ecirc; sữa, n&ecirc;n cho sữa đặc v&agrave;o ly trước, rồi đặt ly n&agrave;y v&agrave;o một ly c&oacute; chế nước s&ocirc;i sẵn, sau đ&oacute; mới để phin c&agrave; ph&ecirc; l&ecirc;n tr&ecirc;n rồi ch&acirc;m nước, c&agrave; ph&ecirc; sẽ giữ được độ n&oacute;ng cần thiết.<br />\r\n<br />\r\nTrước khi th&ecirc;m đ&aacute; v&agrave;o, cần khuấy tan c&agrave; ph&ecirc; v&agrave; đường hoặc sữa, để c&aacute;c th&agrave;nh phần n&agrave;y ho&agrave;n to&agrave;n h&ograve;a lẫn v&agrave;o nhau. C&agrave; ph&ecirc; pha chế xong tuyệt đối kh&ocirc;ng h&acirc;m n&oacute;ng lại v&igrave; sẽ bay hết m&ugrave;i vị.<br />\r\n<br />\r\nBạn c&oacute; thể xem th&ecirc;m b&iacute; quyết pha c&agrave; ph&ecirc; nguy&ecirc;n chất tại đ&acirc;y: http://bit.ly/1CGYAwH<br />\r\n<br />\r\nD&ugrave; c&oacute; sự kh&aacute;c biệt trong c&aacute;ch pha chế t&ugrave;y theo điều kiện của từng người, nhưng c&aacute;c nguy&ecirc;n tắc chung nhất để c&oacute; một t&aacute;ch c&agrave; ph&ecirc; ngon vẫn l&agrave;: Nước s&ocirc;i, ng&acirc;m, thấm qua phin v&agrave; thẩm thấu nhờ &aacute;p suất. Theo đ&oacute;, d&ugrave; bạn chọn phương ph&aacute;p n&agrave;o th&igrave; vẫn cần phải c&oacute;: nước tinh khiết, kh&ocirc;ng c&oacute; chlorine, nhiệt độ từ 90-95 độ C. Nếu nước kh&ocirc;ng đủ n&oacute;ng, c&aacute;c hương vị của c&agrave; ph&ecirc; sẽ kh&ocirc;ng được chiết xuất hết, khiến cho thức uống c&oacute; vị đắng. Ngo&agrave;i ra, c&aacute;c dụng cụ d&ugrave;ng để pha chế c&agrave; ph&ecirc; cần phải rất sạch sẽ, c&oacute; như thế, bạn mới c&oacute; thể thưởng thức trọn vẹn hương vị v&agrave; sự quyến rũ trong từng giọt c&agrave; ph&ecirc;.<br />\r\n<br />\r\nC&agrave; ph&ecirc; phối trộn thật tuyệt vời v&agrave; đ&ograve;i hỏi cần phải c&oacute; một ch&uacute;t tinh tế, h&atilde;y thử s&aacute;ng tạo để tạo cho m&igrave;nh một c&ocirc;ng thức ri&ecirc;ng ph&ugrave; hợp với khẩu vị của m&igrave;nh nh&eacute;. Ch&uacute;c c&aacute;c bạn th&agrave;nh c&ocirc;ng với niềm đam m&ecirc; m&atilde;nh liệt với c&agrave; ph&ecirc;!&nbsp;</p>\r\n', 25, 'Nguyễn Thanh Tình - PHP39 (Sưu tầm)', NULL, 1, '2019-07-28 11:53:05'),
(19, 'Cách pha cà phê Drip – Thơm nhẹ nhàng quyến rũ', 2, 1, 'news-1564327984img1.jpg', '', '<p>Pha c&agrave; ph&ecirc; kiểu Drip Coffee l&agrave; một c&aacute;ch pha c&agrave; ph&ecirc; đơn giản, hiệu quả, được phương t&acirc;y ưa chuộng. Drip Coffee sử dụng phương ph&aacute;p nhỏ giọt bằng giấy lọc v&agrave; một dụng cụ kh&aacute; giống với phin c&agrave; ph&ecirc; của VN.</p>\r\n\r\n<p><strong>Bước 1</strong>&nbsp;&ndash; Đun lượng nước s&ocirc;i cần thiết &ndash; khoảng 400ml</p>\r\n\r\n<p><img src="imgs/cachphacaphedrip/img2.jpg" /></p>\r\n\r\n<p><strong>Bước 2</strong>&nbsp;&ndash; C&acirc;n ch&iacute;nh x&aacute;c lượng c&agrave; ph&ecirc; bạn muốn d&ugrave;ng, khoảng 25-30g</p>\r\n\r\n<p><img src="imgs/cachphacaphedrip/img3.jpg" /></p>\r\n\r\n<p><strong>Bước 3</strong>&nbsp;&ndash; Xay hạt c&agrave; ph&ecirc; &ndash; n&ecirc;n xay th&ocirc;, k&iacute;ch cỡ như hột muối biển</p>\r\n\r\n<p><img src="imgs/cachphacaphedrip/img4.jpg" /></p>\r\n\r\n<p><strong>Bước 4</strong>&nbsp;&ndash; Gấp miếng giấy lọc</p>\r\n\r\n<p><img src="imgs/cachphacaphedrip/img5.jpg" /></p>\r\n\r\n<p><strong>Bước 5</strong>&nbsp;&ndash; Đặt giấy lọc v&agrave;o phin</p>\r\n\r\n<p><img src="imgs/cachphacaphedrip/img6.jpg" /></p>\r\n\r\n<p><strong>Bước 6</strong>&nbsp;&ndash; R&oacute;t một &iacute;t nước s&ocirc;i v&agrave;o để giấy thấm nước</p>\r\n\r\n<p><img src="imgs/cachphacaphedrip/img7.jpg" /></p>\r\n\r\n<p><strong>Bước 7</strong>&nbsp;&ndash; Đổ c&agrave; ph&ecirc; v&agrave;o phin rồi lắc nhẹ để bề mặt bằng phẳng</p>\r\n\r\n<p><img src="imgs/cachphacaphedrip/img8.jpg" /></p>\r\n\r\n<p><strong>Bước 8</strong>&nbsp;&ndash; Bắt đầu từ giữa, đổ nước s&ocirc;i nhẹ nh&agrave;ng v&agrave;o c&agrave; ph&ecirc;, theo v&ograve;ng tr&ograve;n từ trong ra ngo&agrave;i, kh&ocirc;ng đổ l&ecirc;n giấy lọc. Lượng nước s&ocirc;i n&ecirc;n gấp hai lần lượng c&agrave; ph&ecirc;, v&iacute; dụ &ndash; nếu sử dụng 25g c&agrave; ph&ecirc; th&igrave; ta sẽ d&ugrave;ng 50g nước. Sau đ&oacute; đợi 30 gi&acirc;y, bạn sẽ thấy c&agrave; ph&ecirc; nở ra</p>\r\n\r\n<p><img src="imgs/cachphacaphedrip/img9.jpg" /></p>\r\n\r\n<p><strong>Bước 9</strong>&nbsp;&ndash; Khi c&agrave; ph&ecirc; nở xong, tiếp tục, r&oacute;t nước s&ocirc;i c&ograve;n lại nhẹ nh&agrave;ng, từ ngo&agrave;i v&agrave;o trong, từng &iacute;t một. Khi n&agrave;o bạn đ&atilde; cảm thấy lượng c&agrave; ph&ecirc; m&igrave;nh cần đ&atilde; đủ th&igrave; h&atilde;y dừng lại.</p>\r\n\r\n<p><img src="imgs/cachphacaphedrip/img10.jpg" /></p>\r\n\r\n<p><strong>Bước cuối c&ugrave;ng</strong>&nbsp;&ndash; B&acirc;y giờ th&igrave; bạn c&oacute; thể thưởng thức c&agrave; ph&ecirc; của m&igrave;nh được rồi !</p>\r\n\r\n<p>&nbsp;</p>\r\n', 25, 'Nguyễn Thanh Tình - PHP39 (Sưu tầm)', NULL, 1, '2019-07-28 11:55:49'),
(20, 'Cách pha cà phê Capuchino – Latte art – Ngon đúng phong cách Ý', 2, 1, 'news-1564327958img2.jpg', '', '<p>&nbsp;</p>\r\n\r\n<p><img src="imgs/cachphacaphecapuchino/img1.jpg" /></p>\r\n\r\n<p>Cafe cappuccino l&agrave; một m&oacute;n cafe truyền thống của người &Yacute;. Thật kh&oacute; để x&aacute;c định khoảng thời gian ch&iacute;nh x&aacute;c Latte Art được ph&aacute;t minh v&agrave; thậm ch&iacute; do ai ph&aacute;t minh ra cũng kh&ocirc;ng r&otilde;. Những người đam m&ecirc; c&agrave; ph&ecirc; cho rằng David Schomer l&agrave; người đầu ti&ecirc;n định nghĩa n&oacute; ở giữa thập ni&ecirc;n 80. Trong khi đ&oacute;, tại &Yacute;, người đ&agrave;n &ocirc;ng mang t&ecirc;n Lugi Lupi cũng l&agrave;m điều tương tự trong khoảng thời gian tương tự.</p>\r\n\r\n<p>Một điều chắc chắn l&agrave; nếu bạn muốn c&oacute; một kỹ thuật vẽ latte art xuất sắc th&igrave; bạn phải luyện tập rất nhiều. N&oacute; cũng giống như bạn tập viết vậy, khi mới cầm c&acirc;y b&uacute;t th&igrave; bạn chỉ viết được những đường cong xi&ecirc;u vẹo, vụng về, nhưng dần dần qua thời gian bạn sẽ l&agrave;m được điều bạn muốn. Trong giới c&agrave; ph&ecirc; cũng c&oacute; những tranh luận về việc d&ugrave;ng loại sữa n&agrave;o v&agrave; đ&aacute;nh bọt ra sao, nhưng theo t&ocirc;i th&igrave; chỉ cần bọt ra mịn để bạn vẽ đẹp th&igrave; loại sữa đ&oacute; sử dụng được. Thường thấy nhất l&agrave; sữa full cream hoặc sữa thanh tr&ugrave;ng, loại sữa m&agrave; chỉ c&oacute; thời gian sử dụng trong 7 hoặc 10 ng&agrave;y. V&agrave; bạn phải sử dụng sữa lạnh, n&oacute; cũng gi&uacute;p bạn dễ tạo bọt.</p>\r\n\r\n<p>C&aacute;ch l&agrave;m capuchino (cappuccino) cho mọi loại m&aacute;y pha cafe espresso</p>\r\n\r\n<p>L&agrave;m capuchino c&oacute; vẻ đơn giản, nhưng rất kh&oacute; để l&agrave;m tốt. Điều quan trọng cần nhớ l&agrave; c&oacute; hai giai đoạn: nạp kh&ocirc;ng kh&iacute; &ndash; barista gọi l&agrave; &ldquo;stretching&rdquo;, v&agrave; l&agrave;m sữa &ndash; barista gọi l&agrave; &ldquo;texturing&rdquo;. Bạn cần cả hai để c&oacute; thể l&agrave;m dạng sữa bạn th&iacute;ch v&agrave; tạo ra nghệ thuật cafe tuyệt vời.</p>\r\n\r\n<p>Để nạp kh&ocirc;ng kh&iacute; v&agrave;o sữa, bạn chỉ cần đặt đầu v&ograve;i đ&aacute;nh hơi v&agrave;o sữa, sau đ&oacute; bật cần, tạo ra v&ograve;ng xo&aacute;y. V&ograve;ng xo&aacute;y sữa n&agrave;y sẽ bắt lấy kh&ocirc;ng kh&iacute; ở bề mặt v&agrave; kết hợp n&oacute; v&agrave;o sữa. Khi đ&atilde; lấy đủ kh&ocirc;ng kh&iacute;, bạn đặt s&acirc;u v&ograve;i đ&aacute;nh sữa v&agrave;o sữa để tiếp tục nhũ h&oacute;a n&oacute;. Đối với latte, bạn chỉ cần một &iacute;t bọt n&ecirc;n bạn sẽ nạp &iacute;t kh&ocirc;ng kh&iacute;, đối với cappuccino th&igrave; bạn cần nhiều bọt hơn.</p>\r\n\r\n<p><img src="imgs/cachphacaphecapuchino/img2.jpg" /><img src="imgs/cachphacaphecapuchino/img3.jpg" /></p>\r\n\r\n<p>Step 1 &ndash; Đổ sữa v&agrave;o ca đ&aacute;nh sữa. Nếu bạn muốn sử dụng 300ml sữa th&igrave; d&ugrave;ng loại ca đ&aacute;nh sữa chứa được 400ml, c&ograve;n nếu bạn muốn sử dụng 400ml sữa th&igrave; h&atilde;y d&ugrave;ng loại ca chứa được 600ml sữa. Lu&ocirc;n phải c&oacute; khoảng trống để sữa tạo bọt, tốt nhất l&agrave; lượng sữa chỉ ở mức một nửa như trong h&igrave;nh</p>\r\n\r\n<p><img src="imgs/cachphacaphecapuchino/img4.jpg" /><img src="imgs/cachphacaphecapuchino/img5.jpg" /></p>\r\n\r\n<p>Step 2 &ndash; C&ocirc;ng việc kh&oacute; khăn đầu ti&ecirc;n của bạn l&agrave; tạo bọt sữa. Cầm ca đ&aacute;nh sữa theo hướng của v&ograve;i đ&aacute;nh sữa, h&atilde;y nh&igrave;n h&igrave;nh v&agrave; bắt chước y chang. Yếu quyết của việc n&agrave;y l&agrave; l&agrave;m sao để sữa trong ca xoay tr&ograve;n, c&ograve;n hơi nước sẽ tự l&agrave;m sữa b&ocirc;ng l&ecirc;n.</p>\r\n\r\n<p><img src="imgs/cachphacaphecapuchino/img6.jpg" /><img src="imgs/cachphacaphecapuchino/img7.jpg" /></p>\r\n\r\n<p>Step 3 &ndash; Đ&atilde; tới l&uacute;c bạn đưa ca đ&aacute;nh sữa l&ecirc;n v&agrave; vặn n&uacute;t để v&ograve;i đ&aacute;nh sữa hoạt động. Đừng sợ h&atilde;i m&agrave; bu&ocirc;ng tay ra, kh&ocirc;ng c&oacute; điều g&igrave; khủng khiếp cả. Để tạo được bọt tốt th&igrave; v&ograve;i đ&aacute;nh sữa kh&ocirc;ng được ch&igrave;m qu&aacute; s&acirc;u m&agrave; chỉ được c&aacute;ch bề mặt một hoặc hai cm. H&atilde;y nhớ, đặt v&ograve;i l&agrave;m sao để sữa c&oacute; thể xoay tr&ograve;n trong ca, v&agrave; chỉ nh&uacute;ng v&ograve;i đ&aacute;nh sữa s&acirc;u 1-2cm. Trong l&uacute;c đ&aacute;nh sữa h&atilde;y d&ugrave;ng tay cảm nhận nhiệt độ của sữa, khi sữa đạt nhiệt độ 75-85 &deg;C th&igrave; ta c&oacute; thể dừng qu&aacute; tr&igrave;nh đ&aacute;nh sữa.</p>\r\n\r\n<p><img src="imgs/cachphacaphecapuchino/img8.jpg" /></p>\r\n\r\n<p>Step 4 &ndash; Sau khi đ&aacute;nh sữa xong, ta xả một &iacute;t hơi nước v&agrave;o khay tho&aacute;t nước. Mục đ&iacute;ch của việc n&agrave;y l&agrave; l&agrave;m sạch phần b&ecirc;n trong v&ograve;i. Sau đ&oacute; d&ugrave;ng khăn ẩm sạch lau v&ograve;i đ&aacute;nh sữa cẩn thận. Nếu kh&ocirc;ng c&oacute; bước xả &ndash; lau n&agrave;y, sữa sẽ kh&ocirc; cứng lại tr&ecirc;n v&ograve;i g&acirc;y mất vệ sinh cho những đợt đ&aacute;nh sữa kế tiếp, v&agrave; l&acirc;u ng&agrave;y sẽ g&acirc;y ra nghẹt v&ograve;i đ&aacute;nh sữa.</p>\r\n\r\n<p><img src="imgs/cachphacaphecapuchino/img9.jpg" /></p>\r\n\r\n<p>Step 5 &ndash; L&uacute;c ban đầu chỉ c&oacute; nửa ca sữa, sau khi đ&aacute;nh sữa đ&atilde; tạo th&agrave;nh một lớp bọt d&agrave;y v&agrave; mịn ph&iacute;a tr&ecirc;n bề mặt. Đ&acirc;y ch&iacute;nh l&agrave; nguy&ecirc;n liệu để ta tạo h&igrave;nh. G&otilde; nhẹ v&agrave; xoay tr&ograve;n ca đ&aacute;nh sữa tr&ecirc;n mặt b&agrave;n để bong b&oacute;ng lớn bị bể, chỉ c&ograve;n lại lớp bọt mịn</p>\r\n\r\n<p><img src="imgs/cachphacaphecapuchino/img10.jpg" /></p>\r\n\r\n<p>Step 6 &ndash; Đổ sữa từ từ v&agrave;o t&aacute;ch espresso, h&atilde;y cứng tay để d&ograve;ng sữa lu&ocirc;n đều đặn, rồi sẽ tới l&uacute;c những bọt trắng nổi l&ecirc;n, t&ocirc;i tin l&agrave; bạn sẽ cảm nhận được. Khi đổ ra được d&ograve;ng sữa trắng tr&ecirc;n bề mặt rồi th&igrave; h&atilde;y từ từ điều khiển n&oacute;. Tập từ từ bạn sẽ th&agrave;nh thạo hơn. H&atilde;y nh&igrave;n những em b&eacute; tập viết với những n&eacute;t chữ xi&ecirc;u vẹo. Nếu tập viết 200 lần c&oacute; thể viết đẹp th&igrave; bạn cũng c&oacute; thể tập vẽ latte art 200 lần ( tập bằng nước, tự tưởng tượng).</p>\r\n\r\n<p><img src="imgs/cachphacaphecapuchino/img11.jpg" /><img src="imgs/cachphacaphecapuchino/img12.jpg" /><img src="imgs/cachphacaphecapuchino/img13.jpg" /><img src="imgs/cachphacaphecapuchino/img14.jpg" /></p>\r\n\r\n<p>Muốn vẽ đẹp l&agrave; cả một qu&aacute; tr&igrave;nh luyện tập l&acirc;u d&agrave;i v&agrave; bền bỉ. Sữa ch&iacute;nh l&agrave; c&acirc;y b&uacute;t ch&igrave; của bạn, h&atilde;y l&agrave;m chủ n&oacute;. Ch&uacute;c c&aacute;c bạn th&agrave;nh c&ocirc;ng !</p>\r\n', 30, 'Nguyễn Thanh Tình - PHP39 (Sưu tầm)', NULL, 1, '2019-07-28 11:57:02'),
(21, 'Cách pha cà phê Syphon – Phong cách Nhật Bản', 2, 1, 'news-1564327939img3.jpg', '', '<p>&nbsp;</p>\r\n\r\n<p><img src="imgs/cachphacaphesyphon/img1.jpg" /></p>\r\n\r\n<p>PHA C&Agrave; PH&Ecirc; BẰNG B&Igrave;NH SYPHON &ndash; BẠN Đ&Atilde; THỬ?</p>\r\n\r\n<p>Sử dụng Syphon l&agrave; c&aacute;ch pha chế cafe &aacute;p dụng nguy&ecirc;n l&yacute; thẩm thấu ngược, bắt nguồn từ Đức. Phong c&aacute;ch n&agrave;y ph&aacute;t triển hơn khi du nhập v&agrave;o Nhật từ những năm 1950, người Nhật đ&atilde; cải biến để tạo n&ecirc;n nghệ thuật pha c&agrave; ph&ecirc; tuyệt vời n&agrave;y.</p>\r\n\r\n<p>Kiểu pha c&agrave; ph&ecirc; n&agrave;y đ&atilde; du nhập v&agrave;o Việt Nam v&agrave; thu h&uacute;t rất nhiều sự quan t&acirc;m của c&aacute;c t&iacute;n đồ y&ecirc;u th&iacute;ch c&agrave; ph&ecirc;. Ng&agrave;y c&agrave;ng c&oacute; nhiều qu&aacute;n c&agrave; ph&ecirc; mang phong c&aacute;ch n&agrave;y v&agrave;o kh&ocirc;ng gian pha chế của m&igrave;nh để thu h&uacute;t kh&aacute;ch h&agrave;ng. Tại nh&agrave; bạn cũng c&oacute; thể tự pha cho m&igrave;nh một t&aacute;ch c&agrave; ph&ecirc; với b&igrave;nh syphon nếu muốn.</p>\r\n\r\n<p>C&aacute;ch pha c&agrave; ph&ecirc; bằng siphon cũng tựa tựa như c&aacute;ch pha c&agrave; ph&ecirc; bằng b&igrave;nh Moka vậy, đặc biệt hơn ở chỗ khi hơi nước bay l&ecirc;n thấm qua c&agrave; ph&ecirc; v&agrave; kết th&uacute;c qu&aacute; tr&igrave;nh n&agrave;y, ta tắt bếp để c&agrave; ph&ecirc; một lần nữa thấm ngược lại b&igrave;nh đựng. B&acirc;y giờ th&igrave; bạn h&atilde;y xem từng bước nh&eacute;.</p>\r\n\r\n<p><img src="imgs/cachphacaphesyphon/img2.jpg" /></p>\r\n\r\n<p>Step 1 &ndash; Đầu ti&ecirc;n h&atilde;y ng&acirc;m tấm lọc c&agrave; ph&ecirc; v&agrave;o nước s&ocirc;i trong 3-5 ph&uacute;t, sau đ&oacute; đặt ngay ngắn v&agrave;o phễu</p>\r\n\r\n<p><img src="imgs/cachphacaphesyphon/img3.jpg" /></p>\r\n\r\n<p>Step 2 &ndash; Cẩn thận đổ lượng nước s&ocirc;i cần d&ugrave;ng v&agrave;o trong b&igrave;nh chứa h&igrave;nh cầu (khoảng 300ml)</p>\r\n\r\n<p><img src="imgs/cachphacaphesyphon/img4.jpg" /></p>\r\n\r\n<p>Step 3 &ndash; Xay khoảng 45g c&agrave; ph&ecirc; v&agrave; đổ c&agrave; ph&ecirc; v&agrave;o phễu. K&iacute;ch cỡ bột c&agrave; ph&ecirc; n&ecirc;n mịn một ch&uacute;t, giống như pha phin vậy</p>\r\n\r\n<p><img src="imgs/cachphacaphesyphon/img5.jpg" /></p>\r\n\r\n<p>Step 4 &ndash; Cẩn thận lắp phễu v&agrave;o b&igrave;nh chứa h&igrave;nh cầu, sau đ&oacute; đặt bếp lửa ở ph&iacute;a dưới giống như h&igrave;nh, hoặc sử dụng bếp nhiệt halogen</p>\r\n\r\n<p><img src="imgs/cachphacaphesyphon/img6.jpg" /></p>\r\n\r\n<p>Step 5 &ndash; Bật bếp để đun nước, nước s&ocirc;i sẽ bốc hơi v&agrave; hơi nước sẽ thấm qua c&agrave; ph&ecirc; ở phễu</p>\r\n\r\n<p><img src="imgs/cachphacaphesyphon/img7.jpg" /></p>\r\n\r\n<p>Step 6 &ndash; Khi nước bốc hơi l&ecirc;n hẳn h&atilde;y cứ tiếp tục để bếp như vậy trong v&ograve;ng 10 gi&acirc;y, d&ugrave;ng đũa tre khuấy tr&ograve;n, sau đ&oacute; h&atilde;y tắt bếp v&agrave; đợi c&agrave; ph&ecirc; chảy ngược xuống b&igrave;nh đựng</p>\r\n\r\n<p><img src="imgs/cachphacaphesyphon/img8.jpg" /><img src="imgs/cachphacaphesyphon/img9.jpg" /><img src="imgs/cachphacaphesyphon/img10.jpg" /></p>\r\n\r\n<p>B&acirc;y giờ th&igrave; thưởng thức c&agrave; ph&ecirc; th&ocirc;i !</p>\r\n', 30, 'Nguyễn Thanh Tình - PHP39 (Sưu tầm)', NULL, 1, '2019-07-28 11:58:25'),
(22, 'Cách pha cà phê Aeropress – Đơn giản nhưng đậm đà', 1, 1, 'news-1564327899img4.jpg', '', '<p>&nbsp;</p>\r\n\r\n<p><img src="imgs/cachphacapheaeropress/img1.jpg" /></p>\r\n\r\n<p>C&agrave; ph&ecirc; c&oacute; mu&ocirc;n h&igrave;nh vạn trạng v&agrave; người ta cũng c&oacute; v&ocirc; số c&aacute;ch để chế biến c&agrave; ph&ecirc;. Dọc theo phần hướng dẫn pha chế c&agrave; ph&ecirc; n&agrave;y bạn sẽ gặp kh&ocirc;ng &iacute;t điều mới lạ, v&agrave; biết đ&acirc;u chừng sau n&agrave;y khi bạn ngồi nghịch một c&aacute;i g&igrave; đ&oacute; th&igrave; bạn s&aacute;ng chế ra c&aacute;ch pha c&agrave; ph&ecirc; cho ri&ecirc;ng m&igrave;nh th&igrave; sao. Tr&ecirc;n những cơ sở suy đo&aacute;n của t&ocirc;i th&igrave; người s&aacute;ng chế ra b&igrave;nh AeroPress n&agrave;y chắc l&agrave; một b&aacute;c sĩ hoặc dược sĩ, tiếp x&uacute;c nhiều với ống ti&ecirc;m !</p>\r\n\r\n<p>N&oacute;i thế th&ocirc;i chứ c&aacute;ch pha n&agrave;y rất tuyệt vời, d&ugrave;ng &aacute;p suất để n&eacute;n c&agrave; ph&ecirc;, gi&uacute;p c&agrave; ph&ecirc; chiết xuất ra nhiều chất hơn. Nếu kh&ocirc;ng x&eacute;t về văn h&oacute;a th&igrave; t&ocirc;i nghĩ c&aacute;ch pha n&agrave;y tốt hơn c&aacute;ch pha bằng phin ch&uacute;ng ta đang x&agrave;i.</p>\r\n\r\n<p>C&aacute;ch pha c&agrave; ph&ecirc; Aeropress l&agrave; c&aacute;ch pha c&agrave; ph&ecirc; mới nhất, kh&aacute; đơn giản, nhanh ch&oacute;ng m&agrave; vẫn mang lại chất lượng c&agrave; ph&ecirc; rất đậm đ&agrave;, thơm ngon</p>\r\n\r\n<p><img src="imgs/cachphacapheaeropress/img2.jpg" /></p>\r\n\r\n<p><strong>Bước 1</strong>&nbsp;&ndash; Trước ti&ecirc;n vẫn l&agrave; đặt ấm nước l&ecirc;n bếp, nếu bạn muốn đậm đặc th&igrave; bạn pha &iacute;t nước, nếu bạn muốn lo&atilde;ng hơn theo kiểu t&acirc;y th&igrave; bạn cho nhiều nước hơn. T&ocirc;i sẽ giữ nguy&ecirc;n tỉ lệ ở v&iacute; dụ minh họa &ndash; 200g nước pha với 15g c&agrave; ph&ecirc;</p>\r\n\r\n<p><img src="imgs/cachphacapheaeropress/img3.jpg" /><img src="imgs/cachphacapheaeropress/img4.jpg" /></p>\r\n\r\n<p><strong>Bước 2</strong>&nbsp;&ndash; Đặt giấy lọc v&agrave;o phần nắp nhựa</p>\r\n\r\n<p><img src="imgs/cachphacapheaeropress/img5.jpg" /></p>\r\n\r\n<p><strong>Bước 3</strong>&nbsp;&ndash; Đặt giấy lọc v&agrave;o phần nắp nhựa</p>\r\n\r\n<p><img src="imgs/cachphacapheaeropress/img6.jpg" /><img src="imgs/cachphacapheaeropress/img7.jpg" /></p>\r\n\r\n<p><strong>Bước 4</strong>&nbsp;&ndash; Xay c&agrave; ph&ecirc; : hạt c&agrave; ph&ecirc; cần được xay mịn, tốt nhất l&agrave; mịn hơn cả c&agrave; ph&ecirc; sử dụng cho pha phin</p>\r\n\r\n<p><img src="imgs/cachphacapheaeropress/img8.jpg" /></p>\r\n\r\n<p><strong>Bước 5</strong>&nbsp;&ndash; Lắp r&aacute;p &ndash; h&atilde;y chắc chắn l&agrave; c&aacute;i b&igrave;nh sạch v&agrave; kh&ocirc;, hơi ẩm hoặc cặn c&oacute; thể sẽ l&agrave;m hư b&igrave;nh AeroPress của bạn.</p>\r\n\r\n<p><img src="imgs/cachphacapheaeropress/img9.jpg" /></p>\r\n\r\n<p><img src="imgs/cachphacapheaeropress/img10.jpg" /></p>\r\n\r\n<p><strong>Bước 6</strong>&nbsp;&ndash; Đặt ngược b&igrave;nh AeroPress l&ecirc;n mặt b&agrave;n, sau đ&oacute; đổ c&agrave; ph&ecirc; v&agrave;o b&igrave;nh. Trong h&igrave;nh, người ta sử dụng c&acirc;n điện tử để c&oacute; thể đong lượng c&agrave; ph&ecirc;, lượng nước một c&aacute;ch ch&iacute;nh x&aacute;c nhất. H&atilde;y cẩn thận kh&ocirc;ng để c&agrave; ph&ecirc; rơi tr&ecirc;n phần đỉnh, nếu c&oacute; th&igrave; h&atilde;y qu&eacute;t sạch, nếu cứ để c&agrave; ph&ecirc; vương v&atilde;i tr&ecirc;n phần r&igrave;a của đỉnh rồi lắp nắp v&ocirc;, về sau sẽ l&agrave;m b&igrave;nh bị hở, dẫn đến hư b&igrave;nh.</p>\r\n\r\n<p><img src="imgs/cachphacapheaeropress/img11.jpg" /></p>\r\n\r\n<p><strong>Bước 7</strong>&nbsp;&ndash; Chế nước s&ocirc;i v&agrave;o trong b&igrave;nh theo tỉ lệ 2:1, v&iacute; dụ 15g coffe th&igrave; ta d&ugrave;ng 30g nước</p>\r\n\r\n<p><img src="imgs/cachphacapheaeropress/img12.jpg" /></p>\r\n\r\n<p><strong>Bước 8</strong>&nbsp;&ndash; D&ugrave;ng đũa tre, khuấy nhẹ, kh&ocirc;ng n&ecirc;n khuấy nhiều &ndash; sau đ&oacute; đợi 30 gi&acirc;y để c&agrave; ph&ecirc; nở ra</p>\r\n\r\n<p><img src="imgs/cachphacapheaeropress/img13.jpg" /><img src="imgs/cachphacapheaeropress/img14.jpg" /></p>\r\n\r\n<p><strong>Bước 9</strong>&nbsp;&ndash; Sau khi c&agrave; ph&ecirc; nở ra, r&oacute;t phần nước s&ocirc;i c&ograve;n lại v&agrave;o b&igrave;nh rồi đợi 1 ph&uacute;t</p>\r\n\r\n<p><img src="imgs/cachphacapheaeropress/img15.jpg" /></p>\r\n\r\n<p><strong>Bước 10</strong>&nbsp;&ndash; Trong khi đợi, d&ugrave;ng nước s&ocirc;i l&agrave;m ướt nắp v&agrave; giấy lọc, đồng thời tr&aacute;ng qua t&aacute;ch hoặc b&igrave;nh chứa c&agrave; ph&ecirc;.</p>\r\n\r\n<p><img src="imgs/cachphacapheaeropress/img16.jpg" /></p>\r\n\r\n<p><strong>Bước 11</strong>&nbsp;&ndash; Sau một ph&uacute;t, d&ugrave;ng đũa tre khuấy tr&ograve;n khoảng 10 v&ograve;ng</p>\r\n\r\n<p><img src="imgs/cachphacapheaeropress/img17.jpg" /></p>\r\n\r\n<p><strong>Bước 12</strong>&nbsp;&ndash; Đ&oacute;ng phần nắp v&agrave;o b&igrave;nh &ndash; lưu &yacute; rằng c&agrave; ph&ecirc; đang rất n&oacute;ng, h&atilde;y cẩn thận.</p>\r\n\r\n<p><img src="imgs/cachphacapheaeropress/img18.jpg" /></p>\r\n\r\n<p><strong>Bước 13</strong>&nbsp;&ndash; Lật b&igrave;nh aeropress lại v&agrave; r&aacute;p v&agrave;o b&igrave;nh chứa c&agrave; ph&ecirc;, bắt đầu ấn giống b&igrave;nh french press. Tương tự, nếu bạn nhấn qu&aacute; dễ th&igrave; hạt c&agrave; ph&ecirc; đ&atilde; xay qu&aacute; th&ocirc;, c&ograve;n nếu bạn nhấn thấy kh&oacute; khăn một ch&uacute;t th&igrave; bạn đ&atilde; l&agrave;m đ&uacute;ng y&ecirc;u cầu.</p>\r\n\r\n<p><img src="imgs/cachphacapheaeropress/img19.jpg" /><img src="imgs/cachphacapheaeropress/img20.jpg" /></p>\r\n', 25, 'Nguyễn Thanh Tình - PHP39 (Sưu tầm)', NULL, 1, '2019-07-28 11:59:08'),
(23, '6 lý do khiến bạn không thể bỏ qua việc uống một ly cà phê mỗi ngày', 3, 1, 'news-1564327684img1.jpg', 'Thêm tin vui cho những ai là tin đồ của cà phê! Các nghiên cứu gần đây chỉ ra thêm những lý do chứng tỏ cà phê là một đồ uống rất tốt cho sức khỏe. Trong một nghiên cứu của các nhà khoa học tại Đại học Southampton chỉ ra rằng, cà phê có tác dụng giảm nguy cơ xơ gan, viêm gan gây ra do lạm dụng ... ', '<p>&nbsp;</p>\r\n\r\n<p><img src="imgs/6lydo/img1.jpg" /></p>\r\n\r\n<p>Trong một nghi&ecirc;n cứu của c&aacute;c nh&agrave; khoa học tại Đại học Southampton chỉ ra rằng, c&agrave; ph&ecirc; c&oacute; t&aacute;c dụng giảm nguy cơ xơ gan, vi&ecirc;m gan g&acirc;y ra do lạm dụng rượu. Theo Tổ chức Y tế thế giới, c&oacute; đến 50% c&aacute;c ca xơ gan xuất ph&aacute;t từ nguy&ecirc;n nh&acirc;n người bệnh lạm dụng rượu. Nhưng khi c&aacute;c nh&agrave; khoa học tiến h&agrave;nh nghi&ecirc;n cứu th&igrave; ph&aacute;t hiện, uống 1 cốc c&agrave; ph&ecirc; mỗi ng&agrave;y c&oacute; thể l&agrave;m giảm nguy cơ ph&aacute;t bệnh n&agrave;y tới 22%.</p>\r\n\r\n<p>Nghi&ecirc;n cứu tr&ecirc;n chỉ l&agrave; một trong rất nhiều t&aacute;c dụng tốt của c&agrave; ph&ecirc; đối với sức khỏe con người. Sau đ&acirc;y ch&uacute;ng t&ocirc;i xin chia sẻ những l&yacute; do tốt để bạn uống c&agrave; ph&ecirc; h&agrave;ng ng&agrave;y:</p>\r\n\r\n<p><img src="imgs/6lydo/img2.jpg" /></p>\r\n\r\n<p>C&agrave; ph&ecirc; c&oacute; t&aacute;c dụng giảm nguy cơ xơ gan, vi&ecirc;m gan g&acirc;y ra do lạm dụng rượu. (Ảnh minh họa: Internet)</p>\r\n\r\n<p>1. Giảm nguy cơ tử vong v&igrave; bệnh tim mạch</p>\r\n\r\n<p>Theo một nghi&ecirc;n cứu được c&ocirc;ng bố tr&ecirc;n tạp ch&iacute; Circulation của Hiệp hội tim mạch Mỹ v&agrave;o th&aacute;ng 11 năm 2015 cho biết rằng. Người uống 3 &ndash; 5 t&aacute;ch c&agrave; ph&ecirc; mỗi ng&agrave;y sẽ giảm nguy cơ tử vong sớm khi mắc một số bệnh gồm: bệnh tim mạch, bệnh thần kinh (Parkinson), bệnh tiểu đường tu&yacute;p 2 v&agrave; tự tử.</p>\r\n\r\n<p>Theo một nghi&ecirc;n cứu được c&ocirc;ng bố tr&ecirc;n tạp ch&iacute; Circulation của Hiệp hội tim mạch Mỹ v&agrave;o th&aacute;ng 11 năm 2015 cho biết rằng. Người uống 3 &ndash; 5 t&aacute;ch c&agrave; ph&ecirc; mỗi ng&agrave;y sẽ giảm nguy cơ tử vong sớm khi mắc một số bệnh gồm: bệnh tim mạch, bệnh thần kinh (Parkinson), bệnh tiểu đường tu&yacute;p 2 v&agrave; tự tử.</p>\r\n\r\n<p>2. Giảm nguy cơ t&aacute;i ph&aacute;t ung thư v&uacute; sau khi điều trị</p>\r\n\r\n<p>Th&aacute;ng 2 năm 2015, c&aacute;c nh&agrave; khoa học Thụy Điển c&ocirc;ng bố trong một nghi&ecirc;n cứu về ung thư học l&acirc;m s&agrave;ng chỉ ra rằng: Những bệnh nh&acirc;n ung thư v&uacute; uống &iacute;t nhất 2 t&aacute;ch c&agrave; ph&ecirc; mỗi ng&agrave;y sẽ c&oacute; k&iacute;ch thước khối u nhỏ hơn v&agrave; giảm tới 50% nguy cơ t&aacute;i ph&aacute;t so với những bệnh nh&acirc;n chỉ nhấm nh&aacute;p hoặc kh&ocirc;ng hề uống c&agrave; ph&ecirc;. Nghi&ecirc;n cứu n&agrave;y được tiến h&agrave;nh tr&ecirc;n 1090 bệnh nh&acirc;n ung thư v&uacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>3. Giảm nguy cơ tiểu đường loại 2</p>\r\n\r\n<p>Một đ&aacute;nh gi&aacute; v&agrave;o th&aacute;ng 4 năm 2014 của 3 nghi&ecirc;n cứu được tiến h&agrave;nh tr&ecirc;n 123.000 trong suốt 20 năm, c&aacute;c nh&agrave; nghi&ecirc;n cứu t&igrave;m thấy rằng, những người uống 1,5 t&aacute;ch c&agrave; ph&ecirc; mỗi ng&agrave;y trong v&ograve;ng 4 năm c&oacute; thể l&agrave;m giảm nguy cơ ph&aacute;t bệnh tiểu đường đến 11%. Tr&ecirc;n thực tế, những người uống 3 ly c&agrave; ph&ecirc; mỗi ng&agrave;y sẽ giảm nguy cơ mắc bệnh tiểu đường tu&yacute;p 2 so với người kh&ocirc;ng uống l&agrave; 37%.</p>\r\n\r\n<p><img src="imgs/6lydo/img3.jpg" /></p>\r\n\r\n<p>C&agrave; ph&ecirc; đặc biệt tốt cho phụ nữ v&igrave; c&oacute; thể giảm nguy cơ ung thư v&uacute;. (Ảnh minh họa: Internet)</p>\r\n\r\n<p>4. Khả năng sống s&oacute;t cao hơn cho những bệnh nh&acirc;n ung thư ruột kết</p>\r\n\r\n<p>Theo một nghi&ecirc;n cứu được c&ocirc;ng bố v&agrave;o th&aacute;ng 8 năm 2015 tr&ecirc;n tạp ch&iacute; Journal of Clinical Oncology cho biết, việc uống c&agrave; ph&ecirc; h&agrave;ng ng&agrave;y c&oacute; thể ngăn chặn nguy cơ t&aacute;i ph&aacute;t của ung thư ruột kết. Sau khi ph&acirc;n t&iacute;ch kết quả nghi&ecirc;n cứu của gần 1000 người đang điều trị bằng phẫu thuật v&agrave; h&oacute;a trị cho những bệnh nh&acirc;n ung thư đại tr&agrave;ng giai đoạn III, c&aacute;c nh&agrave; nghi&ecirc;n cứu ph&aacute;t hiện ra rằng: Người uống c&agrave; ph&ecirc; h&agrave;ng ng&agrave;y giảm tới 42% nguy cơ t&aacute;i ph&aacute;t, giảm 34% nguy cơ tử vong do ung thư hoặc c&aacute;c nguy&ecirc;n nh&acirc;n kh&aacute;c so với người kh&ocirc;ng hề sử dụng c&agrave; ph&ecirc;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>T&aacute;c giả nghi&ecirc;n cứu, Gi&aacute;m đốc bệnh viện Ung thư Dana &ndash; Faber, B&aacute;c sĩ Charles S.Fuchs cho biết: &ldquo;Nếu bạn l&agrave; một người nghiện rượu v&agrave; c&agrave; ph&ecirc; đang trong qu&aacute; tr&igrave;nh điều trị ung thư ruột kết th&igrave; đừng cai ch&uacute;ng. Nhưng nếu bạn kh&ocirc;ng bao giờ uống c&agrave; ph&ecirc; v&agrave; muốn bắt đầu th&igrave; n&ecirc;n thảo luận trước với b&aacute;c sĩ của m&igrave;nh để c&oacute; kết quả tốt nhất&rdquo;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>5. Tốt cho gan</p>\r\n\r\n<p>Trong một nghi&ecirc;n cứu v&agrave;o th&aacute;ng 10 năm 2014 được tiến h&agrave;nh tr&ecirc;n 28.000 người lớn của Tạp ch&iacute; Bệnh gan Hepatology c&ocirc;ng bố rằng: &ldquo;Những người ti&ecirc;u thụ nhiều hơn 3 cốc c&agrave; ph&ecirc; mỗi ng&agrave;y c&oacute; nồng độ men gan thấp hơn người b&igrave;nh thường&rdquo;. Những nhận định n&agrave;y cho thấy rằng những th&agrave;nh phần trong c&agrave; ph&ecirc; c&oacute; thể th&uacute;c đẩy sức khỏe của gan.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>6. Giảm nguy cơ tử vong</p>\r\n\r\n<p>Năm 2012, trong một nghi&ecirc;n cứu của Viện Ung thư quốc gia Mỹ (NCI) tr&ecirc;n gần 400.000 người lớn tuổi (50 &ndash; 71 tuổi) cho thấy những người thường xuy&ecirc;n uống c&agrave; ph&ecirc; hoặc c&aacute;c thực phẩm chứa cafein &iacute;t c&oacute; khả năng tử vong cho d&ugrave; c&oacute; h&agrave;ng loạt những bệnh hiểm ngh&egrave;o như: bệnh về tim mạch, bệnh về đường h&ocirc; hấp, chấn thương, tai nạn, đột quỵ, tiểu đường, nhiễm tr&ugrave;ng&hellip;</p>\r\n', 25, 'Nguyễn Thanh Tình - PHP39 (Sưu tầm)', NULL, 1, '2019-07-28 15:23:57'),
(24, 'Nâng cấp vẻ đẹp trai từ bã cà phê', 3, 1, 'news-1564328176img2.jpg', 'Mùa đông giá lạnh, hẳn ai cũng thích một ly café ấm thơm lừng. Phần bã café còn lại sau khi pha, bạn nên giữ lại, nó có rất nhiều tác dụng cho việc làm đẹp đó.', '<p>Tẩy da chết</p>\r\n\r\n<p><img src="imgs/6lydo/img2.jpg" /></p>\r\n\r\n<p>Chất caffeine trong b&atilde; caf&eacute; c&oacute; t&aacute;c dụng l&agrave;m tăng tuần ho&agrave;n m&aacute;u, l&agrave;m dịu phần da bị sưng, th&acirc;m quầng. Ch&uacute;t b&atilde; caf&eacute; c&ugrave;ng v&agrave;i giọt dầu olive cho phần b&atilde; vừa đủ s&aacute;nh, bạn đ&atilde; c&oacute; một hỗn hợp tẩy tế b&agrave;o chết lại c&oacute; c&ocirc;ng dụng dưỡng da rất tuyệt vời. Nếu muốn tăng hiệu quả hơn nữa, h&atilde;y th&ecirc;m v&agrave;o ch&uacute;t đường.</p>\r\n\r\n<p>B&atilde; caf&eacute; trộn đều với l&ograve;ng trắng trứng g&agrave;. Với da dầu bạn c&oacute; thể th&ecirc;m 1,2 th&igrave;a sữa chua, da kh&ocirc; n&ecirc;n th&ecirc;m ch&uacute;t x&iacute;u sữa tươi kh&ocirc;ng đường. B&ocirc;i hỗn hợp l&ecirc;n mặt v&agrave; để khoảng 15 ph&uacute;t cho kh&ocirc;, sau đ&oacute; rửa lại mặt với nước.</p>\r\n\r\n<p>Chống l&atilde;o h&oacute;a</p>\r\n\r\n<p>Nghiền nhuyễn nửa quả chuối ch&iacute;n c&ugrave;ng 2, 3 th&igrave;a b&atilde; caf&eacute;, trộn đều v&agrave; thoa l&ecirc;n mặt, để trong khoảng 20 ph&uacute;t rồi rửa mặt sạch.</p>\r\n\r\n<p>K&iacute;ch th&iacute;ch t&oacute;c mọc</p>\r\n\r\n<p><img src="imgs/6lydo/img3.jpg" /></p>\r\n\r\n<p>Sau khi gội đầu hoặc khi t&oacute;c ướt, h&atilde;y lấy b&atilde; caf&eacute; masage l&ecirc;n da đầu. Chất caffeine do c&oacute; t&aacute;c dụng k&iacute;ch th&iacute;ch tuần ho&agrave;n m&aacute;u n&ecirc;n cũng c&oacute; t&aacute;c dụng gi&uacute;p t&oacute;c mọc nhanh hơn. Nếu bạn ngại b&atilde; caf&eacute; kh&oacute; gội lại sạch, h&atilde;y pha caf&eacute; nguy&ecirc;n chất thật đặc rồi lấy phần cốt xoa l&ecirc;n da đầu v&agrave; massage, việc n&agrave;y cũng c&oacute; t&aacute;c dụng gần như tương đương.</p>\r\n\r\n<p>Đ&aacute;nh bay cellulite</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;c sản phẩm kem massage tan mỡ thường rất đắt tiền, bạn c&oacute; thể thực hiện tại nh&agrave; với sản phẩm tự chế như sau : b&atilde; caf&eacute; pha c&ugrave;ng ch&uacute;t &iacute;t dầu olive cho sền sệt vừa phải, xoa đều l&ecirc;n những v&ugrave;ng da sần, dễ bị t&iacute;ch tụ cellulite như bụng, đ&ugrave;i v&agrave; massage trong 5 ph&uacute;t sau đ&oacute; tắm lại bằng nước v&agrave; sữa tắm.</p>\r\n\r\n<p>C&aacute;ch thứ hai c&oacute; phần mất c&ocirc;ng hơn một ch&uacute;t để l&agrave;m tan cellulite l&agrave; sử dụng b&atilde; caf&eacute; ấm đắp l&ecirc;n bụng, đ&ugrave;i v&agrave; bọc lại, để y&ecirc;n trong v&ograve;ng 20-30 ph&uacute;t. N&ecirc;n thực hiện mỗi tuần 2-3 lần.</p>\r\n', 25, 'Nguyễn Thanh Tình - PHP39 (Sưu tầm)', NULL, 1, '2019-07-28 15:35:57');
INSERT INTO `news` (`id`, `title`, `category_id`, `admin_id`, `avatar`, `summary`, `content`, `comment_total`, `author`, `view`, `status`, `created_at`) VALUES
(25, 'Cà phê ngừa ung thư da, bảo vệ thị lực', 3, 1, '', 'Nghiên cứu mới đây của các nhà khoa học Mỹ đã cho thấy, chất caffeine trong cà phê có tác dụng rất tốt để bảo vệ da ngừa ung thư.\r\n\r\n', '<p>&nbsp;</p>\r\n\r\n<p><img src="imgs/caphenguaungthu/img1.jpg" /></p>\r\n\r\n<p>Nghi&ecirc;n cứu mới đ&acirc;y của c&aacute;c nh&agrave; khoa học Mỹ đ&atilde; cho thấy, chất caffeine trong c&agrave; ph&ecirc; c&oacute; t&aacute;c dụng rất tốt để bảo vệ da ngừa ung thư.</p>\r\n\r\n<p>Theo TS.GS về Da liễu tại trường ĐH Washington th&igrave;, cơ chế ngừa ung thư da từ c&agrave; ph&ecirc; l&agrave; nhờ chất caffein trong đồ uống n&agrave;y. Vị GS tr&ecirc;n c&ugrave;ng đồng nghiệp đ&atilde; ph&aacute;t hiện thấy trong ph&ograve;ng th&iacute; nghiệm rằng,chất caffeine trong c&agrave; ph&ecirc; c&oacute; t&aacute;c dụng ngăn ngừa sự ph&aacute; hủy c&aacute;c tế b&agrave;o da từ c&aacute;c tia cực t&iacute;m, tia n&agrave;y gọi l&agrave; tia UV. Đ&aacute;ng ch&uacute; &yacute; l&agrave; chất caffeine đ&atilde; rất xuất sắc khi ngừa một protein c&oacute; t&ecirc;n gọi ATR-Chk1 v&agrave; caffeine c&ograve;n c&oacute; t&aacute;c dụng ph&aacute; hủy c&aacute;c tế b&agrave;o vốn đ&atilde; bị nhiễm ung thư. Uống c&agrave; ph&ecirc; mỗi ng&agrave;y gi&uacute;p ngăn ngừa bệnh ung thư da.Uống c&agrave; ph&ecirc; mỗi ng&agrave;y gi&uacute;p ngăn ngừa bệnh ung thư da.</p>\r\n\r\n<p><img src="imgs/caphenguaungthu/img2.jpg" /></p>\r\n\r\n<p>Theo GS Nghi&ecirc;m, kh&ocirc;ng thể uống ồ ạt c&agrave; ph&ecirc; trong thời gian ngắn m&agrave; tăng khả năng ngừa ung thư được. C&aacute;ch uống tốt nhất l&agrave; uống với liều lượng vừa phải, mỗi ng&agrave;y uống một ly c&agrave; ph&ecirc;, điều n&agrave;y diễn ra trong nhiều năm. GS Nghi&ecirc;m cũng đưa ra &yacute; tưởng rằng, c&oacute; thể đưa lượng caffein trực tiếp v&agrave;o da để ngừa ung thư, th&iacute; nghiệm tr&ecirc;n chuột th&igrave; th&agrave;nh c&ocirc;ng tới 72%, c&ograve;n đưa &aacute;p dụng v&agrave;o người c&oacute; lẽ cần một thời gian.</p>\r\n\r\n<p><img src="imgs/caphenguaungthu/img3.jpg" /></p>\r\n\r\n<p>Cũng theo c&aacute;c nh&agrave; khoa học, chất caffeine ngo&agrave;i ngừa tế b&agrave;o ung thư th&igrave; n&oacute; c&ograve;n c&oacute; t&aacute;c dụng rất tốt để bảo vệ đ&ocirc;i mắt khi ra nắng. TS Robin Ashinoff, GS chuy&ecirc;n về Da liễu thuộc Trường ĐH New York đ&atilde; cho hay, nghi&ecirc;n cứu cần được thẩm định kỹ trước khi ứng dụng rộng r&atilde;i, nhưng c&oacute; bằng chứng r&otilde; r&agrave;ng trong ph&ograve;ng th&iacute; nghiệm rằng, chất caffeine trong c&agrave; ph&ecirc; c&oacute; thể x&oacute;a bỏ c&aacute;c tế b&agrave;o bị ph&aacute; hủy bởi c&aacute;c tia cực t&iacute;m v&agrave; c&aacute;c chất ph&oacute;ng xạ, đồng thời gi&uacute;p c&aacute;c tế b&agrave;o bị tổn thương nhanh ch&oacute;ng t&aacute;i cấu tr&uacute;c lại.</p>\r\n\r\n<p>Cũng qua nghi&ecirc;n cứu của c&aacute;c nh&agrave; khoa học cho thấy, caffeine trong c&agrave; ph&ecirc; c&ograve;n ngừa được những tật nhỏ chưa th&agrave;nh u c&oacute; thể g&acirc;y ung thư da. Tuy vậy, kh&ocirc;ng &iacute;t nh&agrave; khoa học c&ograve;n ho&agrave;i nghi về t&iacute;nh năng loại bỏ độc tố của caffeine, trong đ&oacute; c&oacute; TS Albert Lefkovits, người ph&aacute;t ng&ocirc;n của Tổ chức Ung thư da v&agrave; l&agrave; GS Da liễu thuộc trường ĐH Y khoa Mount Sinai, New York. Vị GS n&agrave;y cũng cho rằng, cần phải c&oacute; nhiều thời gian để khẳng định những t&aacute;c dụng của caffeine trong việc l&agrave;m giảm nguy cơ ung thư da. Cũng phải nghi&ecirc;n cứu ở một phạm vi rộng v&agrave; s&acirc;u hơn để thấy r&otilde; t&aacute;c dụng của ch&uacute;ng.</p>\r\n', 28, 'Nguyễn Thanh Tình - PHP39 (Sưu tầm)', NULL, 1, '2019-07-28 15:37:18'),
(26, 'Đọc bài này bạn sẽ biết mình cần chọn cà phê hạt Arabica hay cà phê hạt Robusta!', 4, 1, 'news-1564328322img1.jpg', 'Khi nói đến việc uống cà phê thông thường, bạn chú ý đến lượng cafein có trong cà phê hay thành phần các loại hạt cà phê nguyên chất? \r\n', '<p>Đối với những ai say m&ecirc; c&agrave; ph&ecirc; thường c&oacute; c&aacute;i nh&igrave;n s&acirc;u sắc hơn, ch&igrave;a kh&oacute;a chất lượng c&agrave; ph&ecirc; ẩn s&acirc;u hơn, họ thưởng thức hương vị của c&agrave; ph&ecirc; một c&aacute;ch tinh tế v&agrave; dễ d&agrave;ng nhận ra được sự kh&aacute;c biệt c&oacute; trong c&aacute;c loai hạt c&agrave; ph&ecirc;.</p>\r\n\r\n<p><img src="imgs/docbainay/img1.jpg" /></p>\r\n\r\n<p>Hai loại hạt c&agrave; ph&ecirc; Arabica v&agrave; Robusta đến từ những giống c&acirc;y kh&aacute;c nhau, v&agrave; cho ra hai sản phẩm cuối c&ugrave;ng kh&aacute;c nhau. Lượng cafeine c&oacute; trong mỗi loại cũng c&oacute; sự kh&aacute;c biệt lớn, v&igrave; c&ograve;n t&ugrave;y v&agrave;o độ đậm đặc v&agrave; m&ugrave;i vị của thức uống.</p>\r\n\r\n<p>Ch&uacute;ng ta sẽ đi s&acirc;u v&agrave; kh&aacute;m ph&aacute; sự kh&aacute;c nhau giữa ch&uacute;ng v&agrave; loại n&agrave;o th&igrave; ph&ugrave; hợp nhất cho từng loại c&agrave; ph&ecirc; m&agrave; bạn muốn thưởng thức.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&Agrave; PH&Ecirc; HẠT ARABICA<img src="imgs/docbainay/img2.jpg" /></p>\r\n\r\n<p>C&agrave; ph&ecirc; hạt nguy&ecirc;n chất Pongour Arabica</p>\r\n\r\n<p>Arabica thường được biết đến như một loại hạt &ldquo;chất lượng cao&rdquo; với vị ngọt v&agrave; &iacute;t đắng, điều c&oacute; thể kh&oacute; c&oacute; ở hạt Robusta.</p>\r\n\r\n<p>1. Lượng cafeine</p>\r\n\r\n<p>Mặc d&ugrave; n&oacute; v&ocirc; c&ugrave;ng phổ biến, nhưng Arabica nhanh ch&oacute;ng bị hạ gục khi so s&aacute;nh với Robusta ở kh&iacute;a cạnh nồng độ cafeine.</p>\r\n\r\n<p>Hạt Arabica thực ra c&oacute; nồng độ cafeine thấp hơn so với đối thủ. Bất ngờ, đ&uacute;ng kh&ocirc;ng?</p>\r\n\r\n<p>Sự kh&aacute;c nhau giữa Arabica v&agrave; Robusta đến từ lượng cafeine, l&agrave; hợp chất được hấp thụ đến từ vấn đề khai th&aacute;c.</p>\r\n\r\n<p>Vậy c&ograve;n điều g&igrave; kh&aacute;c nữa kh&ocirc;ng?</p>\r\n\r\n<p>2. Nguồn gốc v&agrave; khu vực trồng trọt</p>\r\n\r\n<p><img src="imgs/docbainay/img3.jpg" /></p>\r\n\r\n<p>Từ quan điểm lịch sử, Arabica được xem x&eacute;t l&agrave; gốc của c&agrave; ph&ecirc; v&igrave; ch&uacute;ng t&ocirc;i biết n&oacute;, bằng những hồ sơ cho thấy ch&uacute;ng được trồng kh&aacute; sớm, v&agrave;o khoảng thế kỷ 12 ở Ethiopia. Ng&agrave;y nay, doanh số b&aacute;n to&agrave;n cầu ước t&iacute;nh từ 65 &ndash; 70% trong tổng lượng cung cấp c&agrave; ph&ecirc; của thế giới. Trong khi ch&uacute;ng đ&atilde; được ti&ecirc;u thụ trong một khu vực (v&agrave; được đồn đại l&agrave; thậm ch&iacute; được chỉ định l&agrave; phong tr&agrave;o của cư d&acirc;n trong khu vực), quy m&ocirc; sản xuất lớn to&agrave;n cầu đ&atilde; bắt đầu ở Indonexia v&agrave;o thế kỷ 17.</p>\r\n\r\n<p>Điều đ&oacute; kh&ocirc;ng c&oacute; g&igrave; l&agrave; ngạc nhi&ecirc;n cả. Thậm ch&iacute; chuỗi cửa h&agrave;ng c&agrave; ph&ecirc; phổ biến c&oacute; mặt tr&ecirc;n bản đồ của nhiều h&ograve;n đảo kh&aacute;c nhau ở Indonexia cho thấy xuất xứ những hạt c&agrave; ph&ecirc; của họ.</p>\r\n\r\n<p>Bạn cũng c&oacute; khả năng lắng nghe con người xem c&agrave; ph&ecirc; như &ldquo;Java&rdquo;. Điều n&agrave;y thực ra đi ngược từ nhiều loại được ph&acirc;n bổ tại h&ograve;n đảo Java của Indonexia. T&ocirc;i đ&atilde; gh&eacute; thăm nơi đ&oacute; v&agrave;i năm trước v&agrave; c&agrave; ph&ecirc; th&igrave; thật sự rất ngon. T&ocirc;i thậm ch&iacute; c&ograve;n thử loại &ldquo;C&agrave; ph&ecirc; cat poop&rdquo; kh&ocirc;ng mấy nổi tiếng được xử l&yacute; trong ruột m&egrave;o Luwak trước khi đem đi rang!</p>\r\n\r\n<p>Ng&agrave;y nay, Arabica được trồng ở hầu khắp mọi nơi, gồm cả những v&ugrave;ng xa x&ocirc;i như Brazil v&agrave; Hawaii.</p>\r\n\r\n<p>Khi đề cập đến quan điểm trồng trọt, Arabica, th&igrave; tốt, kh&ocirc;ng như robust &ndash; mạnh. C&acirc;y ph&aacute;t triển đến độ cao 2.5 &ndash; 4.5m (khoảng 8.2 &ndash; 14.7 feet), khiến ch&uacute;ng nhỏ hơn so với đối thủ của m&igrave;nh. Một phần v&igrave; sắc th&aacute;i, Arabica sản xuất với năng suất thấp. Tuy nhi&ecirc;n, c&acirc;y Arabica đ&ograve;i hỏi lượng nước 1200 &ndash; 2200 mm, v&agrave;o m&ugrave;a mưa, th&igrave; dễ d&agrave;ng cung cấp hơn so với nhiều v&ugrave;ng tr&ecirc;n thế giới trong khi Robusta cần khoảng 2200 &ndash; 3000 mm mỗi năm. Những c&acirc;y ph&aacute;t triển cần thường xuy&ecirc;n cắt v&agrave; đảm bảo giới hạn quả, v&igrave; ch&uacute;ng mất nguồn năng lượng khi ch&uacute;ng d&agrave;nh để tạo ra loại hạt chất lượng cao.</p>\r\n\r\n<p>V&igrave; cần sự chuy&ecirc;n t&acirc;m v&agrave; năng suất thấp, c&agrave; ph&ecirc; Arabica mang đến sản phẩm v&agrave; gi&aacute; cả sau c&ugrave;ng đến tay người ti&ecirc;u d&ugrave;ng cao hơn.</p>\r\n\r\n<p>C&agrave; ph&ecirc; Single Origin v&agrave; C&agrave; ph&ecirc; Blend (phối trộn)</p>\r\n\r\n<p>V&igrave; vậy bạn c&oacute; n&ecirc;n lu&ocirc;n mua Arabica thay v&igrave; Robusta? Kh&ocirc;ng hẳn đ&acirc;u, ch&uacute;ng đều c&oacute; c&ugrave;ng một nơi sản xuất:</p>\r\n\r\n<p>Nếu bạn pha bằng Espresso, bạn sẽ thường thường thức loại c&agrave; ph&ecirc; Blend hỗn hợp bao gồm một &iacute;t hạt Robusta để tăng cường th&ecirc;m lượng cafeine v&agrave; tận hưởng tốt hơn với lớp bọt mịn nhiều hơn khi pha Arabica.</p>\r\n\r\n<p><img src="imgs/docbainay/img4.jpg" /></p>\r\n\r\n<p>C&agrave; ph&ecirc; Blend giữa Arabica v&agrave; Robusta</p>\r\n\r\n<p>C&Agrave; PH&Ecirc; HẠT ROBUSTA</p>\r\n\r\n<p><img src="imgs/docbainay/img5.jpg" /></p>\r\n\r\n<p>C&agrave; ph&ecirc; hạt nguy&ecirc;n chất Bandon Robusta</p>\r\n\r\n<p>Đầu ti&ecirc;n, h&atilde;y nhớ rằng kh&ocirc;ng phải tất cả hạt Robusta đều k&eacute;m hơn. Hạt Robusta chất lượng cao sẽ lu&ocirc;n mang đến hương vị tốt hơn so với Arabica chất lượng thấp. Việc chăm s&oacute;c v&agrave; rang cẩn thận thật sự đ&oacute;ng vai tr&ograve; quan trọng.</p>\r\n\r\n<p>Vậy th&igrave; ch&uacute;ng t&ocirc;i y&ecirc;u th&iacute;ch điều g&igrave; ở Robusta?</p>\r\n\r\n<p>1. Những lợi &iacute;ch sức khỏe</p>\r\n\r\n<p>Robusta c&oacute; lượng chất chống oxy h&oacute;a cao đặc trưng, c&aacute;i được biết l&agrave; hỗ trợ giải tỏa căng thẳng.</p>\r\n\r\n<p>2. Nguồn gốc v&agrave; khu vực trồng</p>\r\n\r\n<p><img src="imgs/docbainay/img6.jpg" /></p>\r\n\r\n<p>Robusta được trồng ch&iacute;nh ở khu vực b&aacute;n cầu Đ&ocirc;ng, đơn cử l&agrave; ở Indonexia, Việt Nam v&agrave; Ch&acirc;u Phi, nơi c&oacute; kh&iacute; hậu th&iacute;ch hợp. Những khu vực rừng rậm nhiệt đới th&igrave; ho&agrave;n hảo cho c&acirc;y trồng ph&aacute;t triển ở mức cao, c&aacute;i đ&ograve;i hỏi nhiệt độ cao hơn để sản xuất theo năng suất của họ.</p>\r\n\r\n<p>Robusta dễ d&agrave;ng hơn để canh t&aacute;c v&agrave; ph&aacute;t triển v&agrave; v&igrave; thế n&oacute; thường cho năng suất cao hơn hạt Arabica v&agrave; gi&aacute; cả thấp hơn.</p>\r\n\r\n<p>3. Cafeine</p>\r\n\r\n<p>Robusta cung cấp gấp đ&ocirc;i lượng cafeine so với đối thủ Arabica. Điều n&agrave;y kh&aacute; quan trọng, đặc biệt với những người uống c&agrave; ph&ecirc; đ&ograve;i hỏi lượng cafeine cao nhất, cũng như tinh chất espresso.</p>\r\n\r\n<p>Robusta đ&oacute;ng vai tr&ograve; quan trọng khi l&agrave;m espresso. Hầu hết c&aacute;c t&uacute;i hạt c&agrave; ph&ecirc; espresso thực ra l&agrave; 85 &ndash; 90% hạt Arabica v&agrave; khoảng 10 -15% hạt Robusta được xay cẩn thận.</p>\r\n\r\n<p>C&oacute; hai l&yacute; do l&agrave;:</p>\r\n\r\n<p>Đầu ti&ecirc;n l&agrave; hương vị đậm đ&agrave;. Robusta đặc biệt đắng hơn v&agrave; cafeine nặng, tạo cảm hứng cho một hương vị th&uacute; vị khi kết hợp với vị ngọt, nhẹ hơn đường của hạt Arabica.</p>\r\n\r\n<p>Thứ hai, espresso cũng được biết với đặc t&iacute;nh n&oacute;ng, tầng kem m&agrave;u mật ong nhẹ tr&ecirc;n đầu shot. Robusta được biết n&acirc;ng cao độ n&oacute;ng v&agrave; tạo lớp bọt b&eacute;o nhiều hơn khi pha espresso.</p>\r\n\r\n<p>C&ugrave;ng tổng hợp lại 5 &yacute; ch&iacute;nh ph&acirc;n biệt C&agrave; ph&ecirc; hạt Robusta v&agrave; Arabica</p>\r\n\r\n<p><img src="imgs/docbainay/img7.jpg" /></p>\r\n\r\n<p>1. Hương vị</p>\r\n\r\n<p>Arabica c&oacute; hương vị nhẹ nh&agrave;ng, ngọt nhẹ, chua thanh, c&oacute; thể sử dụng như một loại thức uống c&acirc;n bằng</p>\r\n\r\n<p>Robusta đậm đ&agrave; hơn, c&oacute; vị đắng nhẹ hoặc đắng đậm t&ugrave;y v&agrave;o c&aacute;ch rang pha. Robusta ph&ugrave; hợp với những ai th&iacute;ch uống đắng đậm.</p>\r\n\r\n<p>2. Mục đ&iacute;ch sử dụng</p>\r\n\r\n<p>Robusta thường được sử dụng trong việc chế biến c&agrave; ph&ecirc; g&oacute;i sẵn v&igrave; c&oacute; lượng cafein kh&aacute; cao. Hoặc được phối trộn chung một lượng nhỏ với Arabica khi pha Espresso.</p>\r\n\r\n<p>Arabica được sử dụng nhiều trong mọi trường hợp c&ograve;n lại</p>\r\n\r\n<p>3. Cafein</p>\r\n\r\n<p>Robusta l&agrave; v&ocirc; địch về cafein</p>\r\n\r\n<p>4. Xuất xứ</p>\r\n\r\n<p>Arabica được trồng ch&iacute;nh ở ph&iacute;a đ&ocirc;ng v&agrave; trung t&acirc;m Africa, Latin America, Inda v&agrave; Indo</p>\r\n\r\n<p>Robusta được trồng ở Afroca. Đ&ocirc;ng Nam Asia, India v&agrave; Indonexia</p>\r\n\r\n<p>5. Lượng đường</p>\r\n\r\n<p>Arabica c&oacute; lượng đường cao hơn Robusta</p>\r\n\r\n<p>Hai loại hạt c&agrave; ph&ecirc; ch&iacute;nh đến từ c&aacute;c giống c&acirc;y trồng kh&aacute;c nhau v&agrave; tạo ra hai sản phẩm cuối c&ugrave;ng kh&aacute;c biệt. Lượng caffein trong mỗi loại l&agrave; một yếu tố ph&acirc;n biệt rất lớn, cũng như độ mịn v&agrave; hương vị của thức uống.</p>\r\n\r\n<p>T&ocirc;i hi vọng b&agrave;i viết n&agrave;y đ&atilde; mang đến cho bạn sự hiểu biết tốt hơn về việc c&aacute;c chuy&ecirc;n gia c&agrave; ph&ecirc; n&oacute;i g&igrave; khi n&oacute;i về điểm mạnh của Arabica v&agrave; Robusta v&agrave; t&igrave;m kiếm hạt c&agrave; ph&ecirc; xay ho&agrave;n hảo cho vị espresso tuyệt vời.</p>\r\n', 25, 'Nguyễn Thanh Tình - PHP39 (Sưu tầm)', NULL, 1, '2019-07-28 15:38:43'),
(27, 'Honduras - Mảnh đất của cà phê hạt La Tortuga', 4, 1, 'news-1564328390img2.jpg', 'Honduras là một quốc gia tại Trung Mỹ, nơi đây nổi tiếng về một loại cà phê có hương vị thơm ngon đặc trưng, đó là cà phê La Tortuga.', '<p>&nbsp;</p>\r\n\r\n<p><img src="imgs/honduras/img1.jpg" /></p>\r\n\r\n<p>Honduras - Mảnh đất của c&agrave; ph&ecirc; hạt La Tortuga</p>\r\n\r\n<p>Người d&acirc;n Honduras lu&ocirc;n c&oacute; một niềm tự h&agrave;o với những hạt c&agrave; ph&ecirc; do họ l&agrave;m ra, với họ, chất lượng của những hạt c&agrave; ph&ecirc; lu&ocirc;n l&agrave; ưu ti&ecirc;n h&agrave;ng đầu. V&agrave;o năm 2011, Honduras đ&atilde; trở th&agrave;nh khu vực sản xuất c&agrave; ph&ecirc; lớn nhất khu vực Trung Mỹ, đến năm 2012, Honduras đ&atilde; vươn l&ecirc;n chiếm vị tr&iacute; thứ 7 tr&ecirc;n thế giới cũng như đ&aacute;nh bại Colombia v&agrave; Brazil để so&aacute;n ng&ocirc;i &aacute; qu&acirc;n thế giới trong lĩnh vực xuất khẩu c&agrave; ph&ecirc; cao cấp. Một v&agrave;i số liệu được thống k&ecirc; trong bảng sau:</p>\r\n\r\n<p><img src="imgs/honduras/img2.jpg" /></p>\r\n\r\n<p>H&igrave;nh ảnh h&agrave;ng ngh&igrave;n hạt c&agrave; ph&ecirc; tốt tươi mọc tr&ecirc;n sườn ph&iacute;a Đ&ocirc;ng nam Honduras</p>\r\n\r\n<p>Honduras l&agrave; mảnh đất nơi cho ra nhiều loại c&agrave; ph&ecirc; đa dạng m&agrave; lại si&ecirc;u sạch trong điều kiện cơ sở hạ tầng thiếu thốn v&agrave; c&aacute;c thiết bị sấy kh&ocirc; v&ocirc; c&ugrave;ng ngh&egrave;o n&agrave;n. Gần như hơn một nửa c&agrave; ph&ecirc; được sản xuất từ chủ yếu 3 v&ugrave;ng l&agrave;: Montecillos, Cop&aacute;n v&agrave; Agalta. C&aacute;c tiểu điền chủ yếu trồng c&agrave; ph&ecirc; Arabica, bao gồm Pacas v&agrave; Typica, đặc biệt, c&agrave; ph&ecirc; v&ugrave;ng n&agrave;y ho&agrave;n to&agrave;n l&agrave; c&agrave; ph&ecirc; hữu cơ v&agrave; trồng gần như to&agrave;n bộ dưới b&oacute;ng r&acirc;m. Để quảng b&aacute; những hạt c&agrave; ph&ecirc; địa phương, Hiệp Hội c&agrave; ph&ecirc; đ&atilde; đầu tư rất mạnh tay v&agrave;o gi&aacute;o dục v&agrave; đ&agrave;o tạo. Tại Honduras, ta c&oacute; thể bắt gặp những sắc th&aacute;i v&agrave; hương vị c&agrave; ph&ecirc; tương phản nhau như: vị dịu nhẹ, chua nhẹ, vị hạt phỉ v&agrave; vị của kẹo bơ cứng hay chua đậm kiểu c&agrave; ph&ecirc; Kenyan.</p>\r\n\r\n<p>Đ&ocirc;i n&eacute;t về c&agrave; ph&ecirc; La Tortuga</p>\r\n\r\n<p><img src="imgs/honduras/img3.jpg" /></p>\r\n\r\n<p>C&agrave; ph&ecirc; La Tortuga</p>\r\n\r\n<p>1. L&agrave; c&agrave; ph&ecirc; Single Origin</p>\r\n\r\n<p>Nổi bật với hương vị nổi bật đặc trưng của một khu vực, quy tr&igrave;nh chế biến hoặc chủng loại, những hạt c&agrave; ph&ecirc; được thu hoạch v&agrave; rang cẩn thận để tạo ra chất lượng cũng như sự độc đ&aacute;o về m&ugrave;i vị.</p>\r\n\r\n<p>2. C&oacute; hương vị độc đ&aacute;o</p>\r\n\r\n<p>Hạt c&agrave; ph&ecirc; La Tortuga c&oacute; h&igrave;nh thức rất bắt mắt v&agrave; để lại một dư vị v&ocirc; c&ugrave;ng mạnh mẽ trong miệng người thưởng thức, một hương vị v&ocirc; c&ugrave;ng dễ chịu. C&agrave; ph&ecirc; La Tortuga Honduras rất ngọt ng&agrave;o v&agrave; dịu nhẹ, c&oacute; được điều n&agrave;y l&agrave; nhờ mảnh đất phong ph&uacute; của khu rừng nhiệt đới được nu&ocirc;i dưỡng bởi những chất dinh dưỡng từ thực vật đang ph&acirc;n hủy tự nhi&ecirc;n mọc b&ecirc;n tr&ecirc;n v&agrave; dưới c&acirc;y c&agrave; ph&ecirc;. C&agrave; ph&ecirc; La Tortuga kh&ocirc;ng cần ph&acirc;n b&oacute;n nhưng vẫn c&oacute; thể ph&aacute;t triển tốt, v&agrave; nếu cần th&igrave; chỉ c&oacute; ph&acirc;n b&oacute;n tự nhi&ecirc;n được sử dụng. Ch&iacute;nh nhờ yếu tố đ&atilde; biến c&agrave; ph&ecirc; La Tortuga Honduras trở th&agrave;nh một lựa chọn tuyệt vời cho những người y&ecirc;u th&iacute;ch v&agrave; đang t&igrave;m kiếm c&agrave; ph&ecirc; hữu cơ.</p>\r\n\r\n<p>3. Phải mất nhiều thời gian đầu tư v&agrave; gieo trồng để c&oacute; được kết quả như mong đợi</p>\r\n\r\n<p>Một đồn điền trồng c&agrave; ph&ecirc; ở Honduras thường mất 4 năm để đạt được tr&igrave;nh độ sản xuất chuy&ecirc;n nghiệp v&agrave; tận 7 năm để thu được lợi nhuận. Điều n&agrave;y khiến cho nhiều doanh nghiệp cảm thấy lo ngại trước khi quyết định đầu tư v&agrave;o ng&agrave;nh n&agrave;y. Điển h&igrave;nh l&agrave; năm 1984, chỉ c&oacute; 10% lượng c&agrave; ph&ecirc; sản xuất được b&aacute;n ra b&ecirc;n ngo&agrave;i l&atilde;nh thổ Honduras, thực tế n&agrave;y l&agrave;m nổi l&ecirc;n vấn đề nổi cộm l&agrave; sự thiếu thốn về c&aacute;c phương thức vận chuyển v&agrave; xuất khẩu.</p>\r\n\r\n<p>4. Được v&iacute; như c&agrave; ph&ecirc; của những chuyến du lịch</p>\r\n\r\n<p>Kh&ocirc;ng c&oacute; nghi ngờ g&igrave; khi n&oacute;i Honduras l&agrave; một đất nước xinh đẹp. Thực tế n&agrave;y th&uacute;c đẩy nhiều trang trại c&agrave; ph&ecirc; đưa ra c&aacute;c tour du lịch ch&agrave;o đ&oacute;n c&aacute;c vị kh&aacute;ch tới thăm v&agrave; kh&aacute;m ph&aacute; quy tr&igrave;nh sản xuất c&agrave; ph&ecirc;. C&aacute;c loại c&agrave; ph&ecirc; chất lượng cao của Honduras được trồng dưới b&oacute;ng m&aacute;t của một số loại c&acirc;y như c&acirc;y chuối, cũng l&agrave; loại n&ocirc;ng sản thứ 2 được thu hoạch v&agrave; đem b&aacute;n. C&agrave; ph&ecirc; La Tortuga nơi đ&acirc;y nổi tiếng với m&ugrave;i vị thơm ngon nổi bật c&ugrave;ng vị chua nhẹ, đem đến một trải nghiệm tuyệt vời cho những t&iacute;n đồ c&agrave; ph&ecirc; đ&iacute;ch thực.</p>\r\n\r\n<p>5. La Tortuga th&uacute;c đẩy nền kinh tế khu vực</p>\r\n\r\n<p><img src="imgs/honduras/img4.jpg" /></p>\r\n\r\n<p>C&agrave; ph&ecirc; được trồng đầu ti&ecirc;n ở Olancho, sau đ&oacute; dần dần ph&aacute;t triển ra to&agrave;n bộ Honduras</p>\r\n\r\n<p>Một số v&ugrave;ng trọng điểm trồng c&agrave; ph&ecirc;</p>\r\n\r\n<p>1. Montecillos</p>\r\n\r\n<p>Khu vực n&agrave;y bao gồm v&ugrave;ng bộ phận La Paz, Comayagua, Intibuca, v&agrave; Santa Barbara. Nơi đ&acirc;y l&agrave; một trong số những trang trại cao nhất tại Honduras cho ra những hạt c&agrave; ph&ecirc; s&aacute;ng b&oacute;ng, săn chắc với hương tr&aacute;i c&acirc;y tươi m&aacute;t.</p>\r\n\r\n<p>2. Cop&aacute;n</p>\r\n\r\n<p><img src="imgs/honduras/img5.jpg" /></p>\r\n\r\n<p>C&agrave; ph&ecirc; Cop&aacute;n</p>\r\n\r\n<p>C&aacute;c v&ugrave;ng bộ phận của Copan, Ocotepeque, Cort&eacute;s, Santa Barbara, v&agrave; Lempira l&agrave; những nơi tạo ra hạt c&agrave; ph&ecirc; thơm m&ugrave;i cacao c&ugrave;ng độ ngọt đậm quyến rũ.</p>\r\n\r\n<p>3. Agalta</p>\r\n\r\n<p><img src="imgs/honduras/img6.jpg" /></p>\r\n\r\n<p>C&agrave; ph&ecirc; v&ugrave;ng Agalta</p>\r\n\r\n<p>Agalta trải đều khắp c&aacute;c v&ugrave;ng bộ phận của Olancho v&agrave; Yoro. C&agrave; ph&ecirc; nơi đ&acirc;y c&oacute; hương vị nhiệt đới v&agrave; rất ngọt với độ chua đậm c&ugrave;ng dấu ấn của hương socola ngọt ng&agrave;o.</p>\r\n\r\n<p>Mỗi t&aacute;ch c&agrave; ph&ecirc; La Tortuga của Honduras lại mở ra một trải nghiệm tuyệt vời tr&ecirc;n con đường kh&aacute;m ph&aacute; ngh&ecirc; thuật thưởng thức c&agrave; ph&ecirc;, với những ai y&ecirc;u th&iacute;ch c&agrave; ph&ecirc;, xin đừng bỏ qua cơ hội được một lần nếm thử thức uống tuyệt vời n&agrave;y.</p>\r\n', 30, 'Nguyễn Thanh Tình - PHP39 (Sưu tầm)', NULL, 1, '2019-07-28 15:39:51'),
(28, 'Hạt cà phê - tinh hoa của vùng đất Tolima Columbia', 4, 1, 'news-1564329585img3.jpg', '', '<p>N&oacute;i đến Columbia, những t&iacute;n đồ c&agrave; ph&ecirc; thường nhắc đến sự hấp dẫn v&agrave; đầy đặn của từng hạt c&agrave; ph&ecirc; trong cả hương vị lẫn h&igrave;nh thức. Nếu muốn một lần thưởng thức hết những tinh hoa đặc trưng của c&agrave; ph&ecirc;, h&atilde;y đến với Columbia, bởi bạn c&oacute; thể cảm nhận đầy đủ hương vị từ ngọt ng&agrave;o, quyến rũ cho đến vị socola, hoa l&aacute; rồi tới tr&aacute;i c&acirc;y h&ograve;a quyện c&ugrave;ng m&ugrave;i vị của v&ugrave;ng nhiệt đới quen thuộc. Mỗi nơi, mỗi v&ugrave;ng sẽ c&oacute; một hương vị ri&ecirc;ng cho những ai y&ecirc;u c&agrave; ph&ecirc; nếm trải.</p>\r\n\r\n<p>Nhờ lợi thế đến từ những ngọn n&uacute;i nơi đ&acirc;y, ch&uacute;ng ta c&oacute; thể cảm nhận được sự phong ph&uacute; m&agrave; mẹ thi&ecirc;n nhi&ecirc;n đ&atilde; ban tặng cho đất nước Columbia, ch&iacute;nh điều n&agrave;y cũng l&agrave; yếu tố quan trọng tạo n&ecirc;n hương vị kh&ocirc;ng đ&acirc;u c&oacute; được trong từng hạt c&agrave; ph&ecirc;. C&agrave; ph&ecirc; Columbia chủ yếu l&agrave; c&agrave; ph&ecirc; Typica v&agrave; Bourbon, tất cả đều được sơ chế theo phương ph&aacute;p ướt v&agrave; c&oacute; từ một tới hai m&ugrave;a thu hoạch một năm t&ugrave;y thuộc v&agrave;o mỗi khu vực.</p>\r\n\r\n<p><img src="imgs/hatcaphe/img1.jpg" /></p>\r\n\r\n<p>Lợi thế từ địa h&igrave;nh nhiều đồi n&uacute;i ch&iacute;nh l&agrave; yếu tố tạo n&ecirc;n hương vị c&agrave; ph&ecirc; thơm ngon tại đ&acirc;y</p>\r\n\r\n<p>Vụ m&ugrave;a của một v&agrave;i v&ugrave;ng sẽ diễn ra trong khoảng thời gian từ th&aacute;ng 9 đến th&aacute;ng 12, những vụ nhỏ kh&aacute;c v&agrave;o th&aacute;ng 4 hoặc 5, c&aacute;c hộ c&ograve;n lại thường diễn ra từ th&aacute;ng 3 đến th&aacute;ng 6 h&agrave;ng năm v&agrave; c&ograve;n c&oacute; một vụ kh&aacute;c v&agrave;o khoảng thời gian từ th&aacute;ng 10 đến th&aacute;ng 12. Hầu hết người d&acirc;n Columbia sinh sống dựa v&agrave;o nghề trồng c&agrave; ph&ecirc;, họ l&agrave;m việc cho c&aacute;c nh&oacute;m trang trại nhỏ v&agrave; khoảng 560.000 hộ trong số đ&oacute; l&agrave; c&aacute;c cơ sở sản xuất chỉ c&oacute; khoảng 2-41/2 mẫu đất để canh t&aacute;c (tương đương 1-2 ha đất). Trong v&agrave;i năm gần đ&acirc;y, ng&agrave;nh c&ocirc;ng nghiệp c&agrave; ph&ecirc; tại Columbia đ&atilde; c&oacute; nhiều dấu hiệu khởi sắc hơn khi c&aacute;c hộ n&ocirc;ng d&acirc;n nhỏ lẻ c&oacute; cơ hội ti&ecirc;u thụ c&agrave; ph&ecirc; m&igrave;nh trồng được với chi ph&iacute; cao hơn cho mỗi m&ugrave;a vụ. Tại Columbia, người d&acirc;n ng&agrave;y một y&ecirc;u th&iacute;ch uống c&agrave; ph&ecirc;, trung b&igrave;nh người d&acirc;n tại đ&acirc;y ti&ecirc;u thụ khoảng 20% tr&ecirc;n tổng số sản lượng c&agrave; ph&ecirc; sản xuất được.</p>\r\n\r\n<p><img src="imgs/hatcaphe/img2.jpg" /></p>\r\n\r\n<p>Th&ocirc;ng thường hạt c&agrave; ph&ecirc; được phơi tr&ecirc;n mặt b&ecirc; t&ocirc;ng, nhưng đối với những nơi c&oacute; địa h&igrave;nh qu&aacute; dốc, người d&acirc;n chuyển sang phơi hạt tr&ecirc;n m&aacute;i nh&agrave; để l&agrave;m kh&ocirc;.</p>\r\n\r\n<p>Một số v&ugrave;ng trọng điểm trồng c&agrave; ph&ecirc;</p>\r\n\r\n<p><img src="imgs/hatcaphe/img3.jpg" /></p>\r\n\r\n<p>C&agrave; ph&ecirc; Columbia được trồng gọn g&agrave;ng theo h&agrave;ng lối, c&aacute;c trang trại c&agrave; ph&ecirc; tại đ&acirc;y thường được quản l&yacute; rất quy củ v&agrave; hợp l&yacute;</p>\r\n\r\n<p>1. Cauca</p>\r\n\r\n<p><img src="imgs/hatcaphe/img4.jpg" /></p>\r\n\r\n<p>Khu vực trồng c&agrave; ph&ecirc; nổi tiếng v&ugrave;ng Cauca l&agrave; Inza v&agrave; Popayan, 2 v&ugrave;ng n&agrave;y chiếm tổng số 8% lượng c&agrave; ph&ecirc; được sản xuất với hương vị ngọt ng&agrave;o đặc trưng c&ugrave;ng vị cỏ c&acirc;y hoa l&aacute; v&agrave; c&aacute;c loại quả.</p>\r\n\r\n<p>2. Tolima</p>\r\n\r\n<p><img src="imgs/hatcaphe/img5.jpg" /></p>\r\n\r\n<p>C&agrave; ph&ecirc; v&ugrave;ng Tolima được hưởng trọn vẹn &aacute;nh s&aacute;ng mặt trời, v&igrave; vậy ch&uacute;ng c&oacute; vị ngọt ng&agrave;o, &ecirc;m &aacute;i, c&ugrave;ng sự đồng điệu của hương cỏ c&acirc;y hoa l&aacute;, c&agrave; ph&ecirc; Tolima chiếm khoảng 12% tổng sản lượng c&agrave; ph&ecirc; của Columbia.</p>\r\n\r\n<p>3. Nari&ntilde;o</p>\r\n\r\n<p><img src="imgs/hatcaphe/img6.jpg" /></p>\r\n\r\n<p>Nằm ở cực Nam xa x&ocirc;i, Narino nổi tiếng với hương vị c&agrave; ph&ecirc; dịu nhẹ, b&eacute;o ngậy h&ograve;a quyện tinh tế với hương tr&aacute;i c&acirc;y, mặc d&ugrave; vậy, Narino chỉ chiếm 3% tổng sản lượng c&agrave; ph&ecirc; tại Columbia.</p>\r\n\r\n<p>4. Santander</p>\r\n\r\n<p><img src="imgs/hatcaphe/img7.jpg" /></p>\r\n\r\n<p>Cũng l&agrave; một trong số những khu vực nằm tại v&ugrave;ng cực Nam, Santander v&agrave; Norte de Santander chiếm kho&agrave;ng 9% lượng c&agrave; ph&ecirc; tại Columbia, hầu hết c&agrave; ph&ecirc; v&ugrave;ng n&agrave;y được trồng dưới b&oacute;ng r&acirc;m v&agrave; dưới mực nước biển, kết quả l&agrave; m&ugrave;i vị của ch&uacute;ng rất dịu nhẹ v&agrave; đậm đ&agrave; với vị chua kh&ocirc;ng qu&aacute; gắt.</p>\r\n\r\n<p>5. Huila</p>\r\n\r\n<p><img src="imgs/hatcaphe/img8.jpg" /></p>\r\n\r\n<p>12% lượng c&agrave; ph&ecirc; của Columbia đến từ những ngọn n&uacute;i v&ugrave;ng Huila, khu vực c&oacute; nhiều mưa đ&aacute; nhất cả nước. C&agrave; ph&ecirc; v&ugrave;ng n&agrave;y thường c&oacute; vị hoa quả, kết hợp vị chua đậm h&ograve;a quyện c&ugrave;ng vị đậm đặc v&agrave; sự đa dạng trong hương vị.</p>\r\n\r\n<p>Tolima c&agrave; ph&ecirc;</p>\r\n\r\n<p><img src="imgs/hatcaphe/img9.jpg" /></p>\r\n\r\n<p>Tolima chạy dọc theo ph&iacute;a t&acirc;y của d&atilde;y n&uacute;i Andes, ph&iacute;a Bắc Cauca v&agrave; ph&iacute;a T&acirc;y Huila. Tại đ&acirc;y l&agrave; nơi cho ra những hạt c&agrave; ph&ecirc; c&oacute; hương vị tuyệt vời ho&agrave;n hảo, &aacute;nh nắng mặt trời, chất đất cũng như vị tr&iacute; địa l&yacute; ch&iacute;nh l&agrave; yếu tố quan trọng g&oacute;p phần tạo n&ecirc;n hương vị thơm ngon của c&agrave; ph&ecirc; v&ugrave;ng n&agrave;y, c&agrave; ph&ecirc; thường được thu hoạch bằng cup, sau đ&oacute; xay v&agrave; trộn đều giống như loại rượu vang Rhone hay một thứ mật ong địa phương thơm ngon.</p>\r\n\r\n<p>Nhưng để người thưởng thức c&oacute; cơ hội cầm tr&ecirc;n tay t&aacute;ch c&agrave; ph&ecirc; đậm đ&agrave; quyến rũ, mảnh đất Tolima cũng gặp phải kh&ocirc;ng &iacute;t thử th&aacute;ch. Điển h&igrave;nh l&agrave; t&igrave;nh h&igrave;nh tệ nạn x&atilde; hội phức tạp tại Tolima trở th&agrave;nh v&ugrave;ng đất nguy hiểm cho kh&aacute;ch du lịch, nạn cướp b&oacute;c tại đ&acirc;y cũng đ&aacute;ng lo ngại dẫn đến việc vận chuyển c&agrave; ph&ecirc; gặp rất nhiều kh&oacute; khăn v&igrave; nguy cơ bị cướp trong qu&aacute; tr&igrave;nh di chuyển. Nhưng d&ugrave; như v&acirc;y, c&agrave; ph&ecirc; Tolima vẫn lu&ocirc;n được đ&aacute;nh gi&aacute; l&agrave; một trong những loại c&agrave; ph&ecirc; hấp dẫn nhất Columbia.</p>\r\n\r\n<p><img src="imgs/hatcaphe/img10.jpg" /></p>\r\n\r\n<p>H&igrave;nh ảnh c&acirc;y c&agrave; ph&ecirc; tại Tolima</p>\r\n\r\n<p>Thật kh&ocirc;ng hề n&oacute;i qu&aacute; khi v&iacute; Tolima l&agrave; mảnh đất kỳ diệu ấp ủ những tinh hoa từ thi&ecirc;n nhi&ecirc;n v&agrave; ban tặng v&agrave;o từng hạt c&agrave; ph&ecirc;, nếu như c&oacute; dịp, bạn h&atilde;y một lần thưởng thức hương vị c&agrave; ph&ecirc; tại đ&acirc;y nh&eacute;.</p>\r\n', 0, 'Nguyễn Thanh Tình - PHP39 (Sưu tầm)', NULL, 1, '2019-07-28 15:53:44'),
(29, 'ARABICA TẠI LÀNG DÂN TỘC K’HO – ĐẠ SAR - LẠC DƯƠNG', 5, 1, 'news-1564329693img1.jpg', 'Trong quá trình tìm hiểu và khám phá những loại cà phê ngon của Viêt Nam. Purio được đối tác giới thiệu và tiếp cận tiếp cận với những nông hộ người K’Ho đang sống ở Đạ Sar, Lạc Dương, tỉnh Lâm Đồng.', '<p>Trong dự &aacute;n ph&aacute;t triển &ldquo;c&agrave; ph&ecirc; chất lượng cao&rdquo; tại Đ&agrave; Lạt, những n&ocirc;ng hộ n&agrave;y được chuyển giao quy tr&igrave;nh sản xuất c&agrave; ph&ecirc; từ giống, phương ph&aacute;p canh t&aacute;c n&ocirc;ng nghiệp, thu hoạch sản xuất, hỗ trợ m&aacute;y m&oacute;c, trang thiết bị v&agrave; bao ti&ecirc;u sản phẩm từ lời cam kết, đồng h&agrave;nh v&agrave; gắn kết bền vững c&ugrave;ng đối t&aacute;c của Purio Coffee.</p>\r\n\r\n<p>Điều g&igrave; khiến Arabica được trồng v&agrave; sản xuất bởi người n&ocirc;ng d&acirc;n K&rsquo;Ho trở n&ecirc;n kh&aacute;c biệt?</p>\r\n\r\n<p>Đầu ti&ecirc;n, phải n&oacute;i đến vị tr&iacute; địa l&yacute; v&agrave; thổ nhưỡng: với độ cao 1.515-1.560m so với mực nước biển, k&egrave;m theo cấu tr&uacute;c đất đỏ bazan đặc biệt trải d&agrave;i từ Suối V&agrave;ng đến Đạ Sar, Arabica ở đ&acirc;y c&oacute; cấu tr&uacute;c cứng hơn những loại c&agrave; ph&ecirc; được trồng ở c&ugrave;ng độ cao.</p>\r\n\r\n<p>Thứ hai, theo phương ph&aacute;p truyền thống &ndash; h&aacute;i tuốt c&agrave;nh chỉ mất khoảng một th&aacute;ng để thu hoạch xong nhưng l&agrave;m tổn thương đến c&aacute;c c&agrave;nh c&acirc;y, khiến cho năng suất m&ugrave;a vụ sau giảm. Người n&ocirc;ng d&acirc;n ở đ&acirc;y đ&atilde; sử dụng phương ph&aacute;p được chuyển giao&rdquo;thu hoạch chọn lọc&rdquo;- chỉ h&aacute;i những tr&aacute;i ch&iacute;n kh&ocirc;ng s&acirc;u bệnh, kh&ocirc;ng lẫn trong đất. Mặc d&ugrave; phương ph&aacute;p n&agrave;y đ&ograve;i hỏi c&oacute; nhiều sức lao động v&agrave; thời gia để ho&agrave;n th&agrave;nh (k&eacute;o d&agrave;i từ hai đến ba th&aacute;ng) nhưng lại mang đến một chất lượng sản phẩm tốt v&agrave; đảm bảo năng suất cho m&ugrave;a vụ sau.</p>\r\n\r\n<p><img src="imgs/arabica/img1.jpg" /></p>\r\n\r\n<p>Thứ ba, người n&ocirc;ng d&acirc;n được hướng dẫn chế biến hạt c&agrave; ph&ecirc; theo phương ph&aacute;p &ldquo; chế biến ướt&rdquo; &ndash; một trong những phương ph&aacute;p xử l&iacute; c&agrave; ph&ecirc; phổ biến v&agrave; mang lại hiệu quả cao nhất. Đối với phương ph&aacute;p n&agrave;y, những tr&aacute;i c&agrave; đỏ (tỉ lệ tr&aacute;i ch&iacute;n đỏ l&agrave; 100%) được rửa sạch v&agrave; vớt bỏ những tr&aacute;i nổi cũng như tạp chất trước khi đưa v&agrave;o m&aacute;y t&aacute;ch vỏ. Sau khi t&aacute;ch lớp vỏ tr&aacute;i ra khỏi hạt bằng m&aacute;y t&aacute;ch vỏ, hạt c&agrave; ph&ecirc; được chuyển v&agrave;o ng&acirc;m trong những bồn chứa đầy nước. t&ugrave;y thuộc v&agrave;o điều kiện của hạt cũng như kh&iacute; hậu v&agrave; nhiệt độ m&ocirc;i trường, hạt nh&acirc;n c&agrave; ph&ecirc; được ng&acirc;m (l&ecirc;n men) trong v&ograve;ng từ 12 đến 48 giờ để loại bỏ lớp nhầy d&iacute;nh tr&ecirc;n bề mặt. Khi qu&aacute; tr&igrave;nh l&ecirc;n men ho&agrave;n tất, những hạt c&agrave; ph&ecirc; sẽ được rửa sạch lại một lần nữa, sẵn sang cho qu&aacute; tr&igrave;nh phơi. Hạt c&agrave; ph&ecirc; được phơi c&aacute;ch mặt đất từ 70-100mm nhằm gi&uacute;p c&agrave; ph&ecirc; kh&ocirc;ng bị lẫn với đất đ&aacute; v&agrave; đẩy nhanh qua tr&igrave;nh tho&aacute;t nước để thu được c&agrave; ph&ecirc; th&oacute;c c&oacute; độ ẩm mong muốn (từ 9% đến 11%).</p>\r\n\r\n<p><img src="imgs/arabica/img2.jpg" /></p>\r\n\r\n<p>Nhờ những yếu tố tr&ecirc;n, Arabica được trồng v&agrave; sản xuất tại Đạ Sar bởi người n&ocirc;ng d&acirc;n K&rsquo;Ho cho ra một phong vị đậm đ&agrave; kh&aacute;c biệt c&aacute;c sản phẩm Arabica kh&aacute;c.</p>\r\n\r\n<p>Purio Coffee đ&atilde; l&agrave;m g&igrave; để sản phẩm Arabica K&rsquo;Ho trở n&ecirc;n ho&agrave;n thiện hơn?</p>\r\n\r\n<p>Sau khi thu mua nguồn nh&acirc;n chất lượng, ch&uacute;ng t&ocirc;i bảo quản kĩ trong m&ocirc;i trường c&oacute; nhiệt độ v&agrave; độ ẩm tốt nhất. Tiếp đ&oacute;, bằng c&ocirc;ng nghệ rang Hot-Air của m&aacute;y rang Loring c&ugrave;ng với chuy&ecirc;n vi&ecirc;n đầy kinh nghiệm, ch&uacute;ng t&ocirc;i đ&atilde; lựa chọn mức độ rang tối ưu nhất để cho ra sản phẩm Arabica K&rsquo;Ho với m&ugrave;i vị c&acirc;n bằng v&agrave; hương dark-chocolate nổi bật.</p>\r\n\r\n<p><img src="imgs/arabica/img3.jpg" /><img src="imgs/arabica/img4.jpg" /><img src="imgs/arabica/img5.jpg" /></p>\r\n\r\n<p>Với sự chăm ch&uacute;t tỉ mỉ sản phẩm từ khi trồng, thu hoạch, chế biến nh&acirc;n, bảo quản v&agrave; rang, Purio Coffee đ&atilde; bảo tồn hơn 94% hương vị đặc trưng của loại c&agrave; ph&ecirc; n&agrave;y. B&ecirc;n cạnh tạo ra gi&aacute; trị sản phẩm, Purio Coffee tự h&agrave;o v&igrave; đ&atilde; c&ugrave;ng đối t&aacute;c tạo n&ecirc;n mối li&ecirc;n kết bền vững để gi&uacute;p đảm bảo cho người n&ocirc;ng d&acirc;n c&oacute; thu nhập tốt hơn v&agrave; x&acirc;y dựng tham gia v&agrave;o chuỗi gi&aacute; trị ổn định, ph&aacute;t triển so với việc sản xuất truyền thống của những n&ocirc;ng hộ người K&rsquo;Ho. Nhờ c&aacute;c nền tảng đ&oacute;, ch&uacute;ng t&ocirc;i tin rằng gi&aacute; trị sản phẩm kh&ocirc;ng chỉ nằm ở ch&iacute;nh n&oacute; m&agrave; c&ograve;n hướng tới gi&aacute; trị của cộng đồng, đặc biệt đối với người n&ocirc;ng d&acirc;n K&rsquo;Ho.</p>\r\n', 30, 'Nguyễn Thanh Tình - PHP39 (Sưu tầm)', NULL, 1, '2019-07-28 16:01:33'),
(30, 'Đặc sản cà phê Khe Sanh Quảng Trị', 1, 1, 'news-1564329936img2.jpg', 'Cà phê Khe Sanh được người Pháp khai sanh và trồng những cây cà phê đầu tiên từ những năm đầu của thế kỷ 20; tuy nhiên, phải đến đầu thập niên 90 của thế kỷ 20 thì cây cà phê Khe Sanh mới sống lại và được mở rộng diện tích trồng, với giống mới, tạo nên diện mạo một vùng nguyên liệu cà phê hoàn toàn mới và ngày càng được biết đến', '<p>C&agrave; ph&ecirc; Khe Sanh được người Ph&aacute;p khai sanh v&agrave; trồng những c&acirc;y c&agrave; ph&ecirc; đầu ti&ecirc;n từ những năm đầu của thế kỷ 20; tuy nhi&ecirc;n, phải đến đầu thập ni&ecirc;n 90 của thế kỷ 20 th&igrave; c&acirc;y c&agrave; ph&ecirc; Khe Sanh mới sống lại v&agrave; được mở rộng diện t&iacute;ch trồng, với giống mới, tạo n&ecirc;n diện mạo một v&ugrave;ng nguy&ecirc;n liệu c&agrave; ph&ecirc; ho&agrave;n to&agrave;n mới v&agrave; ng&agrave;y c&agrave;ng được biết đến.</p>\r\n\r\n<p><img src="imgs/dacsancaphe/img1.jpg" /></p>\r\n\r\n<p>Bản đồ l&agrave;ng Khe Sanh đầu thế kỷ 19</p>\r\n\r\n<p><img src="imgs/dacsancaphe/img2.jpg" /><br />\r\n<img src="imgs/dacsancaphe/img3.jpg" /></p>\r\n\r\n<p>Khe Sanh c&aacute;ch ph&iacute;a T&acirc;y th&agrave;nh phố Đ&ocirc;ng H&agrave;, Quảng Trị khoảng 63km v&agrave; c&aacute;ch ph&iacute;a Đ&ocirc;ng cửa khẩu Lao Bảo 20km. Khe Sanh c&oacute; độ cao trung b&igrave;nh 400m so với mực nước biển, kh&iacute; hậu nhiệt đới - gi&oacute; m&ugrave;a nhưng nhiệt độ trung b&igrave;nh chỉ khoảng 23-240C. Mưa được ph&acirc;n bố đều cho cả năm, tổng lượng mưa tới gần 2.300mm với 150-160 ng&agrave;y mưa/năm. M&ugrave;a mưa k&eacute;o d&agrave;i từ th&aacute;ng 8 đến th&aacute;ng 1 năm sau nhưng m&ugrave;a h&egrave; của đồng bằng th&igrave; Khe Sanh vẫn mưa do ảnh hưởng của kh&iacute; hậu L&agrave;o. Từ th&aacute;ng 2 đến th&aacute;ng 4 thường c&oacute; sương m&ugrave; d&agrave;y đặc v&agrave;o buổi s&aacute;ng. Tiết trời Khe Sanh đẹp nhất l&agrave; v&agrave;o th&aacute;ng 8 v&agrave; th&aacute;ng 2.</p>\r\n\r\n<p>Người khai sinh ra c&agrave; ph&ecirc; Khe Sanh</p>\r\n\r\n<p>Eug&egrave;ne Poilane đến miền Nam Việt Nam năm 1909 với danh nghĩa l&agrave; c&ocirc;ng nh&acirc;n ph&aacute;o binh l&agrave;m việc cho xưởng c&ocirc;ng binh hải qu&acirc;n. Sau đ&oacute; nh&agrave; tự nhi&ecirc;n học Auguste Chevalier đ&atilde; chỉ định Poilane l&agrave;m thăm d&ograve; vi&ecirc;n cho viện sinh vật học v&agrave; Poilane trở th&agrave;nh đại diện cho dịch vụ quản l&yacute; rừng của Đ&ocirc;ng Dương năm 1922.</p>\r\n\r\n<p>Năm 1918, lần đầu Poilane đi xuy&ecirc;n qua khu vực sau n&agrave;y l&agrave; l&agrave;ng Khe Sanh. Do bị hấp dẫn bởi c&acirc;y cối tươi tốt ở đ&acirc;y v&agrave; nghĩ rằng đất đỏ th&igrave; tốt như bất cứ thứ g&igrave; ở v&ugrave;ng Tuscany (&Yacute;), năm 1926 Poilane quay trở lại Khe Sanh v&agrave; nhập những c&acirc;y c&agrave; ph&ecirc; Chiari để trồng v&agrave; lập n&ecirc;n đồn điền c&agrave; ph&ecirc; đầu ti&ecirc;n ở Khe Sanh.</p>\r\n\r\n<p><img src="imgs/dacsancaphe/img4.jpg" /></p>\r\n\r\n<p>Eug&egrave;ne Poilane (1888-1964)</p>\r\n\r\n<p>C&agrave; ph&ecirc; Khe Sanh ng&agrave;y nay</p>\r\n\r\n<p>Diện t&iacute;ch trồng c&agrave; ph&ecirc; ở Khe Sanh khoảng 4.600ha tập trung ở 3 x&atilde;: Hướng Ph&ugrave;ng , Hướng Linh v&agrave; T&acirc;n Li&ecirc;n; rải r&aacute;c ở c&aacute;c x&atilde; Hướng T&acirc;n, Hướng Sơn, Ph&ugrave;ng L&acirc;m v&agrave; x&atilde; H&uacute;c. X&atilde; Hướng Ph&ugrave;ng đ&oacute;ng vai tr&ograve; quan trọng nhất do (1) c&oacute; diện t&iacute;ch trồng lớn nhất chiếm đến 80% diện t&iacute;ch trồng c&agrave; ph&ecirc; của huyện Hướng Ho&aacute;, (2) thời tiết thuận lợi nhất do mưa &iacute;t nhất trong c&aacute;c x&atilde; v&agrave; (3) độ cao ph&ugrave; hợp, cao 670m so với mực nước biển.</p>\r\n\r\n<p><img src="imgs/dacsancaphe/img5.jpg" /></p>\r\n\r\n<p>V&ugrave;ng c&agrave; ph&ecirc; nguy&ecirc;n liệu Khe Sanh (Ảnh: Quang Hải)</p>\r\n\r\n<p>Giống c&agrave; ph&ecirc; được trồng chủ yếu ở Khe Sanh l&agrave; Catimor tr&aacute;i đỏ từ năm 1993, Catimor tr&aacute;i v&agrave;ng rất &iacute;t. Khe Sanh đ&atilde; từng thử nghiệm trồng Robusta từ năm 1968 nhưng kh&ocirc;ng th&agrave;nh c&ocirc;ng do c&acirc;y Robusta thụ phấn ch&eacute;o v&agrave; kh&ocirc;ng chịu nổi kh&ocirc; hạn, đỉnh điểm l&agrave; m&ugrave;a kh&ocirc; năm 2015 (12/2014 đến 5/2015) kh&ocirc; hạn đ&atilde; giết sạch c&acirc;y Robusta ở Khe Sanh. Năm 2013, Viện nghi&ecirc;n cứu T&acirc;y Nguy&ecirc;n lai tạo th&agrave;nh c&ocirc;ng giống TN1, lai giữa Robusta v&agrave; Typica, hiện được trồng thử nghiệm ở n&ocirc;ng trường T&acirc;n L&acirc;m.</p>\r\n\r\n<p>C&agrave; ph&ecirc; Khe Sanh trổ b&ocirc;ng v&agrave;o th&aacute;ng 3, chia l&agrave;m 3 đợt. Khe Sanh thu hoạch c&agrave; ph&ecirc; k&eacute;o d&agrave;i từ cuối th&aacute;ng 10 đến hết th&aacute;ng 1DL năm sau. Sản lượng trung b&igrave;nh của c&agrave; ph&ecirc; Khe Sanh l&agrave; 18 tấn c&agrave; ph&ecirc; tươi/ha, tương ứng 6 tấn c&agrave; ph&ecirc; th&oacute;c/ha hay 4,8 tấn c&agrave; ph&ecirc; nh&acirc;n/ha. C&agrave; ph&ecirc; nhan của Khe Sanh chủ yếu được chế biến ướt, rồi sấy kh&ocirc;, do thu hoạch đ&uacute;ng m&ugrave;a mưa.</p>\r\n\r\n<p><img src="imgs/dacsancaphe/img6.jpg" /><img src="imgs/dacsancaphe/img7.jpg" /></p>\r\n\r\n<p>Thu hoạch c&agrave; ph&ecirc; (ảnh: Quang Hải)</p>\r\n\r\n<p>M&ocirc; h&igrave;nh c&acirc;u lạc bộ n&ocirc;ng hộ</p>\r\n\r\n<p>Hiệp hội c&agrave; ph&ecirc; Khe Sanh c&oacute; 34 th&agrave;nh vi&ecirc;n, gồm x&iacute; nghiệp chế biến v&agrave; n&ocirc;ng hộ kết hợp cơ sở chế biến. Khe Sanh c&oacute; 3 nh&agrave; m&aacute;y xuất khẩu c&agrave; ph&ecirc; nh&acirc;n: Đại Lộc, Minh Tiến v&agrave; Thương Ph&uacute;, 6 nh&agrave; m&aacute;y doanh nghiệp: Acep, Th&agrave;nh Danh, Trần Thị Hương Trung, B&ugrave;i Thị Hương Khương, S&acirc;n Quyết v&agrave; Vương Th&aacute;i, v&agrave; c&oacute; th&ecirc;m khoảng 6 n&ocirc;ng hộ tham gia chế biến c&agrave; ph&ecirc; nh&acirc;n. Đại Lộc 1 năm chế biến khoảng 3.000 tấn c&agrave; ph&ecirc; nh&acirc;n. Minh Tiến 1 năm xuất khoảng 800 container c&agrave; ph&ecirc; nh&acirc;n.</p>\r\n\r\n<p>Ngo&agrave;i ra, Khe Sanh c&oacute; một c&acirc;u lạc bộ n&ocirc;ng hộ điển h&igrave;nh, từ 7-10 n&ocirc;ng hộ hợp lại c&ugrave;ng trao đổi phương thức canh t&aacute;c, thu hoạch v&agrave; chế biến theo y&ecirc;u cầu của doanh nghiệp thu mua chế biến, theo hướng c&agrave; ph&ecirc; sạch, kh&ocirc;ng d&ugrave;ng thuốc diệt cỏ trong rẫy c&agrave; ph&ecirc;, hạn chế d&ugrave;ng ph&acirc;n b&oacute;n ho&aacute; học (thay thế dần bằng ph&acirc;n vi sinh), v.v. Từ đ&oacute;, doanh nghiệp cam kết thu mua c&agrave; ph&ecirc; tươi với gi&aacute; cao hơn thị trường.</p>\r\n\r\n<p>Quy m&ocirc; n&ocirc;ng hộ lớn cũng chỉ khoảng 4-5ha/n&ocirc;ng hộ, n&ecirc;n c&aacute;c doanh nghiệp v&agrave; cơ sở chế biến phải thu mua từ rất nhiều n&ocirc;ng hộ. Do đ&oacute;, chất lượng c&agrave; ph&ecirc; tươi kh&ocirc;ng đồng đều, kh&oacute; kiểm so&aacute;t chất lượng tr&aacute;i tươi về mặt phẩm v&agrave; chất, tỉ lệ tr&aacute;i xanh dạt bỏ ra cao.</p>\r\n\r\n<p>C&aacute;c doanh nghiệp v&agrave; cơ sở chế biến c&agrave; ph&ecirc; nh&acirc;n ở Khe Sanh tuy &iacute;t nhưng kh&aacute; năng động v&agrave; c&oacute; mong muốn mở rộng thị trường, cải tiến chất lượng chế biến c&agrave; ph&ecirc; nh&acirc;n. Bản th&acirc;n c&agrave; ph&ecirc; Khe Sanh cũng c&oacute; đặc trưng: hương của dứa v&agrave; vị ch&aacute;t mặn. Do vậy, hứa hẹn một tương lai tốt đẹp cho c&agrave; ph&ecirc; Khe Sanh.</p>\r\n', 25, 'Nguyễn Thanh Tình - PHP39 (Sưu tầm)', NULL, 1, '2019-07-28 16:02:27');
INSERT INTO `news` (`id`, `title`, `category_id`, `admin_id`, `avatar`, `summary`, `content`, `comment_total`, `author`, `view`, `status`, `created_at`) VALUES
(31, '3 vùng trồng cà phê Arabica ngon nhất Việt Nam', 5, 1, 'news-1564329923img3.jpg', 'Ở Việt Nam, khá nhiều nơi có khí hậu và thổ nhưỡng thích hợp với việc trồng cây cà phê. Nhưng đâu là vùng trồng trọt cho ra những trái cà phê Arabica ngon nhất trên thế giới, hẳn không nhiều người biết.', '<p>Theo ch&acirc;n c&aacute;c gi&aacute;o sĩ người Ph&aacute;p, c&acirc;y c&agrave; ph&ecirc; đ&atilde; c&oacute; mặt ở nước ta từ thế kỷ 19 v&agrave; ph&aacute;t triển mạnh mẽ cho tới ng&agrave;y h&ocirc;m nay. Hiện Việt Nam l&agrave; nước c&oacute; sản lượng c&agrave; ph&ecirc; xuất khẩu lớn nhất thế giới, với khoảng 900.000 &ndash;1.200.000 tấn/năm.&nbsp;<br />\r\nNhưng trong 500.000 ha trồng c&agrave; ph&ecirc; của cả nước, chỉ c&oacute; khoảng 35.000 ha c&agrave; ph&ecirc; Arabica, tập trung chủ yếu ở L&acirc;m Đồng, Thừa Thi&ecirc;n Huế, Quảng Trị, Nghệ An, H&ograve;a B&igrave;nh, Sơn La, Điện Bi&ecirc;n... C&agrave; ph&ecirc; Arabica vốn l&agrave; loại c&oacute; gi&aacute; trị kinh tế nhất trong c&aacute;c lo&agrave;i c&agrave; ph&ecirc;.<br />\r\nTr&ecirc;n thị trường c&agrave; ph&ecirc; Arabica lu&ocirc;n được đ&aacute;nh gi&aacute; cao nhờ c&oacute; hương vị thơm ngon. Gi&aacute; c&agrave; ph&ecirc; Arabica cũng thường cao gấp đ&ocirc;i so với gi&aacute; c&agrave; ph&ecirc; Robusta. C&agrave; ph&ecirc; Arabica c&oacute; nguồn gốc từ v&ugrave;ng cao nguy&ecirc;n nhiệt đới Ethiopia (Đ&ocirc;ng Phi). Brasil v&agrave; Colombia l&agrave; hai nước xuất khẩu ch&iacute;nh loại c&agrave; ph&ecirc; n&agrave;y, với chất lượng được đ&aacute;nh gi&aacute; cao nhất.</p>\r\n\r\n<p><img src="imgs/news3/img1.jpg" /></p>\r\n\r\n<p>C&agrave; ph&ecirc; Arabica được đ&aacute;nh gi&aacute; l&agrave; ngon v&agrave; thường c&oacute; gi&aacute; cao gấp đ&ocirc;i c&agrave; ph&ecirc; Robusta v&agrave; được trồng ở v&ugrave;ng đất cao từ 1000-1600m.</p>\r\n\r\n<p>C&acirc;y c&agrave; ph&ecirc; Arabica thường được trồng ở độ cao từ 1.000 - 1.600 m. C&acirc;y c&oacute; t&aacute;n lớn, m&agrave;u xanh đậm, l&aacute; h&igrave;nh oval. Hạt Arabica hơi d&agrave;i, to hơn hạt Robusta. D&ugrave; c&oacute; lượng cafeine chỉ bằng 1 nửa Robusta, nhưng c&agrave; ph&ecirc; Arabica c&oacute; hương thơm nhẹ nh&agrave;ng, vị chua thanh n&ecirc;n rất được ưa chuộng.</p>\r\n\r\n<p>Đ&agrave; Lạt &ldquo;thi&ecirc;n đường&rdquo; của c&agrave; ph&ecirc; Arabica</p>\r\n\r\n<p>Khi nhắc đến những v&ugrave;ng đất trồng c&agrave; ph&ecirc; ở nước ta, người ta thường nghĩ ngay đến T&acirc;y Nguy&ecirc;n, hay c&ograve;n gọi l&agrave; Cao nguy&ecirc;n trung phần, nơi may mắn được tạo h&oacute;a ban cho diện t&iacute;ch đất đỏ bazan tr&ugrave; ph&uacute;.</p>\r\n\r\n<p><img src="imgs/news3/img2.jpg" /></p>\r\n\r\n<p>Đ&agrave; Lạt &ldquo;thi&ecirc;n đường&rdquo; của c&agrave; ph&ecirc; Arabica</p>\r\n\r\n<p>C&agrave; ph&ecirc; Arabica được trồng ở c&aacute;c huyện như: Đơn Dương, L&acirc;m H&agrave;, Di Linh, Đức Trọng v&agrave; một số v&ugrave;ng ngoại th&agrave;nh của th&agrave;nh phố Đ&agrave; Lạt được đ&aacute;nh gi&aacute; l&agrave; c&oacute; gi&aacute; trị cao nhất Việt Nam. Thậm ch&iacute;, hương vị s&aacute;nh ngang với c&agrave; ph&ecirc; ngon nhất thế giới l&agrave; Arabica Bourbon (Moka).&nbsp;<br />\r\nV&ugrave;ng đất n&agrave;y được coi l&agrave; &ldquo;thi&ecirc;n đường&rdquo; c&agrave; ph&ecirc; Arabica nhờ những &ldquo;chỉ số v&agrave;ng&rdquo; như : độ cao 1.500 m so với mặt nước biển, kh&iacute; hậu &ocirc;n đới m&aacute;t quanh năm, nhiệt độ cực đại trong năm kh&ocirc;ng vượt qu&aacute; 33 độ, nhiệt độ cực tiểu 5 độ.</p>\r\n\r\n<p>Điện Bi&ecirc;n, Sơn La c&oacute; lịch sử trồng c&agrave; ph&ecirc; Arabica cả trăm năm</p>\r\n\r\n<p>Ở ph&iacute;a Bắc, kh&iacute; hậu lạnh do chịu ảnh hưởng của gi&oacute; m&ugrave;a đ&ocirc;ng bắc, c&ugrave;ng lượng mưa lớn, m&ugrave;a kh&ocirc; kh&ocirc;ng r&otilde; rệt, cũng l&agrave; những lợi thế gi&uacute;p cho c&agrave; ph&ecirc; Arabica sinh trưởng v&agrave; ph&aacute;t triển. Nơi đ&acirc;y cũng đ&atilde; h&igrave;nh th&agrave;nh n&ecirc;n những v&ugrave;ng c&agrave; ph&ecirc; c&oacute; hương vị rất tuyệt như ở huyện Y&ecirc;n B&igrave;nh, thuộc tỉnh Y&ecirc;n B&aacute;i.<br />\r\nV&ugrave;ng T&acirc;y Bắc, c&agrave; ph&ecirc; được trồng ở c&aacute;c tỉnh như Sơn La, Điện Bi&ecirc;n cũng được kh&aacute;ch h&agrave;ng quốc tế đ&aacute;nh gi&aacute; rất cao nhờ c&oacute; thổ nhưỡng tương tự như v&ugrave;ng Sao Paulo của Brasil, c&oacute; kh&aacute;c chăng chỉ l&agrave; 2 v&ugrave;ng c&agrave; ph&ecirc; nằm n&agrave;y ở ph&iacute;a Bắc v&agrave; ph&iacute;a Nam b&aacute;n cầu.</p>\r\n\r\n<p><img src="imgs/news3/img3.jpg" /></p>\r\n\r\n<p>Ngo&agrave;i T&acirc;y Nguy&ecirc;n, ở Việt Nam c&ograve;n c&oacute; v&ugrave;ng c&agrave; ph&ecirc; Arabica T&acirc;y Bắc đ&atilde; c&oacute; lịch sử cả trăm năm.</p>\r\n\r\n<p>Đặc biệt c&agrave; ph&ecirc; Chiềng Ban, Sinh Ban( Sơn La), tuy kh&ocirc;ng được trồng ở v&ugrave;ng đất đỏ bazan v&agrave; kh&ocirc;ng nằm ở độ cao l&yacute; tưởng như c&aacute;c tỉnh T&acirc;y Nguy&ecirc;n, song Sơn La c&oacute; những loại đất trong nh&oacute;m đất đỏ v&agrave;ng th&iacute;ch hợp với c&acirc;y c&agrave; ph&ecirc; như Fk, Fv, Fs&hellip; Lại nằm ở vĩ độ kh&aacute; cao về ph&iacute;a Bắc (20039&rsquo; &ndash; 22002&rsquo; vĩ độ Bắc) n&ecirc;n kh&ocirc;ng phải tưới nước, nhưng c&acirc;y c&agrave; ph&ecirc; Arabica vẫn c&oacute; sức sống rất m&atilde;nh liệt. Nhiều c&acirc;y v&agrave;i chục năm tuổi, th&acirc;n to, t&aacute;n rộng m&agrave; hạt c&agrave; ph&ecirc; c&oacute; hương vị kh&ocirc;ng hề thua k&eacute;m so với giống c&acirc;y m&agrave; người Ph&aacute;p đ&atilde; trồng ở L&acirc;m Đồng từ những năm 30 của thế kỷ trước.</p>\r\n\r\n<p>Quảng Trị, Nghệ An c&agrave; ph&ecirc; Arabica với hương thơm s&acirc;u lắng</p>\r\n\r\n<p>Khu vực Trung bộ nước ta cũng c&oacute; những v&ugrave;ng đất như: Khe Sanh (Quảng Trị), Phủ Quỳ (Nghệ An) rất th&iacute;ch hợp với loại c&agrave; ph&ecirc; Arabica, đặc biệt l&agrave; giống Catimor (loại được lai giữa chủng Caturra với Hybrid de Timor). C&ugrave;ng thuộc họ Arabica, tuy kh&ocirc;ng c&oacute; vị ngọt đậm như Bourbon nhưng Catimor lại c&oacute; hương thơm s&acirc;u lắng v&agrave; vị ch&aacute;t, mặn.<br />\r\nNg&agrave;y nay, ngo&agrave;i những giống Arabica truyền thống, được trồng từ trước, mới đ&acirc;y, Viện Khoa học Kinh tế N&ocirc;ng L&acirc;m Nghiệp T&acirc;y Nguy&ecirc;n đ&atilde; lai tạo ra nhiều giống c&agrave; ph&ecirc; Arabica c&oacute; năng suất v&agrave; chất lượng cao.<br />\r\nTrong đ&oacute;, hai giống lai TN1, TN2 c&oacute; năng suất cao v&agrave; chất lượng tốt đ&atilde; được Bộ N&ocirc;ng nghiệp v&agrave; Ph&aacute;t triển N&ocirc;ng th&ocirc;n cho ph&eacute;p khu vực ho&aacute; canh t&aacute;c. C&aacute;c giống c&agrave; ph&ecirc; Arabica lai mới n&agrave;y c&oacute; đặc t&iacute;nh c&acirc;y sinh trưởng khỏe, ph&acirc;n c&agrave;nh nhiều. Năng suất đạt từ 4 - 5 tấn c&agrave; ph&ecirc; nh&acirc;n/ha, k&iacute;ch cỡ hạt lớn, trọng lượng 100 nh&acirc;n tr&ecirc;n 15 - 17gram, kh&aacute;ng cao đối với bệnh gỉ sắt...</p>\r\n\r\n<p><img src="imgs/news3/img4.jpg" /></p>\r\n\r\n<p>C&agrave; ph&ecirc; Arabica của Việt Nam rất được c&aacute;c thương hiệu c&agrave; ph&ecirc; nổi tiếng nhất tr&ecirc;n thế giới ưa chuộng.</p>\r\n\r\n<p>Như vậy, c&ugrave;ng l&agrave; c&acirc;y c&agrave; ph&ecirc; Arabica, nhưng ở mỗi v&ugrave;ng trồng đặc trưng ở nước ta lại tạo ra những hương vị c&agrave; ph&ecirc; rất ri&ecirc;ng để chinh phục những người s&agrave;nh về ẩm thực nhất tr&ecirc;n thế giới. Ch&iacute;nh v&igrave; l&yacute; do n&agrave;y, c&agrave; ph&ecirc; Arabica của Việt Nam ng&agrave;y c&agrave;ng được c&aacute;c thương hiệu c&agrave; ph&ecirc; nổi tiếng nhất tr&ecirc;n thế giới lựa chọn, trong đ&oacute; c&oacute; thương hiệu Purio Coffee.</p>\r\n', 25, 'Nguyễn Thanh Tình - PHP39 (Sưu tầm)', NULL, 1, '2019-07-28 16:04:30'),
(33, 'Giới thiệu Về Victoria Coffee', 7, 1, '', '', '<p>C&oacute; người n&oacute;i rằng, H&agrave; Nội giống như một c&ocirc; b&eacute; tuổi mới lớn dung mạo xinh đẹp nhưng l&uacute;c n&agrave;o cũng thật kh&oacute; chiều. Tiết trời ẩm ẩm ương ương. Mưa rồi lại nắng. Nắng rồi lại mưa.L&uacute;c n&agrave;o cũng x&ocirc; bồ v&agrave; hối hả. Tiếng c&ograve;i xe v&agrave; d&ograve;ng người tưởng như kh&ocirc;ng bao giờ dứt.Dẫu biết rằng cuộc sống l&agrave; lu&ocirc;n lu&ocirc;n vận động v&agrave; kh&ocirc;ng ngừng di chuyển, nhưng bất gi&aacute;c c&oacute; một khoảnh khắc n&agrave;o đ&oacute; trong ng&agrave;y, ta lại muốn t&igrave;m về một sự b&igrave;nh y&ecirc;n rất đỗi giản dị, đ&atilde; bị đ&aacute;nh mất hoặc qu&ecirc;n l&atilde;ng từ l&acirc;u. Đ&oacute; c&oacute; thể l&agrave; một buổi s&aacute;ng y&ecirc;n ả b&ecirc;n t&aacute;ch c&agrave; ph&ecirc;, nhẹ l&ograve;ng thưởng thức một bữa điểm t&acirc;m tuyệt vời. Hay đ&oacute; c&oacute; thể l&agrave; buổi chiều ch&igrave;m đắm trong kh&ocirc;ng gian tĩnh lặng của khoảng s&acirc;n vườn đầy nắng,thả hồn m&igrave;nh lắng đọng sau một ng&agrave;y l&agrave;m việc mệt nhọc. Đến với Victoria Coffee, dường như những điều giản dị chỉ c&oacute; trong mong muốn ấy hiện l&ecirc;n r&otilde; rệt hơn trước mắt bạn.</p>\r\n\r\n<p><img src="/ckfinder/userfiles/files/image-20190806161438-1.jpeg" style="height:706px; width:945px" /></p>\r\n\r\n<p>Nằm ở ngay khu đ&ocirc; thị sầm uất Himlam, nhưng Victoria Coffee vẫn giữ nguy&ecirc;n những gi&aacute; trị của một qu&aacute;n c&agrave; ph&ecirc; y&ecirc;n b&igrave;nh, chốn nghỉ ngơi cho những t&acirc;m hồn cần lắm một ng&agrave;y l&aacute;nh xa mệt mỏi. Ngay từ c&aacute;i t&ecirc;n cũng chất chứa n&eacute;t nhẹ nh&agrave;ng, thanh tao, v&igrave; Victoria c&oacute; nghĩa l&agrave; &ldquo;Mặt trăng&rdquo;. Sự nhẹ nh&agrave;ng, dễ cảm, hiền dịu của mặt trăng cũng l&agrave; &yacute; tưởng chủ đạo xuy&ecirc;n suốt thiết kế kh&ocirc;ng gian của qu&aacute;n. Thật vậy, kh&ocirc;ng gian sang trọng của kiến tr&uacute;c Ph&aacute;p c&oacute; lẽ l&agrave; điểm nhấn đặc biệt với những thực kh&aacute;ch lần đầu đến với Victoria. C&oacute; hai kh&ocirc;ng gian ch&iacute;nh để kh&aacute;ch c&oacute; thể lựa chọn t&aacute;n ngẫu với bạn b&egrave;, ph&ograve;ng m&aacute;y lạnh (c&oacute; cả khu vực kh&ocirc;ng h&uacute;t thuốc) v&agrave; khu vực s&acirc;n vườn rộng r&atilde;i v&agrave; tho&aacute;ng m&aacute;t. Với thiết kế được đầu tư kĩ lưỡng về phần nh&igrave;n như thế, mọi kh&aacute;ch h&agrave;ng c&oacute; thể cảm nhận r&otilde; n&eacute;t kh&ocirc;ng gian &Acirc;u Ch&acirc;u lịch thiệp, quyến rũ, với nền văn h&oacute;a c&agrave; ph&ecirc; phồn hoa kh&ocirc;ng thể lẩn n&agrave;o đ&acirc;u được.</p>\r\n\r\n<p><img src="/ckfinder/userfiles/files/image-20190806161440-2.png" style="height:538px; width:753px" /></p>\r\n\r\n<p>C&oacute; thể n&oacute;i, Victoria Coffee kh&ocirc;ng đơn thuần l&agrave; một qu&aacute;n c&agrave; ph&ecirc; truyền thống, tại nơi đ&acirc;y ngo&agrave;i nh&acirc;m nhi t&aacute;ch c&agrave; ph&ecirc; thơm lừng, bạn c&ograve;n c&oacute; thể thưởng thức những m&oacute;n điểm t&acirc;m s&aacute;ng, cơm trưa văn ph&ograve;ng c&ugrave;ng bạn b&egrave;, đồng nghiệp. Thực đơn của Victoria Coffee v&ocirc; c&ugrave;ng đa dạng, đặc trưng nhất phải kể đến m&oacute;n điểm t&acirc;m đặc sản c&ugrave;a xứ nghệ, s&uacute;p lươn, miến lươn. S&uacute;p lươn, miến lươn với nước d&ugrave;ng sền sệt, vị cay nồng, ngọt đậm đ&agrave; sẽ k&iacute;ch th&iacute;ch vị gi&aacute;c của những thực kh&aacute;ch kh&oacute; t&iacute;nh nhất, khiến họ c&oacute; thể khởi đầu một ng&agrave;y mới bận rộn với t&acirc;m trạng hưng phấn v&agrave; sảng kho&aacute;i nhất.</p>\r\n\r\n<p><img src="/ckfinder/userfiles/files/image-20190806161440-3.jpeg" style="height:459px; width:688px" /></p>\r\n\r\n<p>Ngo&agrave;i ra,kh&ocirc;ng thể kể đến những m&oacute;n uống hấp dẫn đ&atilde; tạo n&ecirc;n t&ecirc;n tuổi của qu&aacute;n. Thật vậy, Victoria coffee lu&ocirc;n đặt chất lượng v&agrave; sự an to&agrave;n của kh&aacute;ch l&ecirc;n h&agrave;ng đầu, v&igrave; thế nguồn Coffee lu&ocirc;n được xem x&eacute;t kĩ c&agrave;ng từ kh&acirc;u tuyển chọn, đảm bảo nguy&ecirc;n chất 100%. V&igrave; thế, bạn c&ograve;n chần chừ g&igrave; nữa m&agrave; kh&ocirc;ng đến với Victoria Coffee?</p>\r\n\r\n<p><img src="/ckfinder/userfiles/files/image-20190806161440-4.jpeg" style="height:532px; width:800px" /></p>\r\n\r\n<p>Một điểm cộng kh&ocirc;ng thể bỏ qua để lựa chọn Victoria Coffee l&agrave; qu&aacute;n y&ecirc;u th&iacute;ch nhất, đ&oacute; ch&iacute;nh l&agrave; thời gian hoạt động k&eacute;o d&agrave;i, từ 6h đến 12h đ&ecirc;m, khiến bạn c&oacute; thể h&ograve;a m&igrave;nh v&agrave;o kh&ocirc;ng gian y&ecirc;n b&igrave;nh của qu&aacute;n bất cứ khi n&agrave;o trong ng&agrave;y, từ s&aacute;ng sớm b&igrave;nh minh cho đến khi trời đ&atilde; về khuya. C&oacute; thể n&oacute;i, một qu&aacute;n c&agrave; ph&ecirc; đủ để lay động tr&aacute;i tim bạn kh&ocirc;ng chỉ đơn thuần l&agrave; một qu&aacute;n c&agrave; ph&ecirc; h&agrave;i h&ograve;a về h&igrave;nh ảnh v&agrave; chất lượng thức uống, m&agrave; ở tại đ&oacute; bạn thấy l&ograve;ng m&igrave;nh thư thả, c&oacute; thể ngắm nh&igrave;n đường phố H&agrave; Nội với con mắt y&ecirc;n b&igrave;nh v&agrave; hạnh ph&uacute;c nhất, Victoria Coffee ch&iacute;nh l&agrave; nơi để biến những ước mong ấy th&agrave;nh hiện thực, để cảm nhận r&otilde; gi&aacute; trị của thời gian, để nhận ra rằng gi&aacute; trị của sự y&ecirc;n b&igrave;nh đem đến những th&agrave;nh c&ocirc;ng ngo&agrave;i sức tưởng tượng, để hiểu rằng đ&ocirc;i khi điều tuyệt vời nhất, đến từ những điều giản dị bậc nhất.</p>\r\n\r\n<p><img src="/ckfinder/userfiles/files/image.png" style="height:607px; width:1080px" /></p>\r\n', 0, 'Nguyễn Thanh Tình - PHP39', NULL, 1, '2019-08-06 09:15:37'),
(34, 'Cung cấp cà phê nguyên chất giá sỉ cho quán, nhà hàng, khách sạn trên toàn quốc', 8, 1, '', '', '<p><strong>BẠN CẦN T&Igrave;M D&Ograve;NG C&Agrave; PH&Ecirc; CHO QU&Aacute;N?</strong></p>\r\n\r\n<p><strong>Ph&acirc;n kh&uacute;c b&igrave;nh d&acirc;n, ch&uacute;ng t&ocirc;i c&oacute;:</strong></p>\r\n\r\n<p>&middot;&nbsp; <strong>Bản Đ&ocirc;n</strong>&nbsp;với c&aacute;c m&atilde;: RIC1, RIC2, RIC3, đ&acirc;y l&agrave; c&aacute;c d&ograve;ng pha phin, pha vợt. Hương vị đậm đ&agrave;, m&agrave;u n&acirc;u hổ ph&aacute;ch, gần giống gu truyền thống nhất. Gi&aacute; tầm 110.000đ &ndash; 125.000đ/kg.</p>\r\n\r\n<p>&middot;&nbsp; <strong>Passion Blend</strong>&nbsp;th&igrave; c&oacute; BIC1, BIC2, BIC3, d&agrave;nh cho chuỗi qu&aacute;n pha m&aacute;y mang đi (Take Away Coffee). Pha m&aacute;y nhưng đảm bảo phong vị đậm đ&agrave; gần giống pha phin. Điểm nhấn, d&ograve;ng n&agrave;y pha cafe sữa đ&aacute; từ m&aacute;y l&agrave; tuyệt. Gi&aacute; tầm 160.000đ &ndash; 180.000đ/kg.</p>\r\n\r\n<p><br />\r\n&nbsp;</p>\r\n\r\n<p><strong>Ph&acirc;n kh&uacute;c tầm trung đến s&agrave;nh điệu, ch&uacute;ng t&ocirc;i c&oacute;:</strong></p>\r\n\r\n<p>&middot;&nbsp; <strong>Harmony Blend:</strong>&nbsp;7 Arabica, 3 Robusta</p>\r\n\r\n<p>&middot;&nbsp; <strong>Pongour:</strong>&nbsp;100% Arabica</p>\r\n\r\n<p>&middot;&nbsp; <strong>Red Land:</strong>&nbsp;100% Moka</p>\r\n\r\n<p>&middot;&nbsp; <strong>Eroka:</strong>&nbsp;100% Arabica Cầu Đất v&agrave; Arabica Ethiopia</p>\r\n\r\n<p><br />\r\n&nbsp;</p>\r\n\r\n<p><strong>Sự kh&aacute;c biệt ch&iacute;nh từ Victoria Coffee:</strong></p>\r\n\r\n<p>&middot;&nbsp; Xưởng rang đạt chuẩn HACCP</p>\r\n\r\n<p>&middot;&nbsp; M&aacute;y rang Hot Air, rang theo Profile</p>\r\n\r\n<p>&middot;&nbsp; C&aacute;c d&ograve;ng c&agrave; ph&ecirc; đều rang mộc</p>\r\n\r\n<p>&middot;&nbsp; Hướng dẫn c&aacute;c kiểu pha c&agrave; ph&ecirc; v&agrave; tư vấn menu miễn ph&iacute;</p>\r\n\r\n<p><img src="/ckfinder/userfiles/files/image-20190806161710-1.jpeg" style="height:768px; width:1024px" /><img src="/ckfinder/userfiles/files/image-20190806161710-2.jpeg" style="height:768px; width:1024px" /></p>\r\n', 0, '', NULL, 1, '2019-08-06 09:17:29');

