--
-- Table structure for table `product`
--

CREATE TABLE IF NOT EXISTS `product` (
`id` int(10) NOT NULL,
  `name` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT 'Tên sản phẩm',
  `english_name` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,
  `product_category_id` int(10) NOT NULL,
  `img` varchar(100) COLLATE utf8_unicode_ci NOT NULL,
  `price` int(10) NOT NULL,
  `description` text COLLATE utf8_unicode_ci NOT NULL,
  `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `highlight` int(1) DEFAULT NULL
) ENGINE=InnoDB AUTO_INCREMENT=85 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- Dumping data for table `product`
--

INSERT INTO `product` (`id`, `name`, `english_name`, `product_category_id`, `img`, `price`, `description`, `created_at`, `highlight`) VALUES
(3, '   Bò lúc lắc khoai tây chiên    ', 'Beef shakes fries potato    ', 2, '15639839443.bo-luc-lac-khoai-tay-chien-1497412811.jpg', 125000, '<p>Đĩa b&ograve; l&uacute;c lắc với miếng thịt b&ograve; vu&ocirc;ng vắn ch&iacute;n mềm thơm phức, c&ugrave;ng rau củ với m&agrave;u sắc tươi mới v&agrave; kh&ocirc;ng thể thiếu khoai t&acirc;y chi&ecirc;n v&agrave;ng rụm, thơm lừng hẳn sẽ l&agrave;m xi&ecirc;u l&ograve;ng rất nhiều qu&yacute; kh&aacute;ch.</p>\r\n', '2019-08-05 10:17:16', 0),
(4, ' Lẩu gà ', 'Chicken hotpot ', 2, '15639839584.lau-ga-quan-an-ngon-1497412365.jpg', 380000, '<p>G&agrave; ta tươi chặt, nước hầm xương heo trong, thơm m&ugrave;i thuốc bắc v&agrave; nấm hương, vị vừa ăn. B&uacute;n v&agrave; rau ăn k&egrave;m gồm ngải cứu, c&aacute;c loại nấm, th&acirc;n chuối th&aacute;i rối.</p>\r\n', '2019-08-05 10:20:14', 0),
(5, '  Cua nướng   ', 'Steamed / Grilled Crab   ', 2, '15646748585.cua-nuong-quan-an-ngon-1498467425.jpg', 500000, '<p>Cua biển nướng giữ được vị ngọt nguy&ecirc;n thủy được chế biến khi đang tươi, m&oacute;n chấm k&egrave;m gia vị muối ti&ecirc;u chanh ớt.</p>\r\n', '2019-08-01 15:54:18', 1),
(6, ' Cá thu nướng muối ớt  ', 'Grilled mackerel with chilli & salt  ', 2, '15639840006.ca-thu-nuong-muoi-ot-quan-an-ngon-1498468358.jpg', 150000, '<p>C&aacute; thu ngọt thịt, đậm đ&agrave; thấm vị từ gia vị ướp kỹ lưỡng v&agrave; m&ugrave;i ớt hơi hăng hăng cay, c&aacute; nướng thơm đậm m&ugrave;i mằn mặn của biển sẽ l&agrave; thức ăn đưa cơm những ng&agrave;y h&egrave; n&oacute;ng</p>\r\n', '2019-08-05 10:17:47', 0),
(7, ' Bò hầm đặc biệt  ', 'Special beef stew  ', 2, '15639840207.steak-vo-cung-h-p-d-n.jpg', 275000, '<p>Đ&atilde; từng m&ecirc; mẩn với những m&oacute;n ngon từ b&ograve; như b&ograve; sốt vang, b&ograve; nướng l&aacute; lốt, &hellip; th&igrave; bạn kh&ocirc;ng thể bỏ qua m&oacute;n thịt b&ograve; hầm hấp dẫn được. Những miếng thịt mềm như tan v&agrave;o miệng, lại thơm hương đậm vị, kh&ocirc;ng c&ograve;n l&agrave; ăn m&agrave; l&agrave; &ldquo;thưởng thức&rdquo;</p>\r\n', '2019-08-05 14:07:19', 0),
(8, ' Gà hấp lá chanh ', 'Steamed chicken with shredded lemon leaves (half) ', 2, '15639840378.ga-hap-la-chanh-quan-an-ngon-1498466884.jpg', 320000, '<p>G&agrave; ta hấp l&aacute; chanh m&aacute;t, thịt vừa ch&iacute;n, thơm m&ugrave;i l&aacute; chanh.</p>\r\n', '2019-08-05 10:20:40', 0),
(9, ' Gà rang muối ', 'Fried chicken with salt (half) ', 2, '15639840599.ga-rang-muoi-quan-an-ngon-1498467117.jpg', 320000, '<p>G&agrave; ta rang muối b&aacute;m đều, vị vừa ăn thơm m&ugrave;i đặc trưng của nguy&ecirc;n liệu v&agrave; sả, l&aacute; chanh rang gi&ograve;n. Chấm k&egrave;m muối ti&ecirc;u chanh ớt.</p>\r\n', '2019-08-05 10:20:55', 0),
(10, ' Cánh gà chiên tỏi ', 'Fried chicken with salt (half) ', 2, '156398407510.canh-ga-chien-toi-quan-an-ngon-1498464804.jpg', 90000, '<p>C&aacute;nh g&agrave; chi&ecirc;n thấm vị, thơm m&ugrave;i tỏi, &aacute;nh m&agrave;u v&agrave;ng &oacute;ng hấp dẫn. M&oacute;n chấm k&egrave;m tương ớt x&igrave; dầu v&agrave; dưa chuột th&aacute;i l&aacute;t ăn k&egrave;m.</p>\r\n', '2019-08-05 10:21:02', 0),
(11, ' Chim sẻ quay  ', 'Roasted sparrows  ', 2, '156398408911.chim-se-an-them-1501063080.jpg', 90000, '<p>Chim sẻ quay cả con &aacute;nh m&agrave;u v&agrave;ng c&aacute;nh gi&aacute;n, gia vị vừa vặn, l&agrave; m&oacute;n ăn bổ dưỡng cho cả hai ph&aacute;i.</p>\r\n', '2019-08-05 10:17:35', 0),
(12, ' Cơm chiên hoàng bò  ', 'Roasted rice with beef  ', 2, '156398410312.com chien hoang bo.jpg', 150000, '<p>Thưởng thức m&oacute;n cơm chi&ecirc;n thịt b&ograve; n&oacute;ng hổi, hấp dẫn v&agrave;o buổi s&aacute;ng ng&agrave;y lạnh thật th&iacute;ch hợp. Đ&acirc;y l&agrave; m&oacute;n ăn chứa nhiều chất dinh dưỡng v&agrave; c&oacute; h&agrave;m lượng calo cao. Thịt b&ograve; chứa nhiều chất sắt, protein v&agrave; vitamin l&agrave;m tăng khả năng miễn dịch v&agrave; cung cấp th&ecirc;m một số chất cần thiết cho cơ thể</p>\r\n', '2019-08-05 14:58:13', 1),
(13, ' Bò Mỹ Nướng ', 'Special grilled beef ', 2, '156398414313.bo-nuong-nha-trang-quan-an-ngon-1497404840.jpg', 145000, '<p>Thịt b&ograve; Mỹ nướng với m&agrave;u n&acirc;u v&agrave;ng, hương vị hấp dẫn v&agrave; nhiều chất dinh dưỡng. Ăn với b&aacute;nh m&igrave; nướng thật tuyệt.</p>\r\n', '2019-08-05 10:21:22', 0),
(14, ' Cua rang muối  ', 'Vietnamese salted crab  ', 2, '156398415814.cua-rang-muoi-quan-an-ngon-1498467724.jpg', 500000, '<p>Cua rang muối vị mặn ngọt, thịt cua săn chắc, m&agrave;u v&agrave;ng cam bắt mắt, thịt cua ngọt, cộng với m&ugrave;i thơm v&agrave; cay nồng của muối rang l&agrave;m n&ecirc;n n&eacute;t đặc trưng của m&oacute;n ăn.</p>\r\n', '2019-08-05 14:05:23', 1),
(15, ' Lẩu Bông Miền Tây ', 'Western hotpot ', 2, '156398417115.lau-bong-mien-tay 420k.jpg', 420000, '<p>Khi ăn, thực kh&aacute;ch chỉ việc thả hải sản v&agrave;o nồi lẩu s&ocirc;i s&ugrave;ng sục cho ch&iacute;n, nh&uacute;ng một &iacute;t rau vừa đủ, gắp miếng b&uacute;n v&agrave; chan miếng nước lẩu n&oacute;ng hổi l&agrave; đ&atilde; c&oacute; thể thưởng thức trọn vẹn hương vị m&oacute;n lẩu b&ocirc;ng Miền T&acirc;y, từ đ&oacute; thực kh&aacute;ch sẽ cảm nhận được đủ đa vị với vị ngọt thơm của hải sản, vị đăng đắng của b&ocirc;ng đi&ecirc;n điển, vị b&ugrave;i b&ugrave;i của so đũa, chua chua từ me, đến c&aacute;i sần sật của rau nh&uacute;t hay h&iacute;t h&agrave; vị cay của ớt&hellip;</p>\r\n', '2019-08-05 10:21:35', 0),
(16, ' Lẩu cá chình ', 'Lamprey hotpot ', 2, '156398419116.lau-ca-chinh-quan-an-ngon-1497411085.jpg', 575000, '<p>C&aacute; ch&igrave;nh phi l&ecirc;, nước lẩu m&agrave;u hồng nhạt, dậy vị thơm đặc trưng của riềng, sả, gia vị lẩu th&aacute;i Tomyum, vị chua ngọt vừa ăn, hơi cay. Rau ăn k&egrave;m theo m&ugrave;a.</p>\r\n', '2019-08-05 10:21:41', 0),
(17, ' Lẩu hải sản chua cay  ', 'Sour & spicy seafood hotpot  ', 2, '156398425917.lau-hai-san-chua-cay-quan-an-ngon-1497410729.jpg', 420000, '<p>Hải sản nh&uacute;ng lẩu gồm t&ocirc;m s&uacute; loại 50 con/kg, mực ống phi l&ecirc;, c&aacute; l&oacute;c th&aacute;i miếng, ngao v&agrave;ng. Nước hầm xương nấu lẩu l&ecirc;n th&agrave;nh phẩm c&oacute; m&agrave;u hồng nhạt, dậy vị thơm đặc trưng của riềng, sả, gia vị lẩu th&aacute;i Tomyum chua dịu v&agrave; hơi cay, vị vừa ăn. B&uacute;n v&agrave; rau theo m&ugrave;a ăn k&egrave;m (cải thảo, cải ch&iacute;p, cải c&uacute;c&hellip;)</p>\r\n', '2019-08-05 14:10:10', 1),
(18, ' Lẩu riêu cua bắp bò ', 'Sour & spicy seafood hotpot ', 2, '156398427518.lau-rieu-cua-bap-bo-2-1501126359.jpg', 380000, '<p>Bắp b&ograve; loại l&otilde;i hoa tươi th&aacute;i l&aacute;t mỏng. Nước lẩu hầm xương c&oacute; m&agrave;u đỏ bắt mắt từ c&agrave; chua, m&agrave;u v&agrave;ng s&aacute;ng từ h&agrave;nh kh&ocirc; phi thơm, nước lẩu dậy vị chua dịu, quyện m&ugrave;i thơm lừng của giấm bỗng v&agrave; cua đồng xay nhuyễn, v&agrave; những miếng đậu chi&ecirc;n v&agrave;ng thấm đẫm hương vị của nước lẩu. Rau ăn k&egrave;m gồm hoa chuối, bắp v&agrave; th&acirc;n rau muống th&aacute;i rối, h&agrave;nh chẻ, b&aacute;nh đa v&agrave; b&uacute;n rối.</p>\r\n', '2019-08-05 10:21:54', 0),
(19, ' Mực nướng muối ớt  ', 'Grilled cuttle-fish with chilli & salt  ', 2, '156398429219.muc-nuong-muoi-ot-quan-an-ngon-1498471066.jpg', 255000, '<p>Mực một nắng nguy&ecirc;n con to, d&agrave;y m&igrave;nh, nướng x&eacute;m cạnh v&agrave;ng s&aacute;ng, kh&ocirc;ng kh&ocirc;, m&ugrave;i, đặc trưng vị vừa ăn.</p>\r\n', '2019-08-05 14:06:32', 1),
(20, ' Mực một nắng chiên giòn ', 'Crispy fried half-dried cuttle-fish ', 2, '156398431320.muc-mot-nang--chien-gion-375k.jpg', 255000, '<p>Mực một nắng l&agrave; mực ống được chọn tỉ mỉ từ miền trung, đem đi chi&ecirc;n gi&ograve;n. Vị ngọt của mực kết hợp với bột chi&ecirc;n b&ugrave;i v&agrave; xốp, th&ecirc;m v&agrave;o một ch&uacute;t cay cay của tương ớt; bữa ăn đầy đặn bởi sự kết hợp kh&eacute;o l&eacute;o với những m&oacute;n hải sản được chế biến vừa miệng: gỏi cuốn t&ocirc;m thịt, h&agrave;u nướng mỡ h&agrave;nh, cơm chi&ecirc;n hải sản. V&agrave; khi đ&atilde; h&agrave;i l&ograve;ng bởi h&iacute;t h&agrave; &ndash; chẳng ai từ chối một cốc ch&egrave; sương sa hạt lựu ngọt m&aacute;t&hellip;</p>\r\n', '2019-08-05 10:22:10', 0),
(21, ' Bò hầm đặc biệt ', 'Special beef stew ', 2, '156398433721.soho-steak-73-ph-hu(1).jpg', 255000, '<p>Đ&atilde; từng m&ecirc; mẩn với những m&oacute;n ngon từ b&ograve; như b&ograve; sốt vang, b&ograve; nướng l&aacute; lốt, &hellip; th&igrave; bạn kh&ocirc;ng thể bỏ qua m&oacute;n thịt b&ograve; hầm hấp dẫn được. Những miếng thịt mềm như tan v&agrave;o miệng, lại thơm hương đậm vị, kh&ocirc;ng c&ograve;n l&agrave; ăn m&agrave; l&agrave; &ldquo;thưởng thức&rdquo;</p>\r\n', '2019-08-05 10:22:17', 0),
(22, 'Tôm hấp nước dừa    ', 'Steamed shrimp in coconut    ', 2, '156398598840.tom-hap-nuoc-dua-quan-an-ngon-1498554002.jpg', 205000, '<p>Hương thơm từ nước dừa c&ugrave;ng vị ngọt thanh tiết ra từ thịt t&ocirc;m khiến t&ocirc;m ăn đằm vị hơn v&agrave; c&ugrave;i dừa non ngọt b&eacute;o, khiến ai chỉ nh&igrave;n th&ocirc;i cũng cồn c&agrave;o vị gi&aacute;c.</p>\r\n', '2019-08-06 09:13:14', 0),
(23, ' Bún gạo xào tôm thịt ', 'Stir-fried dry rice noodles with pork & shrimps ', 2, '156398437223.bun-gao-xao-tom-thit-1-copy-1501062841.jpg', 130000, '<p>B&uacute;n gạo sợi săn x&agrave;o thơm, t&ocirc;m v&agrave; thịt thăn ch&iacute;n tới, m&ugrave;i thơm đặc trưng.</p>\r\n', '2019-08-05 10:22:28', 0),
(24, ' Cá diêu hồng hấp xì dầu ', 'Steamed red tilapia with soy sauce ', 2, '156398438624.ca-dieu-hong-hap-xi-dau-3-1497326144.jpg', 255000, '<p>C&aacute; đi&ecirc;u hồng thịt ngọt dễ ăn, hợp với thời tiết n&oacute;ng. Thịt c&aacute; m&aacute;t, mềm thơm, b&eacute;o ngậy quyện vị ướp vừa của x&igrave; dầu v&agrave; hương cay nồng ấm bụng của gừng tươi.</p>\r\n', '2019-08-05 10:22:35', 0),
(25, ' Chim câu quay ', 'Roasted pigeon ', 2, '156398440425.chim-cau-quay-quan-an-ngon-1498465221.jpg', 165000, '<p>Chim c&acirc;u được lựa l&agrave; con to vừa, được tẩm ướp cầu kỳ trước khi đem l&ecirc;n quay đều, chim quay ch&iacute;n tới, thơm m&ugrave;i đặc trưng, mang sắc v&agrave;ng &oacute;ng tự nhi&ecirc;n. Thơm m&ugrave;i l&aacute; chanh</p>\r\n', '2019-08-05 10:22:42', 0),
(26, ' Cơm lam gà nướng  ', 'Bamboo cooked rice served with grilled chicken  ', 2, '156398450826.comlamganuong.jpg', 150000, '<p>Cơm lam l&agrave; đặc sản của c&aacute;c d&acirc;n tộc thiểu số ph&iacute;a Bắc v&agrave; T&acirc;y Nguy&ecirc;n. Người v&ugrave;ng cao trước kia khi đi rừng thường phải t&igrave;m ra c&aacute;ch chế biến, nấu cơm sao cho thật tiện lợi, th&iacute;ch nghi với ho&agrave;n cảnh.</p>\r\n', '2019-08-05 14:04:41', 1),
(27, ' Gỏi xoài hải sản ', 'Green mango salad with seafood ', 2, '156398453027.goixoaihaisan.jpg', 138000, '<p>Xo&agrave;i ương b&oacute;p thấu chua ngọt với nước gỏi để &aacute;t bớt vị chua gắt của xo&agrave;i, t&ocirc;m b&oacute;c n&otilde;n, mực hấp sơ, đậu phộng rang thơm rắc l&ecirc;n tr&ecirc;n c&ugrave;ng h&agrave;nh kh&ocirc; phi thơm. B&aacute;nh phồng t&ocirc;m ăn k&egrave;m</p>\r\n', '2019-08-05 10:23:02', 0),
(28, ' Lẩu cá lăng măng chua ', 'Sour green catfish hotpot with fresh bamboo shoot ', 2, '156398454528.lau-ca-lang-mang-chua-quan-an-ngon-1497411417.jpg', 400000, '<p>Thơm m&ugrave;i đặc trưng của c&aacute; lăng, c&oacute; vị hơi chua dịu từ măng củ, nước lẩu m&agrave;u hồng nhạt từ c&agrave; chua. B&uacute;n v&agrave; rau ăn k&egrave;m theo m&ugrave;a.</p>\r\n', '2019-08-05 10:23:11', 0),
(29, ' Lẩu cua bể  ', 'Crab hotpot  ', 2, '156398456329.lau-cua-be-quan-an-ngon-1497411828.jpg', 550000, '<p>Cua bể nguy&ecirc;n con loại 700gr, nước lẩu chua ngọt, vị hơi cay dịu, nổi bật m&ugrave;i thơm của hải sản cua bể, b&uacute;n v&agrave; rau tươi ăn k&egrave;m gồm cải thảo, cải chip, cải c&uacute;c, th&acirc;n hoa chuối th&aacute;i rối, gi&aacute; tươi&hellip;</p>\r\n', '2019-08-05 14:06:00', 0),
(30, ' Miến xào lươn ', 'Stir-fried vermicelli with eels ', 2, '156398458130.mien-xao-luon-quan-an-ngon-1497421418.jpg', 100000, '<p>Miến x&agrave;o sợi săn, lươn thơm m&ugrave;i đặc trưng, vị vừa ăn</p>\r\n', '2019-08-05 10:23:31', 0),
(31, ' Mỳ xào hải sản ', 'Stir-fried egg noodles with seafood & vegetables ', 2, '156398460131.my-xao-hai-san-quan-an-ngon-1498471309.jpg', 125000, '<p>M&igrave; trứng x&agrave;o sợi săn kh&ocirc;ng n&aacute;t, gia vị vừa ăn, t&ocirc;m mực của quả đậm vị ngọt đặc trưng, kh&ocirc;ng tanh. C&oacute; x&igrave; dầu ăn k&egrave;m.</p>\r\n', '2019-08-05 10:23:46', 0),
(32, 'Tôm càng xanh nướng ', 'Grilled giant freshwater prawn ', 2, '156398596641.tom-cang-xanh--nuong-quan-an-ngon-1498552884.jpg', 195000, '<p>T&ocirc;m c&agrave;ng xanh nhập đường bay từ miền s&ocirc;ng nước, nướng mọi đến khi c&oacute; m&agrave;u v&agrave;ng tr&ecirc;n than hồng, chấm muối ti&ecirc;u chanh ớt dậy m&ugrave;i thơm v&agrave; vị đạm ngọt thơm từ hải sản nướng.</p>\r\n', '2019-08-06 05:41:19', 1),
(33, ' Sò huyết nướng ', 'Grilled blood cockles ', 2, '156398464433.so-huyet-nuong-quan-an-ngon-1498552211.jpg', 125000, '<p>S&ograve; huyết tươi đều con nướng ch&iacute;n tới, kh&ocirc;ng kh&ocirc;, c&oacute; m&ugrave;i thơm đặc trưng, vị vừa ăn.</p>\r\n', '2019-08-05 10:23:58', 0),
(34, ' Cá lóc hấp ', 'Steamed snakehead fish served with rice papers ', 2, '156398467434.ca-loc-hap-cuon-banh-trang-2-1497327077.jpg', 255000, '<p>C&aacute; hấp c&ograve;n giữ nguy&ecirc;n được vị ngon, ngọt của thịt c&aacute; c&ugrave;ng c&aacute;c loại rau sống, chuối ch&aacute;t, dưa chuột, khế chua cuốn k&egrave;m b&aacute;nh tr&aacute;ng v&agrave; chấm nước mắm pha thơm.</p>\r\n', '2019-08-05 10:23:25', 0),
(35, ' Cá lăng nướng muối ớt ', 'Grilled green catfish with chilli & salt ', 2, '156398469535.ca-lang-nuong-muoi-ot-quan-an-ngon-1498464492.jpg', 160000, '<p>C&aacute; lăng c&oacute; thịt săn chắc, c&oacute; vị thơm ngon hơn c&aacute;c loại c&aacute; kh&aacute;c, khi nướng vẫn giữ được m&agrave;u v&agrave;ng đều, giữ được vị ngọt v&agrave; kh&ocirc;ng bị kh&ocirc;, thấm gia vị được gia giảm kỹ lưỡng.</p>\r\n', '2019-08-05 10:24:06', 0),
(37, 'Sườn nướng ', 'Grilled pork ribs served with lemongrass & chill ', 2, '156398590437.suong nuong.jpg', 125000, '<p>Sườn chọn miếng sườn non đem nướng ch&iacute;n tới v&agrave;ng m&agrave;u c&aacute;nh gi&aacute;n, dậy m&ugrave;i thơm đặc trưng của sả, ớt, vị vừa ăn. Cay dịu.</p>\r\n', '2019-08-05 10:24:12', 0),
(38, 'Sườn nướng xả ớt ', 'Grilled pork ribs served with lemongrass & chilli ', 2, '156398602538.suon-nuong-xa-ot-quan-an-ngon-1498552483.jpg', 140000, '<p>Sườn chọn miếng sườn non đem nướng ch&iacute;n tới v&agrave;ng m&agrave;u c&aacute;nh gi&aacute;n, dậy m&ugrave;i thơm đặc trưng của sả, ớt, vị vừa ăn. Cay dịu.</p>\r\n', '2019-08-05 10:24:19', 0),
(39, 'Tôm hấp cuốn bánh tráng   ', 'Steamed shrimp rolled with thick rice papers, vegetables & herbs   ', 2, '156398600839.tom-hap-cuon-banh-trang.jpg', 205000, '<p>T&ocirc;m hấp cuốn b&aacute;nh tr&aacute;ng gồm t&ocirc;m s&uacute; cuốn k&egrave;m b&aacute;nh tr&aacute;ng v&agrave; đồ cuốn gồm rau sống, chuối ch&aacute;t, khế chua, dưa chuột v&agrave; ch&uacute;t b&uacute;n. Chấm k&egrave;m mắm n&ecirc;m tang vị đậm đ&agrave;.</p>\r\n', '2019-08-06 09:12:58', 1),
(42, 'Bánh canh cua', 'Tapioca noodle soup with crab meat and pork', 1, '1564886691banh-canh-cua-quan-an-ngon-1498458607.jpg', 60000, '<p>Sợi b&aacute;nh canh l&agrave;m từ bột năng hoặc bột gạo, to hơn sợi b&uacute;n ngo&agrave;i Bắc, m&agrave;u trắng hơi trong trong, ăn dai hơn b&uacute;n. Nước d&ugrave;ng b&aacute;nh canh Qu&aacute;n Ăn Ngon cũng thường c&oacute; dạng sền sệt, ng&ograve;n ngọt ninh từ thịt, cua ghẹ hải sản v&agrave; một số phụ liệu kh&aacute;c, chứ kh&ocirc;ng nhan nh&aacute;t nhạt như nước d&ugrave;ng b&uacute;n phở.<br />\r\nC&oacute; kh&aacute; nhiều loại b&aacute;nh canh như b&aacute;nh canh t&ocirc;m, thịt lợn nhưng &quot;&aacute;m ảnh&quot; nhất vẫn l&agrave; b&aacute;nh canh cua. Con cua to, luộc ch&iacute;n, m&agrave;u đỏ cam bắt mắt, cả c&aacute;i c&agrave;ng chắc thịt ngự tr&ecirc;n b&aacute;t b&aacute;nh canh n&oacute;ng hổi, ăn k&egrave;m với thịt ch&acirc;n gi&ograve; v&agrave; ch&uacute;t ớt xanh, chanh tươi</p>\r\n', '2019-08-05 10:24:37', 0),
(43, 'Bánh cuốn nhân tôm', 'Steamed rice pancake with shrimp and minced pork', 1, '1564886741banh-cuon-nhan--tom-quan-an-ngon-1498458784.jpg', 60000, '<p>B&aacute;nh cuốn nh&acirc;n t&ocirc;m l&agrave; một trong những m&oacute;n b&aacute;nh được rất nhiều người y&ecirc;u th&iacute;ch bởi hương vị thơm ngon, dễ ăn, ăn nhiều m&agrave; kh&ocirc;ng hề cảm thấy ch&aacute;n.</p>\r\n', '2019-08-05 10:25:13', 0),
(44, 'Bò kho bánh mỳ', 'Braised beef bread', 1, '1564886783bo-kho-banh-mi.jpg', 65000, '<p>Thịt b&ograve; l&agrave; m&oacute;n ăn bổ dưỡng với nhiều c&aacute;ch chế biến kh&aacute;c nhau trong đ&oacute; c&oacute; m&oacute;n b&ograve; kho b&aacute;nh m&igrave;. Đ&acirc;y l&agrave; m&oacute;n rất dễ ăn lại gi&agrave;u dưỡng chất l&agrave; một lựa chọn rất tuyệt vời cho bạn v&agrave;o những dịp cuối tuần.</p>\r\n', '2019-08-05 10:25:22', 0),
(45, 'Bún bò Huế', 'Beef noodle soup with pig leg in Hue style', 1, '1564886821bun-bo-hue-27-1496718176.jpg', 60000, '<p>Một t&ocirc; b&uacute;n b&ograve; đạt ti&ecirc;u chuẩn gồm c&oacute;: b&uacute;n, thịt bắp b&ograve;, gi&ograve; heo, rau thơm, sa tế, mắm ruốc,&hellip;Điều đặc biệt của m&oacute;n ăn n&agrave;y l&agrave; nước l&egrave;o phải được nấu trong một nồi tr&ograve;n bằng nh&ocirc;m được g&ograve; bởi b&agrave;n tay kh&eacute;o l&eacute;o của người thợ.</p>\r\n', '2019-08-05 10:25:33', 0),
(46, 'Bún bò Nam Bộ', 'Southern style sautéed beef with rice noodles', 1, '1564886857bun-bo-nam-bo-1496721350.jpg', 60000, '<p>B&uacute;n b&ograve; Nam bộ, nghe c&aacute;i t&ecirc;n cũng đủ biết nguồn gốc xuất xứ. B&uacute;n n&agrave;y c&ograve;n được gọi l&agrave; b&uacute;n x&agrave;o v&igrave; m&oacute;n b&uacute;n n&agrave;y kh&ocirc;ng giống như những loại b&uacute;n nước kh&aacute;c m&agrave; l&agrave; b&uacute;n kh&ocirc;, l&agrave; sự kết hợp giữa b&uacute;n, rau sống, gi&aacute;, thịt b&ograve; x&agrave;o, đậu phộng rang, c&agrave; rốt đu đủ th&aacute;i sợi, h&agrave;nh phi thơm,&hellip;sau đ&oacute; th&ecirc;m nước mắm ớt chua cay v&agrave;o trộn đều.</p>\r\n', '2019-08-05 10:25:40', 0),
(47, 'Bún dọc mùng chân giò', 'Pork-legs rice noodle soup with Vietnamese taro', 1, '1564886908bun-doc-mung-quan-an-ngon-1498461895.jpg', 45000, '<p>M&oacute;ng ninh mềm ngọt, hương thơm nh&egrave; nhẹ k&egrave;m ch&uacute;t hăng của h&agrave;nh cọng sắt mỏng, nước d&ugrave;ng trong, vị chua thanh m&aacute;t.</p>\r\n', '2019-08-05 10:25:50', 0),
(48, 'Bún mắm', 'Rice noodle soup with fermented fish, seafood and roasted pork', 1, '1564886941bun-mam-quan-an-ngon-1498462126.jpg', 55000, '<p>B&uacute;n mắm vốn được biến tấu của mắm kho, một trong những m&oacute;n ăn đặc trưng của miền T&acirc;y s&ocirc;ng nước. Nước l&egrave;o của b&uacute;n mắm được nấu từ c&aacute; linh hay c&aacute; sặc, v&igrave; vậy m&agrave; m&oacute;n ăn c&oacute; m&ugrave;i vị mằn mặn nồng đặc biệt nhưng kh&ocirc;ng dễ thưởng thức với một v&agrave;i người. Th&agrave;nh phần ch&iacute;nh gồm c&aacute; linh, b&ocirc;ng s&uacute;ng, t&ocirc;m, mực,&hellip;v&agrave; quan trọng nhất l&agrave; rau đắng, thứ rau đặc biệt phổ biến ở miền T&acirc;y s&ocirc;ng nước.</p>\r\n', '2019-08-05 10:25:57', 0),
(49, 'Bún măng gà', 'Chicken & dry bamboo shoot rice noodle - soup or dipping sauce', 1, '1564886987bun-mang-ga-quan-an-ngon-1498462230.jpg', 50000, '<p>B&uacute;n măng g&agrave; l&agrave; m&oacute;n ăn ngon ph&ugrave; hợp với ng&agrave;y h&egrave; oi bức. Vị ngọt của g&agrave; v&agrave; măng kh&ocirc; h&ograve;a quyện với nhau l&agrave;m cho m&oacute;n ăn trở n&ecirc;n hấp dẫn hơn.</p>\r\n', '2019-08-05 10:26:04', 0),
(50, 'Bún nem nướng', 'Grilled pork balls served with rice noodles & fresh herbs', 1, '1564887032bun-mem-nuong-quan-an-ngon-1497429549.jpg', 60000, '<p>L&agrave; m&oacute;n ăn phổ biến ở c&aacute;c tỉnh miền Trung Việt Nam. V&agrave; t&ugrave;y theo v&ugrave;ng miền m&agrave; nem nướng c&oacute; c&aacute;ch l&agrave;m, c&aacute;ch ăn cũng kh&aacute;c nhau. B&uacute;n ăn k&egrave;m nem nướng thơm, mỡ h&agrave;nh, rắc đậu phộng rang gi&atilde; nhỏ, chan nước mắm dưa g&oacute;p chua ngọt trộn đều.</p>\r\n', '2019-08-05 10:26:13', 0),
(51, 'Bún ốc', 'Snail noodle soup or dipping sauce', 1, '1564887069bun-oc-quan-an-ngon-1497430880.jpg', 50000, '<p>Ốc nhồi lựa con loại to, nước d&ugrave;ng trong, m&agrave;u đỏ nhạt của c&agrave; chua, thơm m&ugrave;i ốc v&agrave; h&agrave;nh phi, giấm bỗng, vị chua dịu. M&oacute;n ăn k&egrave;m rau sống, tương ớt, mắm t&ocirc;m.</p>\r\n', '2019-08-05 10:26:20', 0),
(52, 'Bún riêu cua', 'Fresh water crab with rice noodle soup', 1, '1564887102bun-reu-cua-quan-an-ngon-1497431304.jpg', 50000, '<p>Gạch cua đồng gi&atilde; tay, nước d&ugrave;ng trong m&agrave;u đỏ nhạt, thơm m&ugrave;i giấm bỗng, đậm m&ugrave;i cua, vị vừa, chua dịu. M&oacute;n ăn k&egrave;m rau sống, tương ớt, một ch&uacute;t mắm t&ocirc;m (để ri&ecirc;ng t&ugrave;y khẩu vị).</p>\r\n', '2019-08-05 10:26:27', 0),
(53, 'Bún Thái', 'Thai noodles', 1, '1564887176bunthai.jpg', 60000, '<p>B&uacute;n Th&aacute;i l&agrave; m&oacute;n ăn c&oacute; nguồn gốc từ Th&aacute;i Lan nhưng lại được nhiều bạn trẻ Việt Nam ưa chuộng v&igrave; c&oacute; vị chua cua, cay cay c&ugrave;ng với m&ugrave;i thơm đặc trưng dễ chịu.</p>\r\n', '2019-08-05 10:26:35', 0),
(54, 'Hủ tiếu Nam Vang khô', 'Cambodia style rice vermicelli with pork,shrimp,pork-liver & quail egg (mixed)', 1, '1564887231hu-tieu-kho-1501063365.jpg', 65000, '<p>Cũng l&agrave; c&aacute;c nguy&ecirc;n liệu giống như một b&aacute;t Hủ tiếu Nam Vang chan nước l&egrave;o, hủ tiếu Nam Vang kh&ocirc; để nguy&ecirc;n c&aacute;c nguy&ecirc;n liệu v&ocirc; b&aacute;t v&agrave; b&ecirc;n cạnh đặt một b&aacute;t nước d&ugrave;ng ri&ecirc;ng cho người n&agrave;o kh&ocirc;ng th&iacute;ch chan nước. Khi ăn trộn đều. Thi thoảng hơi ng&aacute;n c&oacute; thể nhấp một v&agrave;i th&igrave;a nước d&ugrave;ng để sẵn ngo&agrave;i b&aacute;t ri&ecirc;ng c&oacute; thả v&agrave;i l&aacute;t h&agrave;nh l&aacute;.</p>\r\n', '2019-08-05 10:26:41', 0),
(55, 'Hủ tiếu Nam Vang', 'Cambodia style rice vermicelli soup with pork, shrimp, pork-liver & quail egg', 1, '1564887273hu-tieu-nam-vang-2-1501063325.jpg', 65000, '<p>Hủ tiếu Nam Vang, m&oacute;n ăn n&agrave;y c&oacute; nguy&ecirc;n liệu ch&iacute;nh l&agrave; l&ograve;ng heo c&ugrave;ng c&aacute;c phụ liệu kh&aacute;c l&agrave; hủ t&iacute;u kh&ocirc;, nước l&egrave;o hầm từ xương heo, t&ocirc;m, thịt, trứng c&uacute;t, thịt heo băm,&hellip;</p>\r\n', '2019-08-05 10:26:49', 0),
(56, 'Miến măng gà', 'Chicken & dry bamboo shoot vermicelli - soup or mixed', 1, '1564887310mien-mang-ga-1496717069.jpg', 55000, '<p>Măng c&oacute; thể kết hợp v&oacute;i nhiều loại thịt như thịt ch&acirc;n gi&ograve;, thịt vịt, thịt g&agrave; để cho ra những m&oacute;n ăn ngon. Trong số đ&oacute; phải kể đến m&oacute;n miến măng g&agrave;. T&ocirc; miến g&agrave; với đầy đủ sắc m&agrave;u, ngập tr&agrave;n hương vị. Nhờ bổ trợ cho nhau, miếng thịt g&agrave; như đỡ ngậy hơn, v&agrave; miếng măng kh&ocirc; th&igrave; lại đậm đ&agrave; hơn.<br />\r\nMiến măng g&agrave; ngon l&agrave; miến c&oacute; nước l&egrave;o trong v&agrave; ngọt thanh, gồm c&oacute; thịt g&agrave; ta th&aacute;i miếng, h&agrave;nh chẻ, miến v&agrave; th&agrave;nh phần ch&iacute;nh l&agrave; măng kh&ocirc;. Măng kh&ocirc; ngon phải c&oacute; độ gi&ograve;n nhất định. Ăn k&egrave;m với rau thơm, l&aacute;t chanh v&agrave; một ch&uacute;t tương ớt.</p>\r\n', '2019-08-05 10:27:15', 0),
(57, 'Phở bò chín', 'PHO noodle soup with well-done beef', 1, '1564894431pho-bo-chin-quan-an-ngon-1498551790.jpg', 50000, '<p>Phở l&agrave; m&oacute;n ăn truyền thống từ xa xưa của Việt Nam v&agrave; cũng được xem l&agrave; một m&oacute;n ăn đại diện cho nền ẩm thực Việt Nam thời hiện đại.</p>\r\n', '2019-08-08 11:06:41', 0),
(58, 'Mì vằn thắn', 'Chinese noodles', 1, '1564894316my-van-than-quan-an-ngon-1497429996.jpg', 60000, '<p>Vằn thắn (hay ho&agrave;nh th&aacute;nh, sủi cảo) l&agrave; một m&oacute;n ăn c&oacute; nguồn gốc từ Quảng Đ&ocirc;ng, Trung Quốc. Vằn thắn được l&agrave;m từ thịt, hải sản v&agrave; rau băm nhỏ, g&oacute;i lại bằng vỏ bột m&igrave;, đem hấp ch&iacute;n l&ecirc;n. Sau khi hấp, miếng vằn thắn ch&iacute;n, vỏ bột ch&iacute;n trong nh&igrave;n thấy cả nh&acirc;n b&ecirc;n trong, rất quyến rũ.<br />\r\nMỳ vằn thắn ngon nhất l&agrave; ăn v&agrave;o m&ugrave;a Đ&ocirc;ng trời l&agrave;nh lạnh. Ngồi y&ecirc;n vị trong nh&agrave;, h&uacute;p nước soup ngọt lừ, ăn miếng vằn thắn, thịt x&aacute; x&iacute;u ngon ngọt thấy người khỏe khoắn hẳn, y&ecirc;u đời hơn bao nhi&ecirc;u.</p>\r\n', '2019-08-05 10:27:21', 0),
(59, 'Phở gà', 'Chicken PHO noodle-soup or mixed', 1, '1564894349pho-ga-quan-an-ngon-1498552042.jpg', 50000, '<p>Nước phở trong, g&agrave; ta thịt ngọt, thơm m&ugrave;i g&agrave; v&agrave; l&aacute; chanh. Ăn k&egrave;m tương ớt v&agrave; tương đen, hai loại gia vị l&agrave;m tang th&ecirc;m hương vị đậm đ&agrave; của m&oacute;n phở n&agrave;y. Một t&ocirc; phở kh&ocirc;ng thể thiếu rau quế, h&uacute;ng l&igrave;u, ng&ograve; om, v&agrave;i l&aacute;t chanh tươi v&agrave; ớt. Người miền Nam c&ograve;n ăn phở với gi&aacute; trụng ch&iacute;n v&agrave; sa tế ớt.</p>\r\n', '2019-08-05 10:27:34', 0),
(60, 'Mỳ Ý sốt bò băm', 'Spaghetti', 1, '1564894393my-y-sot-bo-bam-don-gian-ma-ngon_2.jpg', 65000, '<p>M&igrave; &Yacute; sốt b&ograve; băm hay c&ograve;n gọi l&agrave; Spaghetti l&agrave; m&oacute;n ăn ngon v&agrave; phổ biến, d&ugrave;ng k&egrave;m với hỗn hợp sốt c&agrave; chua v&agrave; thịt b&ograve; bằm c&oacute; nguồn gốc từ Italia. Đ&acirc;y l&agrave; m&oacute;n ăn bổ dưỡng v&agrave; nhiều năng lượng m&agrave; bạn c&oacute; thể d&ugrave;ng v&agrave;o buổi s&aacute;ng.</p>\r\n', '2019-08-05 10:27:44', 0),
(61, 'Mì xá xíu ', 'Chinese noodles ', 1, '1565256188mixaxiu.jpg', 60000, '<p>Thịt x&aacute; x&iacute;u đậm đ&agrave;, ngọt thơm ăn k&egrave;m c&ugrave;ng m&igrave; trứng dai dai, trộn th&ecirc;m &iacute;t x&agrave; l&aacute;ch m&aacute;t gi&ograve;n khiến m&oacute;n m&igrave; x&aacute; x&iacute;u trở n&ecirc;n rất lạ miệng v&agrave; hấp dẫn. M&igrave; x&aacute; x&iacute;u sẽ l&agrave; bữa ăn &lsquo;đổi gi&oacute;&rsquo; ngon miệng, chất lượng cho gia đ&igrave;nh nhỏ của bạn.</p>\r\n', '2019-08-10 01:11:15', 0),
(62, 'Cà phê sô cô la đá xay  ', '   ', 3, '1564993013caphesocola.jpg', 50000, '<p>Một ly c&agrave; ph&ecirc; s&ocirc;-c&ocirc;-la thơm ngon sẽ gi&uacute;p bạn khoan kho&aacute;i suốt cả ng&agrave;y h&egrave; n&oacute;ng bức, đặc biệt l&agrave; người bị huyết &aacute;p thấp.</p>\r\n', '2019-08-05 14:42:33', 0),
(63, 'Cà phê vani đá xay  ', '  ', 3, '1564993132caphevanidaxay.jpg', 50000, '<p>Dư vị nồng n&agrave;n của c&agrave; ph&ecirc; s&aacute;nh đ&ocirc;i ho&agrave;n hảo với hương vani ngọt ng&agrave;o như v&ograve;ng tay &acirc;u yếm, đệm th&ecirc;m một ch&uacute;t b&eacute;o ngậy của sữa v&agrave; kem tươi, tạo n&ecirc;n vi ngọt đậm m&agrave; h&ograve;a nh&atilde;.</p>\r\n', '2019-08-05 14:36:30', 1),
(64, 'Victoria 04 ', '     ', 3, '1564994093victoria08.jpg', 60000, '<p>Tr&aacute;i c&acirc;y v&agrave; rau củ tươi gi&agrave;u dinh dưỡng l&agrave; thực phẩm gi&uacute;p bạn bổ sung hiệu quả những dưỡng chất cần thiết cho cơ thể. V&igrave; thế uống sinh tố gi&uacute;p bạn ngăn ngừa bệnh tật v&agrave; tăng cường sức khỏe cũng như c&oacute; một v&oacute;c d&aacute;ng đẹp.</p>\r\n', '2019-08-05 14:40:06', 1),
(65, 'Espresso    ', '    ', 3, '1564993244171570471061.jpg', 60000, '<p>Một cốc Espresso nguy&ecirc;n bản được bắt đầu bởi những hạt Arabica chất lượng, phối trộn với tỉ lệ c&acirc;n đối hạt Robusta, cho ra vị ngọt caramel, vị chua dịu v&agrave; s&aacute;nh đặc. Để đạt được sự kết hợp n&agrave;y, ch&uacute;ng t&ocirc;i xay tươi hạt c&agrave; ph&ecirc; cho mỗi lần pha.</p>\r\n', '2019-08-05 10:28:20', 0),
(66, 'Nole 04  ', '  ', 3, '1564993767victoria16.jpg', 45000, '<p>Mứt Việt Quất chua thanh, ng&ograve;n ngọt, phối hợp nhịp nh&agrave;ng với d&ograve;ng sữa chua bổ dưỡng. L&agrave; m&oacute;n sinh tố thơm ngon m&agrave; cả đầu lưỡi v&agrave; l&agrave;n da đều th&iacute;ch.</p>\r\n', '2019-08-06 05:43:30', 0),
(67, 'Cà phê sữa đá  ', '  ', 3, '1564895295caphesuada.jpg', 45000, '<p>C&agrave; ph&ecirc; phin kết hợp cũng sữa đặc l&agrave; một s&aacute;ng tạo đầy tự h&agrave;o của người Việt, được xem m&oacute;n uống thương hiệu của Việt Nam.</p>\r\n', '2019-08-05 14:35:13', 1),
(68, 'Victoria 01 ', ' ', 3, '1564895367victoria01.jpg', 45000, '<p>Vị mứt cam xo&agrave;i h&ograve;a trộn độc đ&aacute;o với sữa chua, cho cảm gi&aacute;c chua ngọt rất sướng. Điểm nhấn l&agrave; những mẩu b&aacute;nh cookie gi&ograve;n tan gi&uacute;p sự thưởng thức th&ecirc;m th&uacute; vị.</p>\r\n', '2019-08-05 10:28:35', 0),
(69, 'Cam tươi   ', '   ', 3, '1564992488576624846599.jpg', 40000, '<p>Nước cam chứa nhiều calci, do đ&oacute; sẽ rất th&iacute;ch hợp cho những ai kh&ocirc;ng th&iacute;ch sữa. Uống nước cam cũng gi&uacute;p tăng cường calci cho răng v&agrave; xương mạnh mẽ, chắc khỏe&hellip;</p>\r\n', '2019-08-05 14:43:41', 1),
(70, 'Victoria 02   ', '    ', 3, '1564992600749042176669.jpg', 45000, '<p>Uống sinh tố hoa quả&nbsp; l&agrave; một trong những c&aacute;ch dễ d&agrave;ng nhất để bổ sung những dưỡng chất cần thiết cho cơ thể</p>\r\n', '2019-08-05 14:41:55', 1),
(71, 'Kem ổi   ', '   ', 3, '1564998289kemoi.jpg', 40000, '<p>Khi thưởng thức kem ổi bạn sẽ cảm nhận được hương thơm d&igrave;u dịu, thanh m&aacute;t của những tr&aacute;i ổi v&ocirc; c&ugrave;ng hấp dẫn. Hơn nữa trong ổi c&ograve;n c&oacute; rất nhiều vitamin C tốt cho l&agrave;n da của c&aacute;c chị em, gi&uacute;p da căng b&oacute;ng, s&aacute;ng mịn.</p>\r\n', '2019-08-05 10:28:57', 0),
(72, 'Matcha đá xay  ', '  ', 3, '1564992899matchadaxay.jpg', 50000, '<p>Sự ho&agrave; quyện mịn m&agrave;ng giữa vị đắng thanh của c&agrave; ph&ecirc; v&agrave; vị đắng ngọt của chocolate, c&ugrave;ng vị b&eacute;o của kem tươi v&agrave; sữa tươi h&ograve;a quyện.</p>\r\n', '2019-08-05 14:42:45', 1),
(73, 'Victoria 05', '  ', 3, '1564993368noel 01.jpg', 50000, '<p>Mứt Việt Quất chua thanh, ng&ograve;n ngọt, phối hợp nhịp nh&agrave;ng với d&ograve;ng sữa chua bổ dưỡng. L&agrave; m&oacute;n sinh tố thơm ngon m&agrave; cả đầu lưỡi v&agrave; l&agrave;n da đều th&iacute;ch.</p>\r\n', '2019-08-05 14:39:28', 1),
(74, 'Sô cô la đá xay ', ' ', 3, '1564993464socoladaxay.jpg', 50000, '<p>Vị đắng của c&agrave; ph&ecirc; kết hợp c&ugrave;ng vị cacao ngọt ng&agrave;ocủa s&ocirc;-c&ocirc;-la, vị sữa tươi ngọt b&eacute;o, đem đến trải nghiệm vị gi&aacute;c cực kỳ th&uacute; vị.</p>\r\n', '2019-08-05 10:29:27', 0),
(75, 'Trà đào ', ' ', 3, '1564993504tradao.jpg', 55000, '<p>Vị thanh ngọt của đ&agrave;o Hy Lạp, vị chua dịu của Cam V&agrave;ng nguy&ecirc;n vỏ, vị ch&aacute;t của tr&agrave; đen tươi được ủ mới mỗi 4 tiếng, c&ugrave;ng hương thơm nồng đặc trưng của sả ch&iacute;nh l&agrave; điểm s&aacute;ng l&agrave;m n&ecirc;n sức hấp dẫn của thức uống n&agrave;y. Sản phẩm hiện c&oacute; 2 phi&ecirc;n bản N&oacute;ng v&agrave; Lạnh ph&ugrave; hợp cho mọi thời gian trong năm.</p>\r\n', '2019-08-05 08:25:05', 1),
(78, 'Victoria 03 ', ' ', 3, '1564993715victoria06.jpg', 50000, '<p>Uống sinh tố hoa quả&nbsp;l&agrave; một trong những c&aacute;ch dễ d&agrave;ng nhất để bổ sung những dưỡng chất cần thiết cho cơ thể</p>\r\n', '2019-08-07 03:28:23', 0),
(79, 'Trà chanh   ', '   ', 3, '1564993624thucuongmuahe04.jpg', 40000, '<p>Tr&agrave; chanh l&agrave; thức uống được nhiều bạn trẻ y&ecirc;u th&iacute;ch. Đặc biệt tại c&aacute;c vỉa h&egrave; của đường phố H&agrave; Nội, tr&agrave; chanh đ&atilde; thực sự trở th&agrave;nh &ldquo;mốt&rdquo;. Tr&agrave; chanh kh&ocirc;ng những c&oacute; t&aacute;c dụng giải nhiệt m&ugrave;a h&egrave; m&agrave; n&oacute; c&ograve;n c&oacute; nhiều t&aacute;c dụng tốt cho sức khỏe.</p>\r\n', '2019-08-08 11:09:50', 0),
(80, 'B52   ', '   ', 4, '1565019190b52.jpg', 150000, '<p>Loại cocktail n&agrave;y c&oacute; t&ecirc;n gọi được bắt nguồn từ kiểu m&aacute;y bay n&eacute;m bom tầm xa B52. Hai nh&agrave; h&agrave;ng nổi tiếng l&agrave; Alice ở Malibu, thuộc nước Mỹ. Đ&acirc;y l&agrave; một loại cocktail ngắn nhiều tầng bao gồm một phần rượu hương c&agrave; ph&ecirc;, một phần rượu Baileys Irish Cream v&agrave; một phần rượu hương cam Le Grand Marnier. Kể từ khi ra đời năm 1977, B52 đ&atilde; nhanh ch&oacute;ng nổi tiếng tr&ecirc;n to&agrave;n thế giới v&agrave; rất được l&ograve;ng những ai th&iacute;ch thưởng thức những loại đồ uống c&oacute; cảm gi&aacute;c mạnh.</p>\r\n', '2019-08-08 11:00:07', 0),
(81, 'Singapore Sling     ', '     ', 4, '1565018795Singapore-Sling-Wide.jpg', 150000, '<p>Với những ai muốn tr&aacute;nh xa rượu mạnh, thức uống ngọt ng&agrave;o n&agrave;y ch&iacute;nh x&aacute;c d&agrave;nh cho bạn. Mang d&aacute;ng vẻ ngọt ng&agrave;onhư một qu&yacute; c&ocirc; bởi sắc đỏ quyến rũ, Singapore Sling l&agrave; một trong những loại cocktail kh&oacute; pha chế nhất tr&ecirc;n thế giới</p>\r\n', '2019-08-05 15:26:35', 0),
(82, 'Blue Margarita   ', '   ', 4, '1564896925BLUE MARGARITA.jpg', 120000, '<p>Ly cocktail Blue Margarita c&oacute; m&agrave;u xanh của biển, thật ph&ugrave; hợp cho bữa tiệc đ&ecirc;m h&egrave;. Đơn giản nhưng ngon miệng, ly cocktail n&agrave;y sẽ mang đến cho bạn cảm gi&aacute;c thật mới mẻ.</p>\r\n', '2019-08-07 03:28:10', 0),
(83, 'Margarita ', ' ', 4, '1564896985MARGARITA.jpg', 120000, '<p>Margarita l&agrave; loại cocktail được sử dụng phổ biến. Loại cocktail n&agrave;y l&agrave; sự kết hợp giữa rượu Tequila với Triple Sec hoặc Cointreau hay bất cứ loại rượu n&agrave;o c&oacute; hương cam n&agrave;o kh&aacute;c c&ugrave;ng với nước chanh tươi.</p>\r\n', '2019-08-08 10:58:53', 0),
(84, 'abc', 'êdd', 1, '1566545323-65601097_335912110646106_5737618292143554560_n.jpg', 12232, '<p>dsadsa</p>\r\n', '2019-08-23 00:28:43', 0);

